INSERT INTO `consulation_replies` VALUES (158, 213, 'هذا يجوز في الحالات التاليه \nان يكون الجد لاب والعم متوفيان او يحضروا للنيابه الحسبيه للموافقه علي نقل الوصايه لها \nالمستندات المطلوبه\nشهاده وفاه وبطاقه الوصيه وحضور القاصر بشخصه ومعه شهاده ميلاده او بطاقته', 569, 1, '2018-08-09 12:04:30', NULL, '2018-08-09 12:07:29', 1);
INSERT INTO `consulation_replies` VALUES (41, 41, 'لايجوز ذلك - حتى لا يكون في استخدام القوة ما يؤثر في نفسية الصغير- وفى هذه الحالة يكون للصادر لصالحه الحكم أن يقيم دعوى بطلب نقل الحضانة من صاحب الحق الممتنع إلى من يليه في الترتيب من أصحاب الحق فى الحضانة', 1, 1, '2018-04-05 15:05:42', NULL, '2018-04-05 15:05:42', 1);
INSERT INTO `consulation_replies` VALUES (42, 42, 'بكافة طرق الإثبات المقررة و يجوز لمحكمة الأسرة الاستعانة بالخبير الاجتماعي المنتدب بدائرة المحكمة لإثبات الامتناع عن التنفيذ في المواعيد والأماكن المحددة بالحكم – ويرفع الاخصائى الاجتماعي تقريرا للمحكمة بذلك', 1, 1, '2018-04-05 15:07:10', NULL, '2018-04-05 15:07:10', 1);
INSERT INTO `consulation_replies` VALUES (43, 43, 'الطلاق شرعا هو حل رابطة الزوجية الصحيحة بلفظ الطلاق الصريح أو بعبارة تقوم مقامه تصدر ممن يملكه وهو الزوج أو نائبه والأصل أن للزوج دون المرأة أن يوقع الطلاق إلا إذا اشترطت عليه وقت العقد أن تكون عصمتها بيدها فتوقع الطلاق علي نفسها نيابة عنه.', 1, 1, '2018-04-05 15:08:17', NULL, '2018-04-05 15:08:17', 1);
INSERT INTO `consulation_replies` VALUES (44, 44, 'هو الطلاق الذي يقع من الزوج وحتى انقضاء العدة وخلال هذه الفترة يجوز للزوج أن يراجع زوجته دون عقد جديد أو مهر جديد.', 1, 1, '2018-04-05 15:09:06', NULL, '2018-04-05 15:09:06', 1);
