INSERT INTO `consulation_replies` VALUES (63, 63, 'يحق للزوجه طلب الطلاق دون المساس بحقوقها فى الحالات الآتية:\r\nإهانة الزوجة أو التعرض لها بالضرب، عدم الانفاق، حبس الزوج مدة تزيد عن ثلاث سنوات يحق لها رفع الدعوى بعد سنة، بسبب العنة (عدم القدرة الجنسية) و مرض البرص، الزواج من أخرى.\r\n \r\nوالضرر المقصود فى القانون لم يرد على سبيل الحصر بمعنى أن أى ضرر تستطيع الزوجة إثباته يحق لها طلب التطليق.', 1, 1, '2018-04-05 19:34:34', NULL, '2018-04-05 19:34:34', 1);
