INSERT INTO `consulation_replies` VALUES (72, 72, 'يظل استحقاق الحاضنة لأجر الحضانة قائما طالما كان الصغير معها بشرط ألا يستمر فرض هذا الأجر بعد بلوغ الصغير أقصى السن المقررة للحضانة، لأن اليد بعد هذه السن تكون يد حفظ وليس لها صفة الحضانة.', 1, 1, '2018-04-05 19:48:41', NULL, '2018-04-05 19:48:41', 1);
INSERT INTO `consulation_replies` VALUES (73, 73, 'طبقاً لنص المادة 84 و86 من قانون الولاية على المال 119 لسنة 1952 يعاقب الوصى الى أخل بأحكام هذا القانون والذى تفرض عليه بعدم جواز تصرفه فى أموال القاصر إلا بالرجوع للمحكمة تعاقبه المحكمة بالغرامة وحرمانه من أجره كله أو بعضه وعزله أو باحد هذه الجزاءات، ويجوز للمحكمة أن تمنح القاصر هذه الغرامة أو جزء منها، ويجوز اعفاء الوصى من الجزاء المالى كله أو بعضه اذا نفذ الأمر الذى ترتب عليه الحكم أو قدم اعذارا تقبلها المحكمة، على أنه يكون مسئولا عما يلحق القاصر من ضرر بسبب ذلك وعلى كل حال.', 1, 1, '2018-04-05 19:51:07', NULL, '2018-04-05 19:51:07', 1);
