INSERT INTO `consulation_replies` VALUES (76, 76, 'يستحق الصغير أجر المسكن سواء كان يقيم لدى الحاضنة بملكها الخاص أو بأجر وذلك من تاريخ الحاجة إليه- تاريخ الامتناع- باعتباره من عناصر نفقته على أبيه.', 1, 1, '2018-04-05 19:53:49', NULL, '2018-04-05 19:53:49', 1);
INSERT INTO `consulation_replies` VALUES (77, 77, 'اللفظ أو الإشارة أو الكتابة واللفظ الذي يقع به الطلاق هو اللفظ الصريح الذي يدل عليه.', 1, 1, '2018-04-05 19:55:54', NULL, '2018-04-05 19:55:54', 1);
