INSERT INTO `entity_localizations` VALUES (35, 5, 'content', 3, 2, '<p><strong>&gt;&gt; L&#39;avocat vous informe</strong><br />\r\n<br />\r\nLe Droit &eacute;volue tous les jours, il devient de plus en plus complexe &agrave; comprendre et &agrave; appliquer.<br />\r\n<br />\r\nChaque jour apporte au particulier ou &agrave; l&#39;entreprise son lot de textes l&eacute;gislatifs, r&eacute;glementaires, de recommandations communautaires, de circulaires administratives ou de d&eacute;cisions de jurisprudence.<br />\r\n<br />\r\nL&#39;avocat est en mesure de vous informer de ces &eacute;volutions et de leurs cons&eacute;quences.</p>');
INSERT INTO `entity_localizations` VALUES (41, 5, 'content', 3, 3, '<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:top\">\r\n			<p><strong>Our Mission Statement</strong>provides the motivation for all we do, establishing the basis for how we interact with, perform services for, and define our responsibilities to our clients.</p>\r\n\r\n			<ul>\r\n				<li>The best interests of our clients come first. We recognize that we are in the service business. Our attorneys and support staff are sensitive to the needs and concerns of our clients. Our lawyers and staff are responsive and accessible.<br />\r\n				&nbsp;</li>\r\n				<li>We are committed to excellence in the practice of law, strict adherence to the ethical principles that govern the legal profession and the highest degree of professionalism.<br />\r\n				&nbsp;</li>\r\n				<li>All client engagements are appropriately staffed. Experience and expertise are matched with the needs of the particular matter to achieve the most effective representation of our clients.<br />\r\n				&nbsp;</li>\r\n				<li>We are sensitive to the high cost of quality legal representation. We are and will remain competitive and innovative in the market place in terms of legal fees. We encourage open discussions with clients about the cost of our work. The client should fully understand the cost of our representation throughout the engagement.<br />\r\n				&nbsp;</li>\r\n				<li>Consistent with our commitment to excellence, our attorneys and staff are committed to education, training, and preparedness in our chosen areas of practice.<br />\r\n				&nbsp;</li>\r\n				<li>Aman Law Firm recognizes a deep commitment to the community and to our profession. All of our professionals and many of our staff are heavily involved in various community and professional activities.</li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>');
