INSERT INTO `news` VALUES (169, 'نائب وزير المالية: مخصصات الموازنة الجديدة تكفى برامج الحماية الاجتماعية', '<p><span dir=\"rtl\">قال أحمد كوجاك، نائب وزير المالية، أن الموازنة العامة الجديدة كانت بها مخصصات مالية تكفى لبرامج الحماية الاجتماعية ومنها زيادة الأجور والعلاوة للمخاطبين وغير المخاطبين بقانون الخدمة المدينة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأضاف فى مؤتمر صحفى بمقر مجلس الوزراء، أن الزيادة الشهرية للعاملين من الدرجات الوسطى تتراوح من 280 لـ290 شهريا، موضحا أن زيادة العلاوات تكلف الخزانة 22 مليار جنيه.&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>وأشار إلى أن زيادة المعاشات تكلف الخزانة 24 مليار جنيه ويستفيد منها 10 ملايين مستفيد، وزيادة حد الاعفاء والخصم الضريبى من 7200 جنيه لـ8000 جنيه.</p>', 'storage/app/public/news/1529497135نمتسضنتسه.jpg', 1, '2018-06-20 14:18:55', '2018-06-20 14:18:55', '2018-06-20 14:18:55', 416, 416, 1, 1);
INSERT INTO `news` VALUES (170, 'رئيس الوزراء يلتقى وزير المالية لمتابعة خطة وزارته خلال الفترة المقبلة', '<p><span dir=\"rtl\">التقى الدكتور مصطفى مدبولى، رئيس الوزراء ووزير الإسكان، منذ قليل محمد معيط، وزير المالية لمتابعة خطة الوزارة خلال الفترة المقبلة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وكان&nbsp; رئيس الوزراء قد التقى صباح اليوم الخميس، طارق عامر، محافظ البنك المركزى لمتابعة عدد من الملفات المتعلقة بالسياسات النقدية وإجراءات الإصلاح الاقتصادى.</span></p>', 'storage/app/public/news/1529583864مدبة.jpg', 1, '2018-06-21 14:24:24', '2018-06-21 14:24:24', '2018-06-21 14:24:24', 416, 416, 1, 1);
INSERT INTO `news` VALUES (171, 'نجاة 41 سائحا ألمانيا إثر نشوب حريق بأتوبيس سياحى فى طريقه للأقصر', '<p><span dir=\"rtl\">أنقذت العناية الإلهية 41 سائحا فى حادث نشوب حريق بأتوبيس سياحى أمام كوبرى المعنا فى طريقه من مدينة الغردقة إلى مدينه الأقصر.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">تلقى مرفق إسعاف قنا برئاسة الدكتور خالد يوسف مدير المرفق بلاغا يفيد باندلاع حريق بأتوبيس سياحى بالقرب من كوبرى المعنا يقل 41 سائحا ألمانيا كانوا فى طريقهم من مدينة الغردقة إلى مدينة الأقصر دون وقوع إصابات.</span></p>\r\n\r\n<p><span dir=\"rtl\">تم الدفع بسيارات الإسعاف والحماية المدنية إلى مكان الحادث وإخلاء الأتوبيس ونقل السياح لأتوبيس آخر وإخطار مديرية أمن قنا للوقوف على تأمين السياح بإشراف اللواء علاء العياط مدير أمن قنا.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1529584054سياحه.jpg', 1, '2018-06-21 14:27:34', '2018-06-21 14:27:34', '2018-06-21 14:27:34', 416, 416, 1, 1);
INSERT INTO `news` VALUES (172, 'رئيس الوزراء: تعاون وتنسيق مستمر بين الحكومة والبنك المركزى بما يخدم الاقتصاد', '<p><span dir=\"rtl\">التقى الدكتور مصطفى مدبولى، رئيس مجلس الوزراء، وزير الإسكان والمرافق والمجتمعات العمرانية، فى مقر مجلس الوزراء، صباح اليوم، الخميس، طارق عامر، محافظ البنك المركزي، الذى بدأ اللقاء بتوجيه التهنئة لرئيس مجلس الوزراء، على منصبه الجديد.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وصرح الدكتور مصطفى مدبولى بأنه تم عقد اجتماع مغلق مع محافظ البنك المركزى لمناقشة عدد من الملفات المشتركة المهمة، مشدداً على أن هذه المرحلة ستشهد تعاوناً تاماً، وتنسيقاً مستمراً بين الحكومة والبنك المركزى، بما يحقق مصلحة الاقتصاد المصرى.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأوضح رئيس مجلس الوزراء أن الاجتماع شهد مناقشة الأوضاع المالية والاقتصادية، حيث تم عرض تطور الاحتياطى النقدى الأجنبى الحالي، ومؤشرات أداء ميزان المدفوعات، وتطورات برنامج صندوق النقد الدولى، وكذا إجمالى موارد القطاع المصرفى من العملة الأجنبية.&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وقال الدكتور مصطفى مدبولى: تمت أيضاً مناقشة مديونيات عدد من الجهات الحكومية للبنوك، وآليات وسبل سدادها، وكذا موقف الدين الخارجي، كما تطرق الاجتماع لمناقشة عدد من التشريعات المقترحة الخاصة بالسياسة النقدية والمالية، المطلوب إصدارها خلال الفترة المقبلة، للمساهمة فى تنمية الاقتصاد المصرى.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وشدد رئي</span>س مجلس الوزراء على أنه تم الاتفاق على آلية مستمرة للتنسيق والتعاون بين الحكومة والبنك المركزى، فالمسئولية تضامنية، والهدف واحد، وهو دفع الاقتصاد المصرى، وتحسين معيشة المواطنين</p>', 'storage/app/public/news/1529588169ناهعاغعهغع55.jpg', 1, '2018-06-21 15:36:09', '2018-06-21 15:36:09', '2018-06-21 15:36:09', 416, 416, 1, 1);
INSERT INTO `news` VALUES (173, 'السبت.. \"تعليم البرلمان\" تبدأ مناقشة مشروع قانون إنشاء فروع للجامعات الأجنبية', '<p><span dir=\"rtl\">تستأنف لجنة التعليم والبحث العلمى بمجلس النواب اجتماعاتها السبت المقبل، بمناقشة مشروع قانون مقدم من الحكومة بإنشاء صندوق رعاية المبتكرين والنوابغ، ومناقشة قرار رئيس مجلس الوزراء بمشروع قانون بشأن إنشاء وتنظيم فروع للجامعات الأجنبية داخل جمهورية مصر العربية والمؤسسات الجامعية.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وتعقد اللجنة اجتماعين يوم الأحد، لمناقشة مشروع قانون مقدم من الحكومة بشأن تعديل بعض أحكام القانون رقم 415 لسنة 1954 فى شأن مزاولة مهنة الطب، واستمرار مناقشة قرار القائم بأعمال رئيس مجلس الوزراء بمشروع قانون بشأن تعديل بعض أحكام قانون تنظيم الجامعات الصادر بالقانون رقم 49 لسنة 1972.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفى يوم الإثنين، تناقش اللجنة قرار رئيس جمهورية مصر العربية رقم 226 لسنة 2018 بالموافقة على الخطابات المتبادلة بين حكومة جمهورية مصر العربية وحكومة اليابان، بشأن تقديم وكالة اليابان للتعاون الدولى قرضاً تصل قيمته إلى 18 مليار و626 مليون ين يابانى للحكومة المصرية، وذلك لتنفيذ الشراكة المصرية اليابانية فى مجال التعليم: برنامج دعم المدارس المصرية اليابانية، والموقعة في القاهرة بتاريخ 21/2/2018.</span></p>\r\n\r\n<p><span dir=\"rtl\">كما تناقش فى اليوم نفسه مشروع قانون مقدم من النائبة الدكتورة ماجدة بكرى و60 نائباً (أكثر من عٌشر عدد أعضاء المجلس) بشأن تعديل بعض أحكام القانون رقم 155 لسنة 2007 بتعديل بعض أحكام قانون التعليم الصادر بالقانون رقم 139 لسنة 1981، وتناقش أيضا قرار رئيس مجلس الوزراء بمشروع قانون بشأن إنشاء وتنظيم فروع للجامعات الأجنبية داخل جمهورية مصر العربية والمؤسسات الجامعية.</span></p>', 'storage/app/public/news/1529588904وزير التعليم.jpg', 1, '2018-06-21 15:48:24', '2018-06-21 15:48:24', '2018-06-21 15:48:24', 416, 416, 1, 1);
INSERT INTO `news` VALUES (174, '\"خطة البرلمان\" تناقش تحديث استراتيجية \"مصر 2030\" الأسبوع المقبل', '<p><span dir=\"rtl\">تعقد لجنة الخطة والموازنة بمجلس النواب برئاسة الدكتور حسين عيسى رئيس اللجنة، اجتماعين الأسبوع المقبل، الاجتماع الأول يوم السبت لمناقشة المشاكل التى تواجه الجهات الإدارية بالدولة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وتعقد اللجنة اجتماعها الثانى يوم الأحد لاستكمال مناقشة تحديث استراتيجية مصر 2030 وعرض منظومة&nbsp;gfmis&nbsp;وكيفية ربطها بوزارة التخطيط والمتابعة والإصلاح الإدارى ووزارة التعاون الدولى وبنك الاستثمار القومى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وكانت لجنة الخطة والموازنة بمجلس النواب قد انتهت خلال الأسابيع الماضية من مناقشة مشروعى الموازنة العامة للدولة 2018/2019، وخطة التنمية المستدامة متوسطة الأجل (2018/2019 &ndash; 2021/2022)، ومشروع قانون بتعديل بعض أحكام قانون الضريبة على الدخل، وتم إقرارهم بالجلسة العامة للبرلمان.</span></p>', 'storage/app/public/news/1529590125متننعتهنعه.jpg', 1, '2018-06-21 16:08:45', '2018-06-21 16:08:45', '2018-06-21 16:08:45', 416, 416, 1, 1);
INSERT INTO `news` VALUES (175, 'الإمارات تنضم إلى قائمة المستثمرين في \"ظهر\"', '<p>انضمت الإمارات رسميا إلي قائمة المستثمرين في حقل &quot;ظهر&quot; الذي يعد أحد أكبر حقول الغاز في البحر المتوسط.</p>\r\n\r\n<p>وقعت وزارة البترول أمس عقد الاتفاق النهائي لبيع 10% من منطقة امتياز شروق البحرية التي يقع بها حقل &quot;ظهر&quot; لصالح شركة &quot;مبادلة&quot; الإماراتية للبترول.</p>\r\n\r\n<p>أكدت الوزارة أنها وقعت عقدًا مع شركات: &quot;إينى&quot; الإيطالية، و&quot;مبادلة&quot; الإماراتية للبترول، والمصرية القابضة للغازات الطبيعية &laquo;إيجاس&raquo; للبيع النهائي لـ 10% من الامتياز الذي يضم حقل غاز &quot;ظُهر&quot;.</p>\r\n\r\n<p>وتقدر قيمة الصفقة بنحو 934 مليون دولار، بالإضافة إلى منحة توقيع بقيمة 94 مليون دولار.</p>\r\n\r\n<p>وفى مارس الماضي أعلنت شركة &quot;إينى&quot; الإيطالية بيع حصة بنسبة 10% من منطقة امتياز شروق، التي يقع بها حقل ظهر العملاق، أكبر حقول الغاز الطبيعي فى البحر المتوسط.</p>\r\n\r\n<p>وقال طارق الملا وزير البترول: إن النتائج &quot;غير المسبوقة&quot; التي تحققت في مشروع حقل &quot;ظهر&quot; في جميع مراحله أسهمت بقوة في زيادة إقبال المستثمرين على الدخول كشريك في هذا المشروع العملاق، وجعلت مصر في بؤرة اهتمام الاستثمارات العالمية في صناعة البترول والغاز.</p>\r\n\r\n<p>وأضاف أن دخول شركاء جدد للاستثمار في قطاع البترول يعكس الفرص الاستثمارية المتميزة التي يزخر بها القطاع، ويخدم الاقتصاد المصري بشكل كبير خلال المرحلة المقبلة من خلال الاستفادة من الخبرات الواسعة للشركاء الجدد.&nbsp;وتستثمر شركة &quot;مبادلة&quot; الإماراتية في مصر لأول مرة من خلال شراكتها في حقل ظهر.</p>\r\n\r\n<p>و&quot;مبادلة للبترول&quot; هي شركة مملوكة بالكامل لشركة مبادلة للاستثمار، المملوكة لحكومة أبوظبي الإماراتية، والتي تعمل في 13 قطاعاً، من ضمنها قطاع الطاقة، وذلك في أكثر من 30 دولة.</p>\r\n\r\n<p>وأبدت &quot;مبادلة&quot; اهتمامها بالفرص الاستثمارية الواعدة في قطاع البترول في أنشطة البحث والاستكشاف وتنمية الحقول.</p>\r\n\r\n<p>وتمتلك شركة &quot;إينى&quot; الإيطالية، المالك الأصلي لمنطقة امتياز شروق حالياً 50% من منطقة الامتياز، مقابل 10% لشركة بى بى البريطانية، و30% لشركة روزنفت الروسية، و10% لشركة مبادلة الإماراتية للبترول.</p>', 'storage/app/public/news/1529755737afa1edcd518f8893f9b7a13c98140750.jpg', 1, '2018-06-23 14:08:57', '2018-06-23 14:08:57', '2018-06-23 14:08:57', 1, 1, 1, 1);
INSERT INTO `news` VALUES (176, 'تحصيل \"الضرائب العقارية\" بلا غرامات حتى آخر يونيو', '<p>أعلنت سامية حسين، رئيس مصلحة الضرائب العقارية، فتح منافذ لتحصيل الضريبة العقارية بمقر المصلحة الرئيسى بميدان لاظوغلى بالقاهرة، تيسيرا على ملاك الوحدات السكنية الموجودة بالساحل الشمالى من الإسكندرية إلى مرسى مطروح والعين السخنة والبحر الأحمر وجنوب سيناء والمناطق النائية والمقيمين بالقاهرة وضواحيها. وأَضافت أن المصلحة تستهدف التيسير عليهم، وإتاحة الفرصة لهم لسداد الضريبة العقارية المستحقة على وحداتهم السكنية، دون أية غرامات، أو اتخاذ إجراءات قانونية ضدهم، وذلك حتى نهاية يونيو الحالي. وقالت ـ فى بيان أمس ـ إن ملاك الوحدات السكنية المقيمين بالساحل الشمالي والمناطق النائية يمكنهم إنهاء جميع إجراءاتهم من خلال جميع المأموريات العقارية المختصة، والموجودة على امتداد الساحل الشمالى، كما يمكنهم سداد الضريبة المستحقة هناك أيضاً. وأوضحت أن المقر العام للمصلحة يفتح أبوابه طوال أيام الأسبوع بما فيها &quot;السبت&quot; من الساعة التاسعة صباحاً حتى السادسة مساءً. وطالبت الممولين الذين لم يبادروا، حتى الآن، بسداد الضريبة المستحقة عليهم سواء عن السنوات السابقة أو المستحقة هذا العام بسرعة التوجه إلى المأموريات المختصة المنتشرة فى جميع مراكز المدن، فى كل المحافظات لسداد الضريبة العقارية المستحقة على الوحدات المملوكة لهم، والخاضعة للضريبة، وذلك فى موعد أقصاه 30 يونيو الحالي، تجنبا لتعرض أصحاب الوحدات السكنية الخاضعة للضريبة لغرامات التأخير المقررة طبقاً للقانون.</p>', 'storage/app/public/news/1529757229f562ef093ba9e1c36c8f9e1a3cb7de97.jpeg', 1, '2018-06-23 14:33:49', '2018-06-23 14:33:49', '2018-06-23 14:33:49', 1, 1, 1, 1);
INSERT INTO `news` VALUES (177, 'وكيل البرلمان: \"عبد العال\" يُرتب موعد إلقاء برنامج الحكومة بالتنسيق مع رئيس الوزراء', '<p><span dir=\"rtl\">قال السيد الشريف وكيل مجلس النواب، إن الدكتور على عبد العال رئيس البرلمان يرتب موعد إلقاء برنامج الحكومة أمام المجلس، بالتنسيق مع المهندس مصطفى مدبولى رئيس مجلس الوزراء، ثم إدراجه فى جدول الجلسات العامة فضلاً عن إخطار النواب به.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">يأتى ذلك بعد عودة رئيس مجلس النواب من زيارته لبروكسل لإجراء مباحثات مع رئيس البرلمان الأوروبى وبعض السادة المسئولين، بناء على دعوة موجهة منه إلى الدكتور عبد العال، المقررة مساء اليوم، و العودة غداً لاستئناف جلسات البرلمان.</span></p>\r\n\r\n<p><span dir=\"rtl\">ووفقاً للمادة 126 من لائحة مجلس النواب، فيقدم رئيس مجلس الوزراء المكلف برنامجَ الحكومة خلال عشرين يوما من تاريخ تشكيلها إلى مجلس النواب، أو فى أول اجتماع له إذا كان غير قائم، و يناقش المجلس بيان رئيس مجلس الوزراء المكلف عن برنامج الحكومة، ويحال هذا البيان إلى لجنة خاصة برئاسة أحد وكيلى المجلس، يُراعى فيها تمثيلُ المعارضة والمستقلين.</span></p>\r\n\r\n<p><span dir=\"rtl\">ويتم دراسة البرنامج وإعداد تقرير عنه خلال عشرة أيام، ويعرض التقرير على المجلس فى أول جلسة تالية لانتهاء هذه المدة، و يصدر قرار المجلس فى شأن حصول الحكومة على ثقة أغلبية أعضاء المجلس سيصدر خلال الأيام العشرة التالية لعرض التقرير عليه، وفى جميع الأحوال يجب ألا تزيد المدة على ثلاثين يوماً من تاريخ تقديم الحكومة برنامجَها.</span></p>\r\n\r\n<p><span dir=\"rtl\">وإذا لم تحصل الحكومة على ثقة المجلس وفق حكم هذه المادة، اعتُبرت مستقيلة، ويكلف رئيس الجمهورية من يرشحه الحزب أو الائتلاف الحائز على أكثرية مقاعد المجلس بتشكيل الحكومة.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1529831189علي عبد العال.jpg', 1, '2018-06-24 11:06:29', '2018-06-24 11:06:29', '2018-06-24 11:06:29', 416, 416, 1, 1);
INSERT INTO `news` VALUES (178, 'مصر تشارك فى الاجتماع السابع لوزراء دفاع دول تجمع الساحل والصحراء بنيجيريا', '<p>شاركت مصر بوفد ممثل عن وزارة الدفاع فى الاجتماع السابع لوزراء دفاع دول تجمع الساحل والصحراء، والذى عقد فى العاصمة النيجيرية &quot;أبوجا&quot; فى الفترة من 20 إلى 22 يونيو 2018</p>', 'storage/app/public/news/1529843616الساحل.jpg', 1, '2018-06-24 14:33:36', '2018-06-24 14:33:36', '2018-06-24 14:33:36', 416, 416, 1, 1);
INSERT INTO `news` VALUES (179, 'البرلمان يوافق على مد حالة الطوارئ فى جميع أنحاء البلاد لمدة ثلاثة أشهر', '<p><span dir=\"rtl\">وافق مجلس النواب خلال جلسته العامة اليوم برئاسة الدكتور على عبد العال، بأغلبية ثلثى أعضاء المجلس وقوفاً، على قرار رئيس جمهورية مصر العربية رقم 266 لسنة 2018 بمد حالة الطوارئ المعلنة بقرار رئيس الجمهورية رقم 168 لسنة 2018 فى جميع أنحاء البلاد لمدة 3 أشهر، تبدأ اعتباراً من الساعة الواحدة من صباح يوم السبت الموافق 14 يوليو 2018.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وينص القرار، على أن تتولى القوات المسلحة والشرطة حفظ الأمن فى جميع أنحاء البلاد، وحماية الممتلكات&nbsp; العامة والخاصة، واتخاذ الإجراءات اللازمة لمواجهة الإرهاب</span></p>', 'storage/app/public/news/1529844040مجلس النواب.jpg', 1, '2018-06-24 14:40:40', '2018-06-24 14:40:40', '2018-06-24 14:40:40', 416, 416, 1, 1);
INSERT INTO `news` VALUES (180, 'اجتماع مشترك لوزراء السياحة والثقافة العرب ديسمبر المقبل بالإسكندرية', '<p><span dir=\"rtl\">أعلن السفير كمال حسن على، الأمين العام المساعد رئيس قطاع الشؤون الاقتصادية بالجامعة العربية، أنه تقرر عقد اجتماع مشترك لمجلس وزراء السياحة والثقافة العرب بمكتبة الإسكندرية يوم 11 ديسمبر المقبل، وذلك بهدف بناء شراكات فاعلة بين الدول العربية فى مجال السياحة الثقافية، واعتماد السياحة محركا رئيسا للتعريف بالتراث الثقافي العربى، وركيزة أساسية للمحافظة عليه واستثماره، وتطويره وإبراز الإمكانيات التنموية الهائلة لقطاع السياحة فى الوطن العربى.</span></p>', 'storage/app/public/news/1529844234جامعه الدول.jpg', 1, '2018-06-24 14:43:54', '2018-06-24 14:43:54', '2018-06-24 14:43:54', 416, 416, 1, 1);
INSERT INTO `news` VALUES (181, 'رئيس هيئة الانتخابات التركية يعلن بدء التحقيق فى خروق بمراكز انتخابية', '<p><span dir=\"rtl\">أعلن رئيس هيئة الانتخابات التركية عن بدء تحقيق عاجل بعد حدوث خروق فى مراكز انتخابية بمحافحة أروقة جنوبى البلاد.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وبدأ الناخبون الأتراك عمليات التصويت فى الانتخابات الرئاسية والبرلمانية فى البلاد، صباح اليوم اللأحد، وذكرت قناة سكاى نيوز بالعربية، أن مراكز الاقتراع فتحت أبوابها أمام نحو 56 مليون ناخب تركى فى عموم البلاد للإدلاء بأصواتهم فى انتخابات رئاسية وبرلمانية تمثل أكبر تحد للرئيس رجب طيب أردوغان وحزبه العدالة والتنمية منذ وصوله للسلطة قبل أكثر من 10 أعوام ونصف العام .</span></p>\r\n\r\n<p><span dir=\"rtl\">ويتنافس فى سباق الرئاسة 6 مرشحين، أبرزهم الرئيس الحالى أردوغان عن حزب العدالة والتنمية ومحرم إنجيه عن حزب الشعب الجمهورى المعارض.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتكتسب هذه الانتخابات أهميتها لكون النظام الرئاسى سيجرى تطبيقه بعد تعديل النظام السياسى من برلمانى إلى رئاسى جمهورى فى استفتاء أجرى فى أبريل 2017.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1529845616انتحابات تركيه.jpg', 1, '2018-06-24 15:06:56', '2018-06-24 15:06:56', '2018-06-24 15:06:56', 416, 416, 1, 1);
INSERT INTO `news` VALUES (182, 'القبض على نجل الفنان المرسى أبو العباس لاتهامه بقتل زوجته وطفلتيه', '<p><span dir=\"rtl\">قالت مصادر أمنية بمديرية أمن الجيزة، أن نجل الفنان المرسى أبو العباس، هو المتورط فى قتل زوجته وطفلتيه داخل شقتهن ببولاق الدكرور.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضافت المصادر، أن المتهم اعترف بقتل المجنى عليهن بعد خسارته ثروته فى البورصة، وخشيته عليهن من الفقر، كما كشفت التحريات أنه يتلقى العلاج من مرض نفسى منذ 6 سنوات.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">أحداث الجريمة كشف عنها بلاغ تلقاه العقيد طارق حمزة، مفتش مباحث غرب الجيزة، يفيد العثور على ربة منزل وابنتيها مفارقين الحياة داخل مسكنهن ببولاق الدكرور.</span></p>\r\n\r\n<p><span dir=\"rtl\">تم إخطار اللواء إبراهيم الديب، مدير الإدارة العامة لمباحث الجيزة، الذى انتقل إلى محل الواقعة، وبصحبته عدد من القيادات الأمنية، وتم العثور على جثة هبة.ا&quot; ربة منزل وابنتيها الكبرى &quot;جنة الله&quot; تبلغ من العمر 14 عاما والثانية حبيبة الله&quot;&nbsp; تبلغ 11 عاما مفارقين الحياة نتيجة تعرضهن للخنق.</span></p>\r\n\r\n<p><span dir=\"rtl\">وكشفت تحريات المقدم محمد الجوهرى، رئيس مباحث قسم شرطة بولاق الدكرور، والرائد طارق مدحت والنقيبان أيمن سكورى وأحمد مندور، عن أن زوج المجنى عليها ووالد الضحيتين &quot;صلاح المرسى&quot;، هو نجل الفنان الراحل المرسى أبو العباس، ادعى أنه عقب عودته للمنزل اكتشف الجريمة، إلا أن تحريات المباحث كشفت كذب ادعائه، وتبين أنه وراء قتلهن، وحرر محضر بالواقعة، وتولت النيابة التحقيق.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1529845970نجل الفنان.jpg', 1, '2018-06-24 15:12:50', '2018-06-24 15:12:50', '2018-06-24 15:12:50', 416, 416, 1, 1);
INSERT INTO `news` VALUES (183, 'نائب يقدم طلب إحاطة للحكومة لتطبيق الرقابة الدورية على المواقف والأسواق', '<p>أكد النائب حسنى حافظ، عضو مجلس النواب عن حزب الوفد بمحافظة الإسكندرية، أنه سوف يقدم طلب إحاطة إلى المهندس مصطفى مدبولى رئيس الوزراء، عقب حصوله حكومته على ثقة الأغلبية، وذلك للمطالبة بتطبيق الرقابة الدورية <span dir=\"rtl\">على الأسواق ومواقف السيارات.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأوضح &quot;حافظ&quot;، وفق طلبه أنه من الضرورى الرقابة الدورية على الأسواق ومواقف السيارات والسرفيس بجميع المحافظات، مطالبا بتحديد رقم معين بخط ساخن داخل لوحات ارشادية ووضعها بشكل واضح داخل الأسواق ومواقف السيارات الداخلية وبين المحافظات.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1529919825النائب.jpg', 1, '2018-06-25 11:43:45', '2018-06-25 11:43:45', '2018-06-25 11:43:45', 416, 416, 1, 1);
INSERT INTO `news` VALUES (184, 'الجريدة الرسمية تنشر قرار السيسي بمد حالة الطوارئ لمدة ثلاثة أشهر', '<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">أصدر الرئيس عبد الفتاح السيسي القرار رقم 266 لسنة 2018 بمد حالة الطوارئ المعلنة بقرار رئيس الجمهورية رقم 168 لسنة 2018 في جميع أنحاء البلاد لمدة ثلاثة أشهر أخرى تبدأ من الساعة الواحدة من صباح يوم السبت المؤلف 14 يوليو 2018.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">نشر القرار في الجريدة الرسمية وفقاً للدستور وبعد موافقة مجلس الوزراء.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1529921751السيسي.jpg', 1, '2018-06-25 12:15:51', '2018-06-25 12:15:51', '2018-06-25 12:15:51', 416, 416, 1, 1);
INSERT INTO `news` VALUES (185, 'وزير النقل: انتهاء تنفيذ وافتتاح مشروع مترو هارون ـ النزهة ديسمبر المقبل', '<p><span dir=\"rtl\">أجرى الدكتور هشام عرفات، وزير النقل جولة تفقدية لمتابعة معدلات التنفيذ بالجزء الثانى من المرحلة الرابعة للخط الثالث للمترو، حيث تفقد عدد من المحطات الجارى تنفيذها، مطالب العاملين بالمشروع بضرورة الالتزام بالجدول الزمنى في تنفيذ الأعمال.</span></p>', 'storage/app/public/news/1529925648وزير النقل.jpg', 1, '2018-06-25 13:20:48', '2018-06-25 13:20:48', '2018-06-25 13:20:48', 416, 416, 1, 1);
INSERT INTO `news` VALUES (186, 'طلب إحاطة لـ\"مدبولي\" لفتح تحقيق حول أزمة أموال تبرعات مستشفى 57357', '<p>قدم النائب محمد عبد الله زين الدين، وكيل لجنة النقل والمواصلات بمجلس النواب، بطلب إحاطة من إلى الدكتور مصطفى مدبولي، رئيس مجلس الوزراء، بخصوص أزمة مستشفى 57357 الأخيرة، مطالبًا بضرورة إجراء تحقيق عاجل في الأزمة وتوضيح الأمر للمواطنين الذين يتبرعون بأموالهم منذ سنوات لهذه المستشفى الكبيرة.</p>\r\n\r\n<p>وأوضح زين الدين، في بيان، أن مستشفى 57357 منذ إنشاءها وحتى هذه اللحظة وهي قائمة على أموال التبرعات، بالإضافة إلى أن المستشفى لها سمعة طيبة وشهرة واسعة حتى أنها تتلقى تبرعات من الداخل والخارج، لذلك فإن جمعها تبرعات بمليار جنيه سنويًا ليس غريبًا ولكن الغريب هو وجود اتهامات تقول أنه لا يتم صرف سوى من 160 إلى 200 مليون جنيه فقط على الأطفال المرضى</p>\r\n\r\n<p>وتابع زين الدين، &quot;المواطنين حين يتبرعون للمستشفيات يتبرعون من أجل تقديم خدمة طبية مميزة للأطفال تساعدهم على الشفاء من السرطان لا لكي يتم صرف أموالهم على الموظفين أو لإطلاق حملات إعلانية ضخمة تتكلف مبالغ طائلة، حتى أنه يصل سعر الدقيقة في الإعلان الواحد بها لأكثر من 100 ألف جنيه&quot;.</p>', 'storage/app/public/news/15300163513398952541529435865.jpg', 1, '2018-06-26 14:32:31', '2018-06-26 14:32:31', '2018-06-26 14:32:31', 1, 1, 1, 1);
INSERT INTO `news` VALUES (187, 'اليوم.. شركات الدواء تبحث زيادة الأسعار', '<p>في الوقت الذي أكدت فيه الدكتورة هالة زايد، وزيرة الصحة والسكان، أن الوزارة لم تصلها أي طلبات من شركات الأدوية لزيادة أسعار منتجاتها منذ قرار رفع أسعار الوقود مطلع الأسبوع الماضي، أكد&nbsp;الدكتور محيي حافظ، عضو غرفة صناعة الدواء، أن أعضاء الغرفة سيجتمعون اليوم &quot;الثلاثاء&quot; لبحث تأثير زيادة أسعار الوقود على منتجاتهم.</p>\r\n\r\n<p>وأوضح أن الشركات لديها عدة مقترحات سيجري مناقشتها خلال الاجتماع، منها تطبيق الموجة الثانية من زيادة أسعار الأدوية المؤجلة منذ شهر أغسطس الماضي، وفقًا للاتفاق المبرم بين وزير الصحة السابق أحمد عماد والشركات، بعد قرار تعويم الجنيه، إلى جانب إعفاء الأدوية من ضريبة القيمة المضافة على جميع مستلزمات الإنتاج ما عدا المادة الفعالة بنسبة 14%&quot;.</p>\r\n\r\n<p>وزيرة الصحة أوضحت لموقع &quot;مصراوي&quot; أمس، أن &quot;الشركات لم تتواصل مع الوزارة، ولم يتصل بها أحد لتحديد اجتماع لزيادة أسعار&quot;.</p>\r\n\r\n<p>وكانت الحكومة قررت منذ أسبوعين، رفع أسعار الوقود بنسب تصل إلى 66%، ويتوقع بشكل كبير تأثير تلك الزيادة على كافة القطاعات الصناعية والتجارية المختلفة.</p>\r\n\r\n<p>من جهة أخرى، نفى خالد مجاهد المتحدث الرسمي باسم وزارة الصحة &quot;زيادة أسعار تذاكر العيادات الخارجية بالمستشفيات العامة والمركزية في مطلع يوليو المقبل&quot;.</p>\r\n\r\n<p>وقال مجاهد، في بيان رسمي أمس، إن &quot;ما تردد عن زيادة أسعار تذاكر العيادات الخارجية شائعات لا أساس لها، وعار تماماً عن الصحة&quot;.</p>\r\n\r\n<h1>&nbsp;</h1>', 'storage/app/public/news/1530016947Medicine_for_the_needy_0.jpg', 1, '2018-06-26 14:42:27', '2018-06-26 14:42:27', '2018-06-26 14:42:27', 1, 1, 1, 1);
INSERT INTO `news` VALUES (188, 'مصر والسودان تسلمان ملاحظاتهما على ملء \"سد النهضة\"... وإثيوبيا ترد نهاية الشهر', '<p>اختتمت مجموعة العمل البحثية العلمية المستقلة بشأن &quot;سد النهضة&quot; والمكونة من خبراء مصريين سودانيين وإثيوبيين، اجتماعاتها في الخرطوم أمس، وتناول الاجتماع ملاحظات مصر والسودان بشأن مقترح قدمته إثيوبيا لطريقة &quot;ملء السد&quot;.</p>\r\n\r\n<p>واتفقت الأطراف الثلاثة على أن ترد مجموعة الباحثين الإثيوبيين على ملاحظات مصر والسودان في الاجتماع القادم في أديس أبابا يومي 21 و 22 يوليو المقبل.</p>\r\n\r\n<p>وقال المهندس خضر قسم السيد رئيس الاجتماع والجانب السوداني أمس: إن &quot;روحا ودية سادت المداولات العلمية للمجموعة، اتسمت بالاستناد على الحقائق، مما مكن الاجتماع من الوصول إلى مخرجات اتفق عليها وفق الجدول الزمني والأجندة المحددة&quot;.</p>\r\n\r\n<p>وكان الاجتماع التساعي (وزراء الخارجية، والري، ورؤساء المخابرات في الدول الثلاث) الذي عقد في أديس أبابا في أبريل الماضي، انتهى إلى وثيقة مخرجات منها تشكيل لجنة علمية مستقلة لدراسة الملاحظات على ملء سد النهضة.</p>', 'storage/app/public/news/15300172789dc9e0ecd092a8a3d5b886e38cd35b42.jpg', 1, '2018-06-26 14:47:58', '2018-06-26 14:47:58', '2018-06-26 14:47:58', 1, 1, 1, 1);
INSERT INTO `news` VALUES (189, 'وزير الخارجية يبحث العلاقات الثنائية مع وفد الحزب الشيوعى الصينى', '<p><span dir=\"rtl\">نشر السفير أحمد أبو زيد، المتحدث الرسمى باسم وزارة الخارجية، عبر حسابه على تويتر، صورا من لقاء وزير الخارجية سامح شكرى، مع وفد الحزب الشيوعى الصينى لبحث العلاقات الثنائية.</span></p>', 'storage/app/public/news/1530093801المجلس الشيوعي.jpg', 1, '2018-06-27 12:03:21', '2018-06-27 12:03:21', '2018-06-27 12:03:21', 416, 416, 1, 1);
INSERT INTO `news` VALUES (190, '\"إسكان البرلمان\" تطالب الحكومة بوضع قانون التسجيل العقارى على أولوية أجندتها', '<p><span dir=\"rtl\">قال النائب يسرى المغازى، وكيل لجنة الإسكان بمجلس النواب، إن مشروع قانون التسجيل العقارى من القوانين الهامة المتوقع صدورها خلال الايام القليلة المقبلة، مطالبا الحكومة بوضعه على قائمة اهتماماتها على أن يكون أول مشروع قانون تتقدم به للبرلمان عقب منحها الثقة.</span></p>', 'storage/app/public/news/1530104574النائب يسري.jpg', 1, '2018-06-27 15:02:54', '2018-06-27 15:02:54', '2018-06-27 15:02:54', 416, 416, 1, 1);
INSERT INTO `news` VALUES (191, 'المصيلحى :لا زيادة في أسعار السلع الغذائية بسبب تحريك أسعار الوقود', '<p><span dir=\"rtl\">وفقا لبيان رسمى صادر عن وزارة التموين صباح اليوم، توافق الجميع على جدية المشاركة في المسئولية المجتمعية للقطاع الخاص في هذه المرحلة وتعهدت كافة الأطراف على عدم تحريك أسعار السلع الغذائية بسبب تحريك اسعار الوقود ، حيث تتحمل سلاسل الإمداد من منتجين ومستوردين وتجار جملة وتجزئة أثار تحريك اسعار الوقود على تكاليف النقل</span></p>', 'storage/app/public/news/1530182520وزير التموين.jpg', 1, '2018-06-28 12:42:00', '2018-06-28 12:42:00', '2018-06-28 12:42:00', 416, 416, 1, 1);
INSERT INTO `news` VALUES (192, 'اللواء عباس كامل  رئيسا\" للمخابرات العامة', '<p><span dir=\"rtl\">أدى اللواء عباس كامل اليمين الدستورية أمام الرئيس عبد الفتاح السيسي رئيساً لجهاز المخابرات العامة، والوكيل ناصر فهمى نائباً لرئيس الجهاز.</span></p>', 'storage/app/public/news/1530185078اللواء عباس.jpg', 1, '2018-06-28 13:24:38', '2018-06-28 13:24:38', '2018-06-28 13:24:38', 416, 416, 1, 1);
INSERT INTO `news` VALUES (193, 'مصطفى بكرى: لا أجد مبررا لفرض رسم 50 جنيها على شراء خط المحمول', '<p>قال النائب مصطفى بكرى، عضو لجنة الشؤون الدستورية والتشريعية بمجلس النواب، إنه لا يجد أى مبرر لفرض رسم 50 جنيها عند شراء خط محمول جديد، وهو البند الذى تم استحداثه بمشروع تعديل قانون تنمية الموارد المالية للدولة، قائلا:&quot; أريد تخفيف العبء عن كاهل المواطن &quot;.</p>', 'storage/app/public/1530187816بكري.jpg', 1, '2018-06-28 14:10:16', '2018-06-28 13:32:34', '2018-06-28 14:10:16', 416, 416, 1, 1);
INSERT INTO `news` VALUES (194, 'على عبدالعال يلتقى رئيسة لجنة حقوق الإنسان بالبوندستاج الألمانى', '<p>&nbsp;<span dir=\"rtl\">التقى الدكتور على عبد العال، رئيس مجلس النواب، اليوم، الخميٍس، بمقر إقامته مع النائبة جيدا ينسن، رئيس لجنة حقوق الإنسان بالبوندستاج الألمانى، وذلك فى إطار برنامج زيارته الرسمية إلى ألمانيا.</span></p>\r\n\r\n<p>ف<span dir=\"rtl\">ى بداية اللقاء رحب الدكتور على عبدالعال بالنائبة الألمانية، وأكد على أن وجوده اليوم فى ألمانيا إنما يأتى فى إطار الالتزام الواضح من البلدين على الدفع بعلاقاتهما إلى المزيد من التطور ولاسيما على المستوى البرلمانى، وأشاد الدكتور على عبدالعال بوجود نائبة شابة على رأس لجنة مهمة كلجنة حقوق الإنسان فى البوندستاج، وأشار إلى أن البرلمان المصرى يضم فى عضويته 90 امرأة و33% من الشباب وهو الأمر الذى مهد له دستور 2014.</span></p>', 'storage/app/public/news/1530194149ععععععععع.jpg', 1, '2018-06-28 15:55:49', '2018-06-28 15:55:49', '2018-06-28 15:55:49', 416, 416, 1, 1);
INSERT INTO `news` VALUES (195, 'لجنة الخطة بالبرلمان توافق على تعديلات قانون الجمارك المقدمة من الحكومة', '<p><span dir=\"rtl\">وافقت لجنة الخطة والموازنة بمجلس النواب، خلال اجتماعها اليوم الاثنين، على مشروع قانون بتعديل يعض أحكام قانون الجمارك الصادر بقرار رئيس الجمهورية بالقانون رقم 66 لسنة 1962 وتعديلاته، ومن المتوقع عرضه على الجلسة العامة المقرر لها غدا برئاسة الدكتور على عبد العال.</span></p>', 'storage/app/public/news/1530528699ممممممممممممممم.jpg', 1, '2018-07-02 12:51:39', '2018-07-02 12:51:39', '2018-07-02 12:51:39', 416, 416, 1, 1);
INSERT INTO `news` VALUES (215, 'الصحة: مستشفى \"الشيخ زايد\" بديلاً عن \"الحسين الجامعى\" لاستمرار تقديم الخدمة', '<p><span dir=\"rtl\">وجهت الدكتورة هالة زايد، وزيرة الصحة والسكان، بوضع مستشفى الشيخ زايد أل نهيان تحت تصرف عميد كلية الطب جامعة الأزهر، الدكتور أحمد سليم، لاستقبال الأطقم الطبية والحالات المرضية بمستشفى الحسين الجامعى، حتى الانتهاء من أعمال الصيانة والإصلاحات بالمستشفى نتيجة الحريق، الذى نشب بها أمس.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531049112هاله زااايد.jpg', 1, '2018-07-08 13:25:12', '2018-07-08 13:25:12', '2018-07-08 13:25:12', 416, 416, 1, 1);
INSERT INTO `news` VALUES (197, 'البرلمان يوافق على تعديل قانون ضريبة الدخل فى مجموعه ويرسله لمجلس الدولة', '<p><span dir=\"rtl\">وافق مجلس النواب خلال الجلسة العامة اليوم برئاسة الدكتور على عبد العال من حيث المبدأ على مشروع قانون باستبدال أحكام المادة رقم (42) من القانون رقم (91) لسنة 2005 بإصدار قاون الضرائب على الدخل، والمُقدم من النائبة ميرفت ألكسان عضو اللجنة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وجاءت الموافقة مجلس النواب على مشروع القانون فى مجموعه، وأرسل البرلمان المشروع لمجلس الدولة لأخذ رأى قسم الفتوى والتشريع فيه.</span></p>', 'storage/app/public/news/1530544357مجلس النواب.jpg', 1, '2018-07-02 17:12:37', '2018-07-02 17:12:37', '2018-07-02 17:12:37', 416, 416, 1, 1);
INSERT INTO `news` VALUES (198, 'اتفاق مصري بولندي لإنشاء منطقة صناعية في \"اقتصادية\" قناة السويس', '<p>اتفقت بولندا مع مصر على إنشاء منطقة صناعية متخصصة بالمنطقة الاقتصادية لقناة السويس. ووقع المهندس عمرو نصار وزير التجارة والصناعة وجيرزى كوشينسكى وزير الاستثمار والتنمية البولندى أمس البيان الختامى للجولة الأولى للمشاورات الثنائية بين البلدين والتى عقدت بالقاهرة صباح أمس، وهى الجولة المقرر عقدها سنويا بكلا البلدين بالتناوب.</p>\r\n\r\n<p>وقال وزير التجارة والصناعة: إن الجولة الأولى للمشاورات تضمنت توقيع اتفاق تعاون بين بنك مصر والبنك المصري لتنمية الصادرات مع بنك التنمية البولندي&nbsp;BGK&nbsp;بهدف التعاون في المجال المصرفي من خلال تمويل المشروعات المشتركة.</p>\r\n\r\n<p>وأكد أنه تم الاتفاق على اتخاذ الإجراءات النهائية الخاصة بتوقيع اتفاق تعاون بين وزارة الزراعة المصرية ونظيرتها البولندية يستهدف تعزيز التعاون المشترك في المجالات الزراعية المختلفة ونفاذ المنتجات الزراعية المصرية إلى السوق البولندية، وإنشاء منطقة صناعية بولندية بالمنطقة الاقتصادية لقناة السويس.</p>\r\n\r\n<p>وأوضح نصار أنه تم بحث إمكانية عودة الطيران المنتظم بين القاهرة ووارسو من جانب شركة الخطوط البولندية&nbsp;&nbsp;LOT، إلى جانب الترويج للسياحة العلاجية في مصر، وإطلاق عدد من القوافل السياحية المصرية إلى أهم المدن البولندية للتواصل مع الشركات السياحية البولندية الكبرى والترويج لملف السياحة في مصر.</p>', 'storage/app/public/news/15306098188931.jpg', 1, '2018-07-03 11:23:38', '2018-07-03 11:23:38', '2018-07-03 11:23:38', 1, 1, 1, 1);
INSERT INTO `news` VALUES (199, 'الاثنين الموافق 2 يوليو \"التموين\" تنفى إلغاء صرف الأرز على البطاقات التموينية', '<p><span dir=\"rtl\">نفت وزارة التموين، ما أثير من أنباء عن إلغاء صرف الأرز على البطاقات التموينية، مُؤكدة على توافر كافة السلع التموينية فى جميع المنافذ التموينية، وعدم إلغاء أى سلعة من على البطاقات التموينية، بل على العكس فقد تم إضافة نوع زيت جديد، و4 أنواع من المكرونة ليختار من بينها المواطن، مشيرة إلى أن تلك الأنباء شائعات لا أساس لها من الصحة تستهدف إثارة البلبلة بين المواطنين.</span></p>', 'storage/app/public/news/1530696084علي مصلحي.jpg', 1, '2018-07-04 11:21:24', '2018-07-04 11:21:24', '2018-07-04 11:21:24', 416, 416, 1, 1);
INSERT INTO `news` VALUES (208, 'الأربعاء، 04 يوليه:وزراء الاستثمار والإنتاج الحربى والرياضة يتفقون على دعم مشروعات الشباب', '<p><span dir=\"rtl\">عقدت الدكتورة سحر نصر، وزيرة الاستثمار والتعاون الدولى، واللواء محمد العصار، وزير الإنتاج الحربى، والدكتور أشرف صبحى، وزير الشباب&nbsp; والرياضة، اجتماعا بمقر وزارة الاستثمار والتعاون الدولى بصلاح سالم، لبحث التعاون بين الوزارات الثلاثة فى دعم مشروعات&nbsp; الشباب، تنفيذا&nbsp; لتوجيهات&nbsp; الرئيس عبد الفتاح السيسى، بدعم هذه المشروعات.</span></p>', 'storage/app/public/news/1530785593وزير الانتاج.jpg', 1, '2018-07-05 12:13:13', '2018-07-05 12:13:13', '2018-07-05 12:13:13', 416, 416, 1, 1);
INSERT INTO `news` VALUES (209, 'تعرف على ملتقى الشباب العالمى المقام فى الغردقة بمشاركة 120 دولة × 8 معلومات', '<p><span dir=\"rtl\">تقام بأحد فنادق منطقة مكادى شمال مدينة سفاجا ورش عمل الملتقى العالمى السنوى للشباب فى دورته الـ70، برعاية وزارتى الشباب والرياضة والسياحة، بحضور عدد كبير من الشباب.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\"><strong>تعرف على الملتقى فى 8 معلومات الأتية:&nbsp;</strong></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">1- ينظم الملتقى مؤسسة أيسيك العالمية للمرة الثانية فى مصر.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">2- يشارك فى الملتقى العالمى السنوى للشباب 800 شاب من 120 دولة جميعهم إعمارهم أقل من 30 سنة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">3- يرعى الملتقى وزارتى الشباب والرياضة والسياحة ومحافظة البحر الأحمر.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">4- حضر الجلسة الافتتاحية للمؤتمر التى أقيمت بأحد فنادق جنوب الغردقة اللواء أحمد عبد الله محافظ البحر الأحمر، والدكتورة أمل جمال مدير الإدارة المركزية بوزارة الشباب والرياضة.&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">5- قال عبد الرحمن أيمن رئيس مؤسسة ايسيك المنظمة للملتقى فى مصر، أن الهدف الرئيسى من الملتقى تنمية المهارات القيادية للشباب ونشر السلام فى العالم والاستدامة والتنوع فى الثقافات.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">6- تقام ورش عمل الملتقى العالمى السنوى للشباب فى دورته الـ70، فى موضوعات &quot;الاستدامة، وريادة الأعمال، والتنوع، وتوظيف الشباب&quot;.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">7- تم تنظيم الملتقى العالمى السنوى للشباب للمرة الأولى عام 2013 وتلك المرة تعد الثانية لمصر.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">8- تعمل مؤسسة إيسيك المنظمة للملتقى العالمى السنوى للشباب فى مصر منذ عام 1984 وتم تنظيم الملتقى مرتين بها.</span></p>', 'storage/app/public/news/1530785728الملتقي.jpg', 1, '2018-07-05 12:15:28', '2018-07-05 12:15:28', '2018-07-05 12:15:28', 416, 416, 1, 1);
INSERT INTO `news` VALUES (207, 'سفير فلسطين يشكر السيسي على السماح بعلاج الجرحى الفلسطينيين بالقاهرة', '<p><span dir=\"rtl\">زار سفير دولة فلسطين بالقاهرة ومندوبها الدائم لدى جامعة الدول العربية السفير دياب اللوح، اليوم الثلاثاء، الجرحى الفلسطينيين الجدد وهم 13 حالة حيث وصلوا مصر لتلقى علاجهم فى إحدى المستشفيات بالجيزة.</span></p>', 'storage/app/public/news/1530784564السفيرررر.jpg', 1, '2018-07-05 11:56:04', '2018-07-05 11:56:04', '2018-07-05 11:56:04', 416, 416, 1, 1);
INSERT INTO `news` VALUES (204, ': الاثنين الموافق  2 يوليو: ننشر نص تعديلات الحكومة على قوانين المنشآت الفندقية ومكافحة الإرهاب والمرور', '<p><span dir=\"rtl\">مشروع قانون مقدم من الحكومة بتعديل بعض أحكام القوانين رقم 1 لسنة 1973 فى شأن المنشآت الفندقية والمرور الصادر بالقانون رقم 66 لسنة 1973 ومكافحة الإرهاب الصادر بالقانون رقم 94 .</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">ويهدف مشروع القانون، إلى مواجهة خطر جرائم الإرهاب الذى يهدد الأسس التى تميز الدولة، كما يسعى لسد الطريق أمام مختلف صور الإيواء للعناصر الإرهابية أو توفير الملاذ الآمن لهم أو الوسائل التى تساعدهم على التخفى بعيدا عن أعين الجهات المختصة بتنفيذ أحكام القانون بالدولة، كما يشدد العقوبات على بعض الجرائم الواردة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">يذكر أن اللجنة التشريعية بمجلس النواب وافقت اليوم خلال اجتماعها برئاسة النائب أحمد الشريف، م</span>ن حيث المبدأ على المشروع وأجلت مناقشة المواد لاجتماع&nbsp; القادم.</p>', 'storage/app/public/1530783104الجنه التشريعيه.jpg', 1, '2018-07-05 11:32:20', '2018-07-05 11:30:15', '2018-07-05 11:32:20', 416, 416, 1, 1);
INSERT INTO `news` VALUES (205, 'على عبد العال يرفع الجلسة العامة.. ورئيس الوزراء يلقى بيان الحكومة غدا', '<p><span dir=\"rtl\">رفع الدكتور على عبد العال، رئيس مجلس النواب، الجلسة العامة الصباحية المنعقدة اليوم الاثنين، بعد الموافقة على مشروع القانون المٌقدم من النائبة ميرفت إليكسان على قانون الضريبة على الدخل، ومشروع قانون الحكومة بتعديل بعض أحكام قانون الجمارك.</span></p>', 'storage/app/public/news/1530783316علي عبد العال.jpg', 1, '2018-07-05 11:35:16', '2018-07-05 11:35:16', '2018-07-05 11:35:16', 416, 416, 1, 1);
INSERT INTO `news` VALUES (206, 'الثلاثاء 3 يوليو:  : رئيس الوزراء يلقى بيان الحكومة اليوم أمام نواب الشعب', '<p><strong>التزاما بالدستور والقانون، يُلقى الدكتور مصطفى مدبولى، رئيس مجلس الوزراء، أمام مجلس النواب، بيان الحكومة اليوم الثلاثاء، لاسيما وأن المدة القانونية المحددة بـ20 يوما لتقديم الحكومة برنامجها إلى البرلمان من تاريخ تشكيلها تنتهى غداً، لا سيما وأنها أدت اليمين الدستورية فى 14 يونيو الماضى</strong><strong><span dir=\"LTR\">.</span></strong></p>', 'storage/app/public/news/1530783472رئيس الوزراء.jpg', 1, '2018-07-05 11:37:52', '2018-07-05 11:37:52', '2018-07-05 11:37:52', 416, 416, 1, 1);
INSERT INTO `news` VALUES (210, 'اليوم :\"التضامن\" تبدأ اليوم صرف معاشات بنك ناصر ومكاتب البريد بعلاوة 15%', '<p><span dir=\"rtl\">تبدأ وزارة التضامن الاجتماعى صرف معاشات شهر يوليو، وذلك للمعاشات المحول صرفها على بنك ناصر الاجتماعى، ومكاتب البريد شاملة الزيادة المقررة وعددها 3547879 حالة بقيمة إجمالية 5.8 مليار جنيه اعتبارًا من اليوم الخميس 5 يوليو.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وكانت غادة والى وزيرة التضامن الاجتماعى، قد أعلنت عن صرف معاشات شهر يوليو فى مواعيدها المقررة شاملة الزيادة التى تقررت اعتبارًا من يوم الأحد الماضى أول شهر يوليو، وفقًا للقانون رقم 99 لسنة 2018، وذلك بنسبة 15% بحد أدنى 150 جنيهًا وحد أقصى 625 جنيهًا ورفع الحد الأدنى للمعاشات إلى 750 جنيهًا، وتحسب الزيادة المستحقة على المعاش الشامل وما أضيف إليه من زيادات حتى 30/6/2018، ويبلغ إجمالى عدد أصحاب المعاشات والمستفيدين عنهم 9.5 مليون مواطن.</span></p>', 'storage/app/public/news/1530786753وزيره التضامن.jpg', 1, '2018-07-05 12:32:33', '2018-07-05 12:32:33', '2018-07-05 12:32:33', 416, 416, 1, 1);
INSERT INTO `news` VALUES (211, 'مرفت ألكسان: تعديل التصرفات فى الضريبة العقارية يسد الثغرات بقوانين الضرائب', '<p>قالت النائبة مرفت الكسان، عضو لجنة الخطة والموازنة، عضو مجلس النواب عن حزب حماة الوطن، فى تعليقها على موافقة البرلمان على مشروع قانون المقدم منها فى شأن استبدال أحكام المادة رقم (42 (من القانون رقم (91 (لسنة 2005 بإصدار قانون الضرائب&nbsp; علي&nbsp;<span dir=\"rtl\">&nbsp;الدخل: &quot;الغرض من التعديل سد الثغرات فى قوانين الضرائب ومادة مهمة وتؤدى فى النهاية لزيادة الايرادات الضريبية والحصيلة الضريبة ويزود ايرادات الدولة&quot;.</span></p>', 'storage/app/public/news/1530787286النائبه.jpg', 1, '2018-07-05 12:41:26', '2018-07-05 12:41:26', '2018-07-05 12:41:26', 416, 416, 1, 1);
INSERT INTO `news` VALUES (212, 'النائبة أنيسة حسونة: ثقتنا فى الحكومة مشروطة بالإنجاز', '<p><span dir=\"rtl\">أكدت النائبة أنيسة عصام حسونة أن بيان الحكومة الذى ألقاه د مصطفى مدبولي، رئيس الوزراء أمام البرلمان، يتضمن حلولا مقترحه لمشكلات مستعصية، أبرزها برامج حماية الفئات الأولى بالرعاية وتمكين الأسر اجتماعيا واقتصاديا، الذى يستهدف رفع كفاءة المساكن الريفية، وأيضا توفير السلع التموينية فى جميع المناطق وخاصة الشعبية من خلال 6 آلاف منفذ لوزارة التموين بخلاف منافذ القوات المسلحة ووزارة الداخلية وتحيا مصر.</span></p>', 'storage/app/public/news/1530788090انيسه.jpg', 1, '2018-07-05 12:54:50', '2018-07-05 12:54:50', '2018-07-05 12:54:50', 416, 416, 1, 1);
INSERT INTO `news` VALUES (213, 'الجريدة الرسمية تنشر قواعد صرف العلاوات الدورية والاستثنائية للعاملين بالدولة', '<p><span dir=\"rtl\">نشرت الجريدة الرسمية قرار الدكتور محمد معيط، وزير المالية،&nbsp;بشأن قواعد صرف العلاوة الدورية والعلاوة الخاصة والعلاوة الاستثنائية للموظفين والعاملين بالدولة اعتبارا من يوليو 2018.</span></p>', 'storage/app/public/news/1530789392وزير الماليه.jpg', 1, '2018-07-05 13:16:32', '2018-07-05 13:16:32', '2018-07-05 13:16:32', 416, 416, 1, 1);
INSERT INTO `news` VALUES (214, 'المالية: إلزام الممولين والمسجلين بالضرائب بالتقدم إلكترونيا أول أكتوبر', '<p><span dir=\"rtl\">أصدر الدكتور محمد معيط، وزير المالية قرارا وزاريا لإلزام المجتمع الضريبى بتقديم جميع الإقرارات الضريبية إلكترونيا على أن يبدأ ذلك تجريبيا فى الفترة من يوليو الحالى ولمدة 3 أشهر ليصبح بعدها إلزاميا من أول أكتوبر المقبل.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وقال وزير المالية، فى بيان منذ قليل، إن القرار يأتى ضمن جهود تحديث وتطوير دورة العمل بمصلحة الضرائب والأخذ بالآليات الإلكترونية الحديثة التى تطبقها كبرى دول العالم بما يسهم فى أحكام الرقابة على الإيرادات العامة وعمليات تلقى وفحص الإقرارات الضريبية.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/1530789880الماليه.jpg', 1, '2018-07-05 13:24:40', '2018-07-05 13:20:48', '2018-07-05 13:24:40', 416, 416, 1, 1);
INSERT INTO `news` VALUES (216, 'على مذهب السوشيال ميديا.. زوج يطلق زوجته برسالة على \"الواتس آب\"', '<p><span dir=\"rtl\">5 سنوات شقى فى منزل الزوجية لم تكن تكفى الزوج&quot;صلاح.أ.ط&quot;، ليعتق زوجته ويكف عن التسبب لها فى الأذى بعد أن تفنن طوال زواجها فى تعذيبها والتعدى عليها ضربا وفق المحاضر والبلاغات التى حررتها الزوجة أمام قسم شرطة أكتوبر، بعد أن تسبب لها بالإجهاض 3 مرات وحرمها من الأمومة وحقوقها الشرعية بسبب عنفه وجنونه.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأكدت الزوجة&quot;حنان.ع.ح&quot; فى الدعوى القضائية التى أقامتها أمام محكمة الأسرة بأكتوبر وطالبت فيها بإثبات تطليقها من زوجها ومنحها حقوقها الشرعية من نفقة وعدة ومتعة: تزوجته بعد ضغط أهلى على للموافقة بسبب كثرة شقيقاتى ورغبتهم فى أن يخف الحمل عنهم وأترك مكان فى ظل الزحام فى منزلنا الذى يحتوى على غرفتين فقط لسبع أبناء ووالدى ووالدتى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتتابع: أنا حاصلة على دبلوم وأعمل فى محل فى منطقة وسط البلد لبيع الملابس وكنت اتقاضى مبلغ يساعدنا وأهلى على محاولة تدبير نفاقتنا وشقيقاتى وأشقائى الأصغر سنا، وعندما ظهر زوجى الذى يعمل تاجرا ويمتلك شقة كبيرة وأموال تنتشل أهلى من الحياة الصعبة وافقوا عليه رغم زواجه بزوجتين غيرى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضافت حنان: ذهبت للعيش معه فى منزل الزوجية وهناك رأيت حقيقة الوهم الذى باعه لى أهلى، وصدقته فالمنزل الكبير والمال ملك لضرتى وأنا فقط خادمة تزوجها زوجها للمتعة والتكفل بعمل المنزل .</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشارت الزوجة: &nbsp;عاملنى كأننى سلعة أشتراها وأخذ غرضه منى وهرب وجعلنى فى موقف لا أحسد عليه بعد أن عدت لأهلى فارغة اليدين، وكل ما أسمعه عبارات المواساة والتعاطف معى والريبة منى من قبل أهلى بسب خوفهم من جبروت زوجى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأكملت فى دعواها التى حملت رقم 5132 لسنة 2018 :كنت كثيرا ما أتمارض لكى لا أتعرض للإهانات على يد زوجى الذى يصب عليه غضبه والضغوط التى عليه من عمله وزوجتيه، وبسبب اليئس من الحياة وبعد إجهاضى على يد زوجى 3 مرات تركت المنزل وهربت.</span></p>\r\n\r\n<p><span dir=\"rtl\">واستطردت: هدد أهلى فأجبرونى للرجوع له وهناك تفنن فى إيذائى وكاد أن يخنقنى فى إحدى المرات بحزامه ولم يتدخل أحد لنجدتى، وبعدها ذهبت وحررت بلاغا ضده لأول مرة حتى أتصدى لعنفه ضدى ومن لحظتها وأصبح يلاحقنى فى&nbsp; كل مكان للإقتصاص منى وطلقنى عبر التليفون أثناء مكالمتى وأرسل لى رسالة على &quot;الواتس آب&quot;، ورفض أن يثبت ذلك حتى يتركنى &quot;كالبيت الوقف&quot; كما يقول لى دائما.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531049394اسره.jpg', 1, '2018-07-08 13:32:12', '2018-07-08 13:29:54', '2018-07-08 13:32:12', 416, 416, 1, 1);
INSERT INTO `news` VALUES (217, 'تعرف على أهم 7 معلومات حول رئيس محكمة استئناف القاهرة الجديد', '<p><span dir=\"rtl\">تولى المستشار محمد رضا شوكت، رئاسة محكمة استئناف القاهرة، خلفا للمستشار عادل بعبش، الذى بلغ السن القانونية للتقاعد، وذلك بعد اعتماد مجلس القضاء الأعلى برئاسة المستشار مجدى أبو العلا قرار تعيينه.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">ويقدم &quot;اليوم السابع&quot;، أهم&nbsp; 7 معلومات عن رئيس محكمة استئناف القاهرة الجديد:</span></p>\r\n\r\n<p><span dir=\"rtl\">1 - تخرج فى كلية الحقوق جامعة القاهرة عام 1969 بتقدير جيد جدًا مع مرتبة الشرف.</span></p>\r\n\r\n<p><span dir=\"rtl\">2 - التحق بالنيابة العامة عام 1970، وتدرج فى النيابة العامة من مدير إلى رئيس للنيابة، ثم محاميًا عامًا.</span></p>\r\n\r\n<p><span dir=\"rtl\">3 - التحق بإدارة التفتيش بالنيابة العامة حتى تمت ترقيته إلى رئيس محكمة استئناف عام 1992</span></p>\r\n\r\n<p><span dir=\"rtl\">4 - عمل رئيسا لمحكمة الجنايات لمدة 22 عاما.</span></p>\r\n\r\n<p><span dir=\"rtl\">5 - تم ندبه مساعدا لوزير العدل لشئون ديوان الوزارة، ثم تمت ترقيته لمنصب مساعد أول لوزير العدل عام 2015 ، فى عهد الوزير السابق أحمد الزند.</span></p>\r\n\r\n<p><span dir=\"rtl\">6 - تولى رئاسة محكمة استئناف قنا، ثم محكمة استئناف الإسماعلية،&nbsp;وفى أغسطس 2017 تولى بحكم أقدميته رئاسة محكمة استئناف المنصورة.</span></p>\r\n\r\n<p><span dir=\"rtl\">7 - بداية من أول يوليو 2018 فهو رئيس محكمة استئناف القاهرة، وعضو بمجلس القضاء الأعلى.</span></p>', 'storage/app/public/news/1531049999المستشلررررررررررر.jpg', 1, '2018-07-08 13:39:59', '2018-07-08 13:39:59', '2018-07-08 13:39:59', 416, 416, 1, 1);
INSERT INTO `news` VALUES (218, 'بهاء أبو شقة: رفع دور الانعقاد الثالث 15 يوليو المقبل بعد الرد على بيان الحكومة', '<p><span dir=\"rtl\">قال المستشار بهاء أبو شقة رئيس لجنة الشئون الدستورية والتشريعية بمجلس النواب، إن دور الانعقاد الثالث، من المنتظر أن ينتهى يوم 15 يوليو المقبل، وذلك بعد جلسة البرلمان للرد على بيان الحكومة ومن ثم تكون هذه هى آخر جلسة للبرلمان فى دور الانعقاد الثالث.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأضاف أبو شقة فى تصريحات لــ&quot;اليوم السابع&quot;، أنه كان من المفترض أن ينتهى دور الانعقاد الثالث وفقا للائحة الداخلية للبرلمان فى 30 يونيو الماضى، لكن تم تأجيل انتهاء دور الانعقاد حتى يوم 15 يوليو، وذلك حتى يتم الرد على بيان حكومة المهندس مصطفى مدبولى .</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح أبو شقة، أنه بذلك سيتم تأجيل مناقشة قانون الإجراءات الجنائية وباقى القوانين الهامة العالقة لدور الانعقاد الرابع، والذى من المنتظر أن يبدأ فى أول أكتوبر المقبل، لحسمها أول أيام عودته.&nbsp;</span></p>', 'storage/app/public/news/1531054230ابو شقه.jpg', 1, '2018-07-08 14:50:30', '2018-07-08 14:50:30', '2018-07-08 14:50:30', 416, 416, 1, 1);
INSERT INTO `news` VALUES (219, 'أمين سر لجنة الشباب البرلمان: جمهور المصرى واعي.. ولابد من عودته إلى المدرجات', '<p><span dir=\"rtl\">علق فوزى فتى أمين سر لجنة الشباب والرياضة بمجلس النواب، على قرار اتحاد الكرة بتشكل لجنة&nbsp; لعقد سلسلة اجتماعات مع الجهات الأمنية، بهدف عودة الجماهير للملاعب مع انطلاق الدورى العام، قائلا: جمهور مصرى واعي وبيفهم.. وأكبر من أى حد .. المهم صدق الإرادة السياسية فى عودتهم.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشار أمين سر لجنة الشباب والرياضية بمجلس النواب، إلى أن النوادي المصرية تضررت كثيرًا من قرار تأخير عودة الجماهير إلى المدرجات ماليًا، لافتًا إلى أن أمر تنظيم الجماهير فى غاية البساطة، تتحمل فيه &nbsp;النوادى مسئولية تأمين الجماهير، وضبط الأمن داخل النوادى، بينما يتحمل الأمن مسئولية النظام فى الخارج.</span></p>', 'storage/app/public/news/1531127320ملاعب.jpg', 1, '2018-07-09 11:08:40', '2018-07-09 11:08:40', '2018-07-09 11:08:40', 416, 416, 1, 1);
INSERT INTO `news` VALUES (220, 'تعرف على خطوات استخراج رخص القيادة أثناء تغيير محل الإقامة × 8 نقاط', '<p><span dir=\"rtl\">توجد العديد من الأوراق والإجراءات التى يجب على سائقى السيارات القيام بها فى حالة تغيير محل الإقامة فى رخصة القيادة والتسيير، لعدم المساءلة القانونية للمواطنين، ولكى تكون الإجراءات صحيحة يجب اتباع تلك الخطوات:</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">ـ يجب على المرخص له عند تغيير محل إقامته المثبت فى الرخصة بدائرة المحافظة التى يقيم فيها إخطار قسم المرور المختص بذلك خلال 30 يومًا من اليوم التالى لتاريخ التغيير.</span></p>\r\n\r\n<p><span dir=\"rtl\">ـ تقديم سند لقسم المرور المختص لإثبات محل إقامته الجديد فى حكم المادة 212 من هذه اللائحة للتأشير به بالرخصة</span></p>\r\n\r\n<p><span dir=\"rtl\">ـ إذا كان تغيير محل الإقامة المثبت فى الرخصة إلى محافظة أخرى فعلى المرخص له أن يتقدم إلى قسم المرور بهذه المحافظة الجديدة خلال المدة المشار إليها فى المادة السابقة بطلب نقل قيد الرخصة على النموذج المعد</span></p>\r\n\r\n<p><span dir=\"rtl\">ـ تقديم مستند مقبول فى إثبات محل الإقامة الجديد فى حكم المادة 212 من اللائحة</span></p>\r\n\r\n<p><span dir=\"rtl\">ـ تقديم أوراق تثبت قيامه بالوفاء بالغرامات المحكوم بها لمخالفة أحكام قانون المرور، وهذه اللائحة صادرة من الجهة المختصة فى دائرة قسم المرور المقيد به المركبة أصلا وشهادة الوفاء بالغرامات.</span></p>\r\n\r\n<p><span dir=\"rtl\">ـ تقديم ما يفيد تعديل وثيقة التأمين الإجبارى من حوادث المركبات بإثبات محل الإقامة الجديد طبقًا للقانون الخاص بذلك</span></p>\r\n\r\n<p><span dir=\"rtl\">ـ يقوم قسم المرور بالمحافظة الجديدة بفحص المركبة للتحقق من مطابقة البيانات المثبتة بالرخصة</span></p>\r\n\r\n<p><span dir=\"rtl\">ـ تحرر نتيجة المطابقة على النموذج المعد لذلك ويصرف تصريح مؤقت لحين ورود ملف المركبة من قسم المرور المقيدة به وتطابق بيانات الفحص مع البيانات المثبتة به.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531135268رخصه.jpg', 1, '2018-07-09 13:21:08', '2018-07-09 13:21:08', '2018-07-09 13:21:08', 416, 416, 1, 1);
INSERT INTO `news` VALUES (221, '\"استرداد أراضى الدولة\" تعلن عن جدول زمنى لإنهاء إجراءات التقنين للجادين', '<p><span dir=\"rtl\"><q>أكد المهندس شريف إسماعيل، مساعد رئيس الجمهورية للمشروعات القومية رئيس لجنة استرداد أراضى الدولة ومستحقاتها، على وضع آلية عمل موحدة تسير عليها كل لجان تقنين أراضى الدولة بالمحافظات تأكيدا على توافق القرارات ومعايير التقييم.</q></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\"><q>وأشار إسماعيل، خلال الاجتماع الأول للجنة برئاسته، إلى أنه سيكون هناك تنسيقا كاملا بين كل جهات الدولة لإنجاز هذا الملف وتقديم التيسيرات الكاملة للمواطنين الجادين فى التقنين مع تشكيل لجان متابعة لعمل المحافظات لضمان إنهاء التقنين فى أسرع وقت وتحصيل حق الدولة</q></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\"><q>واستعرض مساعد الرئيس خلال الاجتماع لتقرير الأمانة الفنية الذى قدمه اللواء عبدالله عبد الغنى عن طلبات التقنين التى تلقتها لجان المحافظات حتى نهاية المهلة المحددة فى 14 يونيو الماضى، حيث أشار التقرير إلى تلقى 265 ألف طلب تقنين تم تسجيل منها على منظومة اللجنة التى تشرف عليها هيئة النظم بالقوات المسلحة 177 ألف طلب وجارى استكمال تسجيل بقية الطلبات بمعرفة المحافظات، كما حدد التقرير عدد من المعوقات التى واجهت لجان التقنين وتم التغلب عليها لصالح التيسير على المواطنين&nbsp;.</q></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\"><q>وخلال الاجتماع اتخذت اللجنة عدد من القرارات من أهمها مخاطبة جميع المحافظات بضرورة تشكيل لجان عمل لسرعة إنجاز إجراءات الفحص والتقييم للطلبات المقدمة لها وإنهاء خطوات التقنين اللازمة وفق جدول زمنى محدد.</q></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\"><q>استمرار المتابعة مع المحافظات لتقديم كافة المساعدات والتيسيرات المطلوبة من خلال اللجنة العليا لدعمها فى هذا الأمر سواء كان دعما فنيا أو قانونيا، الإعداد للموجة العاشرة لإزالة التعديات على الأراضى التى لم يقدم عنها طلبات تقنين على أن يتم التنسيق فى الإزالة بين قوات إنفاذ القانون والمحافظات وكافة الجهات المعنية وأن تتواصل موجة الإزالات بشكل مستمرا لاسترداد حق الدولة، التأكيد على المحافظات بحصر حالات العود فى التعدى على الأراضى التى تمت إزالتها قبل ذلك حتى يتم ضمها لموجات الإزالة القادمة، التأكيد على المحافظات بسرعة الانتهاء من تسجيل 90 ألف طلب تقنين لم تسجل على المنظومة الإلكترونية لاستكمال قاعدة البيانات، والتنسيق مع هيئة المجتمعات العمرانية لدراسة خطة العمل المقترحة لتقنين أوضاع أراضى طريق مصر إسكندرية الصحراوى، بما يضمن استرداد حق الدولة دون الإضرار بالأوضاع المستقرة قانونا.</q></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\"><q>وأشار المهندس شريف إسماعيل، إلى أن اللجنة العليا ستواصل جهودها بالتنسيق مع الجهات المعنية لإتخاذ كل الإجراءات اللازمة وبشكل حاسم للإسراع فى إنجاز التقنين لكل من تتوافر لهم الشروط، وفى الوقت نفسه ستواصل قوات إنفاذ القا</q></span>نون موجات الإزالة.</p>', 'storage/app/public/news/1531145483المهندس.jpg', 1, '2018-07-09 16:11:23', '2018-07-09 16:11:23', '2018-07-09 16:11:23', 416, 416, 1, 1);
INSERT INTO `news` VALUES (222, 'رئيس برلمانية \"حماة الوطن\": الحزب وضع عددا من التوصيات على برنامج الحكومة', '<p>قال اللواء أسامة أبو المجد، رئيس الهيئة البرلمانية لحزب حماة الوطن ، إن اللجنة الداخلية بالحزب المكلفة بدراسة برنامج الحكومة مازالت تعمل على دراسة البرنامج، مؤكدا أن برلمانية حماة الوطن ستوافق على برنامج الحكومة من حيث المبدأ مع وضع عدد من الطلبات والتوصيات للحكومة للعمل على تنفيذها أثناء تنفيذ ما ورد بمحاور برنامج الحكومة.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>وأوضح أبو المجد فى تصريح خاص لـ&quot; اليوم السابع&quot; أبرز الملاحظات التى وضعتها الهيئة البرلمانية لحزب حماة الوطن على برنامج الحكومة، قائلا إنها تتمثل فيما يتعلق بدور قطاع الأعمال والقطاع العام ، حيث لم يحصل على حقه من الموازنة ولم تتضح رؤية الحكومة لرفع مستوى شركات القطاع الأعمال ، بالإضافة إلى ملاحظة تتعلق بالفلاح المصرى والقطاع الزراعى إذ لم يذكر فى بيان الحكومة الإجراءات التى من شأنها رفع مستوى الفلاح.</p>\r\n\r\n<p>وتابع رئيس الهيئة البرلمانية لحزب حماة الوطن، أنه يجرى الآن وضع عدد من الملاحظات من قبل لجنة الحزب المكلفة بدراسة برنامج الحكومة تمهيدا لعرضها بالجلسة العامة المخصصة لدراسة برنامج الحكومة وإصدار توصيات مع الحصول على وعد من الحكومة بتنفيذها.</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531215609رئيس الهئه البرلمانيه.jpg', 1, '2018-07-10 13:30:44', '2018-07-10 11:40:09', '2018-07-10 13:30:44', 1, 1, 1, 1);
INSERT INTO `news` VALUES (223, 'وزيرة الصحة تلزم المستشفيات بإذاعة السلام الجمهورى لتعزيز الانتماء', '<p><span dir=\"rtl\">أصدرت الدكتورة هالة زايد وزيرة الصحة والسكان، قراراً تم تعميمة على جميع مستشفيات الجمهورية، ينص على إذاعة السلام الجمهورى ثم يعقبه قسم الأطباء يومياً، عن طريق الإذاعة الداخلية بكل مستشفى.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأوضحت وزيرة الصحة والسكان فى بيان اليوم، أن هذا القرار يعزز من قيم الانتماء للوطن لجميع المستمعين فى المستشفيات، سواء للمريض أو الأطقم الطبية، مضيفةً أن بث القسم سيذكر الأطباء بمبادئ الإنسانية المنصوص عليها فى القسم، والتى هى أساس أى عملية خدمية نبيلة تقدم للإنسان.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531216571الدكتورة هالة زايد وزيرة الصحة والسكان.jpg', 1, '2018-07-10 11:56:11', '2018-07-10 11:56:11', '2018-07-10 11:56:11', 416, 416, 1, 1);
INSERT INTO `news` VALUES (224, 'تعرف على أهم توصيات البرلمان عن محور \"بناء الإنسان\" ببرنامج الحكومة', '<p><span dir=\"rtl\">صدرت اللجنة الفرعية المنبثقة من اللجنة الخاصة المشكلة بمجلس النواب لدراسة برنامج الحكومة، والمعنية بدراسة المحور الثانى &quot;بناء الإنسان المصرى&quot;، عدة توصيات فى تقريرها عقب موافقتها على برنامج الحكومة، ويرصد &quot;اليوم السابع&quot;، فيما يلى أبرز ما جاء بتقرير لجنة دراسة محور بناء الإنسان المصرى:</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">1- أكدت اللجنة فى تقريرها على ضرورة تحقيق التوازن بين معدلات النمو السكانى والموارد المتاحة للدولة وفقا لما نصت عليه المادة (41) من الدستور .</span></p>\r\n\r\n<p><span dir=\"rtl\">2- الاستفادة من الزيادة السكانية من خلال الاهتمام بالتنمية البشرية وتعزيزها واستغلال طاقات الشباب، مثلما هو الحال في دول مثل الصين والهند، حيث تمثل التنمية البشرية 60% من اقتصاديات تلك الدول.</span></p>\r\n\r\n<p><span dir=\"rtl\">3- وحتمية ضمان حصول الإنسان علي كافة حقوقة ومنها الحق فى السكن والصحة والتعليم.</span></p>\r\n\r\n<p><span dir=\"rtl\">4- أوصت اللجنة بتعويض الأسر المصرية عن ضرر &quot;البطالة&quot; وعمل بطاقات تموينية للأسر قبل رفعها من بطاقة الأسرة المصرية.</span></p>\r\n\r\n<p><span dir=\"rtl\">5- أوصت اللجنة&nbsp; بعمل بطاقات تموينية للأسر قبل رفعها من بطاقة الأسرة المصرية، وتعويض الأسر المصرية عن الضرر الواقع عليها من آثار البطالة.</span></p>\r\n\r\n<p><span dir=\"rtl\">6- أكد التقرير على أهمية إصدار التشريعات التي تعمل علي توازن العلاقة بين المؤجر والمستأجر، وتبسيط إجراءات التقاضي أمام المحاكم وتحديد حد أقصي لزمن كل خصومة قضائية، وإعلان السند القانوني لملكية أرض الدولة والوقف الخيرى وإنهاء النزاع بين الدولة والافراد بطريق يتحقق معه التوازن ومصلحة كل من الطرفين.</span></p>\r\n\r\n<p><span dir=\"rtl\">7- طالبت اللجنة بسرعة إنشاء الهيئات الثلاث بقانون التأمين الصحي وإنشاء لجنة تسعير الأدوية.</span></p>\r\n\r\n<p><span dir=\"rtl\">8- طالبت اللجنة بضرورة الاهتمام بشركات صناعة الأدوية فى قطاع الأعمال وحل مشاكلها واستغلال أصولها للنهوص بها وحل أزمة مديونياتها، سرعة إنشاء لجنة لتسعير علاج الأمراض المختلفة وأخرى لتسعير الأدوية.</span></p>\r\n\r\n<p><span dir=\"rtl\">9-&nbsp; أوصت اللجنة فى تقريرها برفع سن المعاش للأطباء العاملين فى الحكومة من ذوى التخصصات النادرة إلى 65 عاما وتوزيعهم على المناطق النائية والأكثر احتياجاً.</span></p>\r\n\r\n<p><span dir=\"rtl\">10 &ndash; تضمن تقرير اللجنة بتنظيم حقوق المسنين والاستفادة من خبراتهم فى شتى المجالات، تنظيم حقوق رعاية المسنين والاستفادة من خبراتهم فى شتى المجالات.</span></p>\r\n\r\n<p><span dir=\"rtl\">11- إنشاء صندوق خاص للأمراض النادرة مثل أمراض ضمور العضلات وتصلب الشرايين، وأهمية التوعية المستمرة للشباب بخطورة الإدمان وتعاطى المخدرات سواء داخل الأسرة أو المؤسسات التعليمية.</span></p>\r\n\r\n<p><span dir=\"rtl\">12-&nbsp; أوصت اللجنة فى تقريرها بإعداد قاعدة بيانات عن احتياجات سوق العمل وربطها بالمناهج الدراسي، و إنشاء أكاديميات وكليات ومعاهد تكنولوجية تساهم في تخريج دفعات مؤهله لسوق العمل والاقتداء بأكاديمية ناصر العسكرية.</span></p>\r\n\r\n<p><span dir=\"rtl\">13- وضع آليات لخفض نسبة الأمية التي تعهد برنامج الحكومة النزول بها من 29&nbsp; إلي 15% والاستفادة من خريجي الجامعات في هذا الشأن، الربط بين وزارتي الرتبية والتعليم والشباب والرياضية، ومراكز الشباب في تفعيل النشاط الرياضي بالمدارس.</span></p>\r\n\r\n<p><span dir=\"rtl\">14- أوصى تقرير اللجنة بإنشاء قناة تلفزيونية تختص بمواهب الاطفال واحياء وتفعيل الكتاب الإليكتروني، واقترحت اللجنة إنشاء صندوق لتوفير الدعم لمراكز الشباب وتعزيز مواردها علي أن يتم توفير مصادرة ،ـ نسبة 2% من حصيلة مخالفات المرور، نسبة 2% من الأموال المصادرة في قضايا المخدرات، نسبة 2% من حصيلة الرسوم القضائية</span></p>\r\n\r\n<p><span dir=\"rtl\">15- أوصت اللجنة بتوزيع لجان طعن الضرائب في مدن المحافظات للتهسيل علي الممولين في التعامل معها.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531218711مجلس النوااااااااااب.jpg', 1, '2018-07-10 12:31:51', '2018-07-10 12:31:51', '2018-07-10 12:31:51', 416, 416, 1, 1);
INSERT INTO `news` VALUES (225, 'عجز الحضانات مشكلة تضرب أرجاء أسوان.. والصحة: توفير أخرى جديدة بكوم أمبو وإدفو', '<p><span dir=\"rtl\">العجز فى الحضانات مشكلة تؤرق الكثير من الأسر بأسوان بسبب عجز الأهالى عن توفير حضانات لحديثى الولادة والمبتسرين واضطرارهم للجوء للحضانات الخاصة، فضلا عن شكوى الأهالى من عدم قدرتهم على إجراء بعض العمليات الجراحية بالمستشفيات الحكومية الكبرى بأسوان وإضرارهم للسفر خارج المحافظة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">ويقول أحمد الشيخ من أهالى أسوان، إنهم عانوا معاناة شديدة للحصول على حضانة فى مدينة أسوان، لطفل رضيع حيث ترددوا على أكثر من مستشفى، مضيفا أن الحضانات تستقبل يوميا عشرات الأطفال منهم من يحتاج للأكسجين ومشاكل فى التنفس والصفراء ولكن المستشفيات الحكومية لا تستوعب كل هذه الأعداد.</span></p>\r\n\r\n<p><span dir=\"rtl\">يرى عبد الظاهر محمود من أهالى أسوان، أنه لابد من توفير حضانات كافية بالمستشفيات بأنحاء أسوان، مراعاة لظروف الأهالى الصعبة فضلا عن ضرورة التعاون مع المجتمع المدنى لإنقاذ الرضع.</span></p>', 'storage/app/public/news/1531221199الحضانات.jpg', 1, '2018-07-10 13:13:19', '2018-07-10 13:13:19', '2018-07-10 13:13:19', 416, 416, 1, 1);
INSERT INTO `news` VALUES (226, 'إسقاط قيد 350 محاميًا من جداول النقابة يتصدر العناوين اليوم', '<p><span dir=\"rtl\">إسقاط قيد 350 محاميًا من جداول نقابة المحامين.. كان هو الخبر الذي احتل الصدارة، خلال صحف اليوم وموقع النقابه&nbsp;</span></p>\r\n\r\n<p><span dir=\"rtl\">وجاءت كلمة &ldquo;عاشور&rdquo; بختام معهد أسيوط، بالمركز الثاني، وتناولتها؛ &rdquo; الفجر، الوطن، صدى البلد، أهل مصر، فيتو، الدستور، اليوم السابع، دوت مصر، البوابة نيوز، الجمهورية&rdquo;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وحصل بيان النقابة بشأن أتعاب المحاماة، و&rdquo;العامة تعلن تفعيل وثيقة التأمين&rdquo;، و&rdquo;عاشور&rdquo; يكلف أحمد عبد المؤمن بالإدارة القانونية، .</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531228820النقابه.jpg', 1, '2018-07-10 15:20:20', '2018-07-10 15:20:20', '2018-07-10 15:20:20', 416, 416, 1, 1);
INSERT INTO `news` VALUES (227, '530 مليون دولار من البنك الدولي لتطوير قطاع الصحة', '<p>وقعت وزارة الاستثمار والتعاون الدولي اتفاقا مع البنك الدولي؛ تنفيذ تطوير قطاع الرعاية الصحية بقيمة 530 مليون دولار.</p>\r\n\r\n<p>وقالت سحر نصر وزيرة الاستثمار ـ في مؤتمر عقد أمس بمجلس الوزراء ـ إن الاتفاق يتضمن محاور تعزز من الاستراتيجية الوطنية لإصلاح قطاع الصحة، وما اتخذته الدولة بالفعل من خطوات مهمة في هذا الصعيد، خاصةً لمكافحة فيروس سي، وقانون التأمين الصحي الشامل.</p>\r\n\r\n<p>من ناحيتها أشارت هالة زايد وزيرة الصحة، إلى أن المشروع الجديد يتضمن إجراء حملات واسعة النطاق في جميع أنحاء البلاد تشمل نحو 45 مليون مواطن بالغ، حيث سيتم إحالة أي شخص مصاب بالعدوى إلى العلاج المجاني (حوالي 1.5 مليون حالة)، كما سيدعم المشروع أول فحص شامل لمحددات الأمراض غير المعدية &quot;ضغط الدم، مستوي السكر بالدم، ومؤشر كتلة الجسم&quot;.</p>\r\n\r\n<p>وأضافت أن المشروع سيساعد في تعزيز جودة خدمات الرعاية الصحية الأساسية في 600 وحدة صحية و27 مستشفى في 10 محافظات كمرحلة أولى هي: بورسعيد والإسماعيلية والسويس وشمال سيناء وجنوب سيناء وقنا والأقصر وأسوان والإسكندرية ومطروح، وهى محافظات المرحلة الأولي من قانون التأمين الصحي الجديد.</p>', 'storage/app/public/news/15313046402018-636668579579862427-986.jpg', 1, '2018-07-11 12:24:00', '2018-07-11 12:24:00', '2018-07-11 12:24:00', 1, 1, 1, 1);
INSERT INTO `news` VALUES (228, 'التضخم يسجل أعلى مستوى منذ 11 شهرا', '<p>كما كان متوقعًا، تسببت الإجراءات الحكومية الأخيرة بزيادة أسعار المواد البترولية ورفع أسعار الكهرباء &nbsp;والمياه وتذاكر المترو، في ارتفاع معدلات التضخم العام لأعلى مستوى منذ 11 شهرا.</p>\r\n\r\n<p>وأعلن الجهاز المركزي للتعبئة العامة والإحصاء أمس، تسجيل المعدل الشهري لزيادة أسعار المستهلكين 3.5% خلال يونيو مقابل 0.2% خلال مايو، وارتفاع المعدل السنوي إلى 14.4% مقابل 11.1%.</p>\r\n\r\n<p>ويعود ارتفاع التضخم بشكل رئيسي إلى رفع أسعار المواد البترولية بين 17.4% و66.7% بدءا من منتصف يونيو الماضي، لتساهم بنسبة 14.9% في معدلات التضخم خلال شهر يونيو، كما ساهمت أسعار خدمات النقل بنسبة 36.8%، والمياه وخدمات السكن بنسبة 31.8%.</p>\r\n\r\n<p>هذا بينما ساهمت أسعار البطاطس بنسبة 17.9% في هذه المعدلات، والنقل الخاص 24.5%، والموز 15.9%، والبطل والموالح 7% و5.7%، والخبز والبيض والأرز 4.8% و2.5% و2.2% على التوالي.</p>\r\n\r\n<p>وأعلن البنك المركزي تسجيل المعدل الشهري للتضخم الأساسي نسبة 1.6% خلال يونيو مقابل 1.5% خلال مايو السابق عليه، بينما تراجع المعدل السنوي للتضخم الأساسي إلى 10.9% مقابل 11.1% خلال الفترة&nbsp;.</p>\r\n\r\n<p>وترى رضوى السويفي، رئيس قسم البحوث ببنك الاستثمار فاروس، أن معدل التضخم في يونيو جاء أعلى من التوقعات بعد أن رفعت الشركات أسعار منتجاتها لتمرير زيادة الوقود قبل نهاية الشهر، بخلاف ما كان متوقعا من قبل، بحسب ما نقله عنها موقع مصراوي، بينما قال محمد أبو باشا محلل الاقتصاد الكلي ببنك الاستثمار هيرميس، لنفس الموقع، إن أرقام التضخم في يونيو جاءت &quot;متوافقة تماما مع توقعاتنا&quot;.</p>\r\n\r\n<p>وتتوقع السويفي أن يسجل التضخم الشهري معدلا أقل في شهر يوليو يتراوح بين 2.5% و3%، بينما يواصل المعدل السنوي الارتفاع في يوليو ليسجل بين 14.5% و15.5% مع استمرار انعكاس زيادة أسعار الطاقة على السلع والخدمات الأخرى.</p>\r\n\r\n<p>وأعلن صندوق النقد الدولي منذ أيام توقعاته المحدثة لمعدلات التضخم عند مستويات 13.1% بنهاية يونيو المقبل، و14.4% لمتوسط الفترة.</p>\r\n\r\n<p>وفي سياق متصل توقعت &quot;بلتون للأبحاث&quot; أن يبقي البنك المركزي على معدلات الفائدة كما هي خلال اجتماع أغسطس المقبل وحتى نهاية العام وذلك بالتزامن مع ارتفاعات التضخم، مؤكدة أن التضخم ما زال في نطاق مستهدف البنك المركزي المصري رغم ارتفاعه في يونيو الماضي.</p>', 'storage/app/public/news/1531304851b65d67b539e8f3f958de6ca83b9dc34d.jpg', 1, '2018-07-11 12:27:31', '2018-07-11 12:27:31', '2018-07-11 12:27:31', 1, 1, 1, 1);
INSERT INTO `news` VALUES (229, 'مطالب برلمانية بتوعية 6 ملايين شخص سنويا للتحذير من مخاطر الزيادة السكانية', '<p>اتفقت اللجنة البرلمانية المعنية بالرد على برنامج الحكومة، مع المعلومات الواردة فى البرنامج بشان النمو&nbsp; السكانى، وان هذه الزيادة التى بلغت 2.6%، فى السنوات القليلة الماضية تشكل خطرا كبيرا يعرقل الجهود المبذولة من اجل تحسين الأوضاع المعيشية.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>وأوصت اللجنة فى التقرير المزمع مناقشته الأسبوع المقبل بالجلسة العامة، بأهمية ضبط النمو السكانى والانتشار العمرانى، مما يستلزم ضرورة زيادة المبلغ المخصص من الحكومة للتوعية ومواجهة هذه الأزمة والمقدر بـ1.9 مليار جنيه إلى 4 مليارات، وذلك نظرا لخطورة المشكلة ومراعاة زيادة هذا الرقم فى السنوات المقبلة.</p>\r\n\r\n<p>وشددت اللجنة على ضرورة تكاتف المؤسسات فى الدولة ومنظمات المجتمع المدنى لمواجهة الزيادة السكانية، وزيادة عدد الزيارات المستهدفة على أرض الواقع إلى 500 ألف زيارة لاستهداف الوصول لـ6 مليون شخص بدلا من 4 ملايين، مع ضرورة تعيين وتثبيت الرائدات الريفيات ليصبحن موظفات بالوحدة الصحية، مع وضع خطة إعلامية شهرية تفصيلية واضحة لجميع المحافظات المصرية للتوعية من أخطار الزيادة السكانية، إنشاء عدد من المدن الجديدة بصعيد مصر، التوسع فى إنشاء وحدات تنظيم الأسرة.</p>', 'storage/app/public/news/1531390379الجلسه العامه.jpg', 1, '2018-07-12 12:12:59', '2018-07-12 12:12:59', '2018-07-12 12:12:59', 416, 416, 1, 1);
INSERT INTO `news` VALUES (230, 'المواعيد الجديدة للقطارات بعد تعديلها نقلا عن اليوم السابع', '<p>1ـ موعد&nbsp;قطار&nbsp;مكيف رقم 919 القاهرة ـ الإسكندرية هو 14.25 ظهرا بدلا من 14.20</p>\r\n\r\n<p>2ـ موعد&nbsp;قطار&nbsp;رقم 959 القاهرة ـ الإسماعيلية أصبح الساعة 17.40 بدلا من 1.45 ظ</p>\r\n\r\n<p>3ـ تعديل موعد قيام&nbsp;قطار&nbsp;إكسبريس رقم 15 القاهرة ـ الإسكندرية هو 11.25 ص بدلا من 11.30 ص</p>\r\n\r\n<p>4ـ أصبح موعد قيام&nbsp;قطار&nbsp;رقم 839 بنها ـ ميت غمر 20.30 بدلا من 20.15</p>\r\n\r\n<p>5ـ موعد قيام&nbsp;قطار&nbsp;رقم 829 بنها ـ ميت غمر هو 14.00 بدلا من 14.05</p>\r\n\r\n<p>&nbsp;6ـ أما&nbsp;قطار&nbsp;رقم 838 ميت غمر ـ بنها أصبح 20.15 بدلا من 19.50</p>\r\n\r\n<p>&nbsp;7ـ موعد قيام&nbsp;قطار&nbsp;رقم 816 ميت غمر ـ بنها هو 21.00 بدلا من 20.40</p>\r\n\r\n<p>8ـ&nbsp;قطار&nbsp;رقم 841 بنها ـ ميت غمر موعده الجديد هو 22.15 بدلا من 22.00</p>\r\n\r\n<p>9ـ&nbsp;قطار 836 ميت غمر ـ بنها أصبح 19.05 بدلا من 18.55</p>', 'storage/app/public/news/1531396552قطارررررررر.jpg', 1, '2018-07-12 13:55:52', '2018-07-12 13:55:52', '2018-07-12 13:55:52', 416, 416, 1, 1);
INSERT INTO `news` VALUES (231, '\"س & ج\".. كل ما تريد معرفته عن آخر تطورات حادث \"أطفال المريوطية\"', '<p><span dir=\"rtl\">ما زال لغز العثور على جثث 3 أطفال بجوار سور إحدى الفيلل المهجورة بشارع الثلاثينى الجديد، بمنطقة المريوطية لغزًا يحير الأجهزة الأمنية، والتى تعمل بالتنسيق مع جهات التحقيق على كشف الغموض، ومعرفة من وراء تلك الجريمة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">ويجيب &quot;افوكاتو &quot;&nbsp; فى التقرير التالى عن أبرز الأسئلة المتعلقة بالقضية، وآخر تطوراتها، وما توصلت إليه جهات التحقيق وتحريات الأجهزة الأمنية من نتائج:</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">كيف لقى الأطفال مصرعهم؟</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">التقرير المبدئى للطب الشرعى أشار إلى أنه من المرجح أن وفاة الأطفال الثلاثة ربما يرجع إلى حروق واختناق بدخان.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">هل هناك ما يشير إلى أن الأطفال تعرضوا لسرقة الأعضاء؟</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">تقرير الطب الشرعى المبدئى أشار إلى عدم وجود ظواهر تشير إلى تعرض الأطفال الثلاثة لعملية سرقة أعضاء.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">هل هناك مشتبه بهم فى القضية؟</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">لا يوجد مشتبه بهم فى القضية حتى الآن، وأجهزة الأمن قامت بتوسيع دائرة الاشتباه للتوصل لهوية الجناة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">هل هناك صلة قرابة بين الضحايا الثلاثة؟</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">تنتظر النيابة العامة تقرير تحليل عينة &quot;دى.أن.أى&quot; لتحديد ما إذا كان هناك ثمة قرابة بين الأطفال الثلاثة من عدمه.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">هل التقطت كاميرات المراقبة واقعة التخلص من الجثث؟</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">لم تلتقط أى من كاميرات المراقبة واقعة التخلص من الجثث، ولم يكن ثمة كاميرات مراقبة بمحيط الحادث، سوى بعض الكاميرات البعيدة عن موقع العثور على الجثث</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">ماذا قال شهود العيان حول الواقعة؟</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">أكد شهود العيان على اكتشافهم جثث الأطفال الثلاثة صباح يوم أمس، بعدما ارتابوا فى وجود 3 أكياس بلاستيكية سوادء، تنبعث منها رائحة كريهة، وبفتحها عثروا بداخلهم على الأطفال الثلاثة، ولم يشيروا إلى الشخص الذى القى بالجثث الثلاثة بموقع الحادث.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">هل تم التوصل لهوية أهلية الأطفال الثلاثة؟</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">لم يتم التوصل لهوية أسرة الأطفال الثلاثة، وجارى فحص بلاغات التغيب لمعرفة الأجهزة الأمنية للتوصل لهويتهم.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">ما هى قرارات النيابة بشأن الواقعة؟</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">النيابة العامة تنتظر تقرير الصفة التشريحة الخاص بجثامين الأطفال الثلاثة، لتحديد أسباب وفاتهم بشكل مفصل، فضلًا عن تحريات الأجهزة الأمنية التكميلية، لاستكمال التحقيقات.</span></p>', 'storage/app/public/news/1531396758جثث الضحايا.jpg', 1, '2018-07-12 13:59:18', '2018-07-12 13:59:18', '2018-07-12 13:59:18', 416, 416, 1, 1);
INSERT INTO `news` VALUES (232, 'بدء تحصيل رسوم التأمين الصحي من شركات السجائر', '<p>أبلغت الحكومة، ممثلة في وزارة المالية، شركات السجائر المحلية والأجنبية ببدء تطبيق الرسوم الخاصة بقانون التأمين الصحي التي تبلغ 75 قرشا على كل علبة سجائر يتم بيعها، وهى الرسوم التي يتم تحصيلها بدءأ من يوم الخميس.</p>\r\n\r\n<p>وقال مصدر مسئول في إحدى الشركات إن مصلحة كبار الممولين التابعة لوزارة المالية أرسلت خطابات ظهر أمس إلي الشركات تخطرها فيه بالبدء في تحصيل تلك الرسوم.</p>\r\n\r\n<p>وجاء في نص الخطاب: &quot;إيماءً إلي صدور القانون 2 لسنة 2018 بإصدار قانون التأمين الصحي الشامل، ووفقا لأحكام المادة 40 من القانون المشار إليه، وكذا ما ورد بالمادة 42 من اللائحة التنفيذية رقم 909 الصادرة في 8 مايو 2018، رجاء التفضل بالتنبيه بمراعاة تطبيق أحكام القانون واللائحة المشار إليهما.&quot;</p>\r\n\r\n<p>ونص قانون التأمين على أنه يتم تحصيل 75 قرشا من قيمة كل علبة سجائر مبيعة بالسوق المحلية، سواء كانت سجائر محلية أو أجنبية، على أن تتم زيادة تلك القيمة كل 3 سنوات بقيمة 25 قرشا أخرى حتى تصل إلي 150 قرشا.</p>\r\n\r\n<p>ولا تقتصر تلك الرسوم على السجائر، لكنها تمتد إلي منتجات المعسل المستخدم في &quot;الشيشة&quot;، حيث ينص القانون على تحصيل 10% من قيمة كل وحدة مبيعة من مشتقات التبغ بخلاف السجائر.</p>\r\n\r\n<p>وقال المصدر إن السوق تشهد حالة ترقب من جانب الشركات، مؤكدا أنه لم يتم حتى الآن الإعلان عن أى زيادات في الأسعار، لكنه أشار إلي أن أغلب الشركات سيحسم الأمر بحلول ظهر اليوم.</p>\r\n\r\n<p>وأضاف المصدر أن أى ضريبة أو رسوم جديدة تطبق على السجائر تصيب الأسواق بارتباك، وهناك اجتماع اليوم بمصلحة الضرائب للاستقرار على آلية واضحة بشأن كيفية المعاملة الضريبية مع شركات السجائر، بعد تطبيق الرسوم الجديدة.</p>\r\n\r\n<p>وفيما يتعلق بالضرائب الجديدة التي تعتزم وزارة المالية فرضها على شركات السجائر - بعيدا عن رسوم التأمين الصحي - قال المصدر إن مجلس النواب سوف يجتمع الأسبوع المقبل، وقد تتم دراسة الضرائب الجديدة، مضيفا أنه لو لم يحسم البرلمان هذه المسألة الأسبوع المقبل قد يتم تأجيل الضرائب حتى أكتوبر مثلما حدث العام الماضي.</p>\r\n\r\n<p>وتستهدف وزارة المالية تحصيل ضرائب على منتجات التبغ تقدر بنحو 58.5 مليار جنيه في العام المالي الحالي، مقابل 51.4 مليار متوقعة في موازنة العام المالي السابق، أى بزيادة 7.5 مليار جنيه.</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/15315642192a40f519910f1d675352787bf05e0025.jpg', 1, '2018-07-14 12:30:19', '2018-07-14 12:30:19', '2018-07-14 12:30:19', 1, 1, 1, 1);
INSERT INTO `news` VALUES (233, 'سداد مستحقات الأجانب يفتح شهية الشركات للتنقيب في سيناء والسويس', '<p>في ترجمة واضحة لتأثير خطة الحكومة بشأن سداد مستحقات الشركاء الأجانب، وقعت وزارة البترول 3 اتفاقيات للبحث عن البترول والغاز الطبيعى وإنتاجهما بمنطقتى شمال سيناء البحرية وخليج السويس.</p>\r\n\r\n<p>وأعلن طارق الملا وزير البترول قبل أيام انخفاض مستحقات الشركاء الأجانب لأدنى مستوى لها منذ 2010، وهبوطها إلي 1.2 مليار دولار، مشيرا إلى أن وزارته تستهدف سداد كافة المستحقات بنهاية 2019.</p>\r\n\r\n<p>وبحسب بيان صادر عن الوزارة أمس، وقعت الاتفاقية الأولى بمنطقة امتياز غرب جبل الزيت بجنوب خليج السويس مع شركتى جنوب الوادى المصرية القابضة للبترول و&quot;جى إتش بى&quot; البريطانية باستثمارات حدها الأدنى 6 ملايين دولار لحفر 4 آبار جديدة.</p>\r\n\r\n<p>أما الاتفاقية الثانية فقد وقعتها هيئة البترول بمنطقة امتياز رأس فنار بخليج السويس، والاتفاقية الثالثة مع مع شركة &quot;برينكو&quot; البريطانية بمنطقة امتياز شمال سيناء البحرية.</p>\r\n\r\n<p>وقال وزير البترول إن الاستمرار فى توقيع الاتفاقيات البترولية الجديدة مهم لأنه الركيزة الأساسية التى تقوم عليها الأنشطة البترولية من بحث واستكشاف وإنتاج وصناعات تحويلية وصناعات القيمة المضافة.</p>\r\n\r\n<p>وأشار الملا، إلى توقيع أكبر عدد من الاتفاقيات البترولية للبحث عن البترول والغاز مع الشركات العاملة فى مصر منذ نوفمبر 2013، حيث بلغ عددها نحو 83 اتفاقية جديدة ساهمت فى استعادة مصر لمكانتها كلاعب رئيسى فى صناعة البترول والغاز الطبيعى، موضحا أنه يتم حاليا العمل على الانتهاء من إجراءات استصدار 18 اتفاقية جديدة.</p>', 'storage/app/public/news/153156430740d511dc051435611eb6835889c016e0.jpg', 1, '2018-07-14 12:31:47', '2018-07-14 12:31:47', '2018-07-14 12:31:47', 1, 1, 1, 1);
INSERT INTO `news` VALUES (234, 'مجلس النواب يحيل اتفاقيتين للجان المختصة لمناقشتهما', '<p><span dir=\"rtl\">أحال الدكتور على عبد العال رئيس مجلس النواب، اتفاقية بشأن طريقة إقرار الموافقة على إلغاء القرار الجمهورى رقم 161 لسنة 2013 بشأن اتفاقية بيع لأجل بين حكومة جمهورية مصر العربية والبنك الإسلامى للتنمية بشأن توكيل حكومة جمهورية مصر العربية لشراء معدات باسم ونيابة عن البنك ثم بيعها للوكيل لاستخدامها فى مشروع إنشاء المستشفى التعليمى لجامعة الأزهر، والموقعة فى القاهرة بتاريخ 14/1/2013، والصادر بها قرار رئيس جمهورية مصر العربية رقم 242 لسنة 2018، إلى لجنة مشتركة من لجنة الشئون الدينية والأوقاف ومكتبى لجنتى الشئون الدستورية والتشريعية والشئون الاقتصادية.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">&nbsp;كما أحال رئيس مجلس النواب قرار رئيس جمهورية مصر العربية رقم 293 لسنة 2018 بشأن طريقة إقرار الموافقة على اتفاق قرض مشروع دعم إصلاح التعليم فى مصر بين حكومة جمهورية مصر العربية والبنك الدولى لإعادة الإعمار والتنمية بمبلغ 500 مليون دولار أمريكى، والموقع فى واشنطن بتاريخ 21/4/2018، إلى لجنة مشتركة من لجنة التعليم والبحث العلمى ومكتب لجنة الشئون الاقتصادية.</span></p>', 'storage/app/public/news/1531652261التووووووواب.jpg', 1, '2018-07-15 12:57:41', '2018-07-15 12:57:41', '2018-07-15 12:57:41', 416, 416, 1, 1);
INSERT INTO `news` VALUES (235, 'مجلس النواب يبدأ مناقشة مشروع قانون تنظيم إنشاء فروع للجامعات الأجنبية', '<p><span dir=\"rtl\">بدأ مجلس النواب، منذ قليل، خلال الجلسة العامة المنعقدة برئاسة الدكتور على عبد العال، فى مناقشة تقرير اللجنة المشتركة من لجنة التعليم والبحث العلمى ومكتبى لجنتى الخطة والموازنة والشئون الاقتصادية، عن مشروع قانون مقدم من الحكومة بشأن تنظيم إنشاء فروع للجامعات الأجنبية داخل جمهورية مصر العربية والمؤسسات الجامعية.</span></p>\r\n\r\n<p><span dir=\"rtl\">واستعرض الدكتور جمال شيحة، رئيس لجنة التعليم والبحث العلمى بمجلس النواب، تقرير اللجنة أمام الجلسة العامة للبرلمان.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضحت اللجنة المشتركة فى تقريرها، أن هذا المشروع بقانون المعروض يهدف إلى الاستفادة من الخبرات التعليمية والبحثية لتلك الجامعات بما يُساهم فى إحداث نقلة نوعية بتلك المنظومة وربطها بمثيلاتها فى الدول المتقدمة وتوفير فرص التعليم العالى العالمية داخل الدولة لجعلها إحدى منارات التعليم والبحث العلمى على المستوى العالمى.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531658790الدكتور جمال.jpg', 1, '2018-07-15 14:46:30', '2018-07-15 14:46:30', '2018-07-15 14:46:30', 416, 416, 1, 1);
INSERT INTO `news` VALUES (236, 'التعديلات الكاملة بقانون الصحافة والإعلام بعد موافقة مجلس النواب', '<h2><span dir=\"rtl\">وجاءت التعديلات كالتالى :<br />\r\n<br />\r\nأولاً الخاصة بالمجلس الأعلى لتنظيم الإعلام :</span></h2>\r\n\r\n<p><span dir=\"rtl\">1. إضافة مادة جديدة لمواد الإصدار برقم (المادة الرابعة) تتضمن حكم انتقالى باستمرار عمل المجلس الحالى لحين تشكيل مجلس جديد، نصها الآتى: يستمر المجلس الأعلى، بتشكيله الحالى، فى مباشرة مهامه واختصاصاته إلى حين صدور قرار بتشكيله الجديد وفقًا لأحكام القانون المرافق.</span></p>\r\n\r\n<p><span dir=\"rtl\">2. &quot; المادة 12 &quot; : إضافة عبارة &quot; فى الأحوال التى تتطلب ذلك &quot; إلى المادة ليكون نصها الآتى : للصحفى أو للإعلامى فى سبيل تأدية عمله الحق فى حضور المؤتمرات والجلسات والاجتماعات العامة، وإجراء اللقاءات مع المواطنين، والتصوير فى الأماكن غير المحظور تصويرها، وذلك كله بعد الحصول على التصاريح اللازمة فى الأحوال التى تتطلب ذلك.</span></p>\r\n\r\n<p><span dir=\"rtl\">3. &quot; المادة 16 &quot; : استبدال كلمة &quot; ستين &quot; بكلمة &quot; ثلاثين &quot; الواردة فى المادة والخاصة بالمهلة التى يتعين فيها التوفيق بين الصحفى المزمع اتخاذ قرار بفصله وجهة عمله، ليكون نصها : لا يجوز فصل الصحفى أو الإعلامى من عمله إلا بعد التحقيق معه، وإخطار النقابة المعنية بمبررات الفصل، وانقضاء ثلاثين يومًا من تاريخ هذا الإخطار، تقوم خلالها النقابة مرحلة التوفيق دون نجاح، تطبق الأحكام الواردة فى قانون العمل بشأن فصل العامل، ولا يجوز وقف راتبه أو ملحقاته خلال مدة التوفيق.</span></p>\r\n\r\n<p><span dir=\"rtl\">4. &quot; المادة 25 &quot; : حذف عبارة &quot; وإذا ثبتت إدانته تؤول قيمة التبرعات أو الإعانات أو المزايا المادية التى حصل عليها إلى صندوق معاشات النقابة التى يتبعها &quot; الواردة فى عجز المادة ليكون نصها الآتي: مع عدم الإخلال بأحكام قانون العقوبات، يُحظر على الصحفى أو الإعلامى قبول تبرعات أو إعـانات أو مزايا خاصة بسبب أو بمناسبة عمله من أى شخص أو جهة محلية أو أجنبية، سواء كانت بطريقة مباشرة أو غير مباشرة، ويسأل المخالف تأديبيًا.</span></p>\r\n\r\n<p><span dir=\"rtl\">5. &quot; المادة 26&quot;: حذف عبارة &quot; وإذا ثبتت إدانته يلتزم برد قيمة المبالغ أو المزايا التى حصل عليها إلى مؤسسته الصحفية أو الوسيلة الإعلامية التى يعمل بها &quot; الواردة فى عجز المادة ليكون نصها الآتي: يُحظر على الصحفى أو الإعلامى السعى إلى جلب الإعلانات، أو الحصول على أى مبالغ أو مزايا عن طريق نشر الإعلانات أو بثها بأى صفة، أو التوقيع باسمه على مادة إعلانية، أو المشاركة بصورته أو صوته فى إعلانات تجارية مدفوعة الأجر، ويسأل المخالف تأديبيًا.</span></p>\r\n\r\n<p><span dir=\"rtl\">6. &quot;المادة 29 &quot;: إلغاء الحبس الاحتياطى فى قانون تنظيم الصحافة والإعلام واستجاب لمطلب نقابة الصحفيين ليصبح نصها مطابق لنص المادة 71 من الدستور كالأتى: &quot;لا توقع عقوبة سالبة للحرية فى الجرائم التى ترتكب بطريق النشر أو العلانية، فيما عدا الجرائم المتعلقة بالتحريض على العنف أو التمييز بين المواطنين أو بالطعن فى أعراض الأفراد&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">7. &quot; مادة 43&quot;: استبدال نسبة (70% ) بنسبة ( 50% ) الخاصة بنسبة المحررين المقيدين بنقابة الصحفيين فى كل صحيفة ليكون نص الفقرة الآتى : يشترط فى كل صحيفة تطلب ممارسة النشاط الصحفى ألا تقل نسبة المحررين بها من المقيدين بنقابة الصحفيين ابتداء عن 70% من طاقة العمل الفعلية.</span></p>\r\n\r\n<p><span dir=\"rtl\">8. مادة &quot; 59&quot;: استبدال نص الفقرة الثانية بما يضمن تنظيم جميع حالات البث ليكون نصها الآتي: وفى جميع الأحوال، لا يجوز البث أو إعادة البث من خارج المناطق الإعلامية المعتمدة من المجلس الأعلى، واستثناء من ذلك يجوز البث من خارج هذه المناطق بشرط أن يكون للشركة أو المكتب استوديو تباشر منه أعمالها داخل الشركة المصرية لمدينة الإنتاج الإعلامى، وأن يصدر للبث تصريح مسبق من المجلس الأعلى محددًا وقت البث ومكانه.</span></p>\r\n\r\n<p><span dir=\"rtl\">9. &quot; مادة 60 &quot;: إضافة عبارة &quot; يتم تحصيله نقدًا أو بأى طريقة دفع أخرى &quot; إلى عجز المادة لتحديد طرق دفع الرسم المنصوص عليه بالمادة التزاما بالدستور ليكون نصها الآتي: يقدم طلب إنشاء أو تشغيل الوسيلة الإعلامية أو الموقع الإلكترونى، إلى المجلس الأعلى على النماذج التى يضعها، مستوفيًا البيانات والمستندات التى يحددها، ويبت المجلس فى الطلب فى مدة لا تجاوز تسعين يومًا من تاريخ استيفاء الطلب، وذلك مقابل رسم لا يجاوز مائتين وخمسين ألف جنيه للوسيلة الإعلامية وخمسين ألف جنيه للموقع الإلكترونى، يتم تحصيله نقدًا أو بأى طريقة دفع أخرى.</span></p>\r\n\r\n<p><span dir=\"rtl\">10. &quot;مادة 61 &quot;: إضافة عبارة &quot; ومقابل دفع قيمة الرسوم المشار إليها بالمادة (60) من هذا القانون &quot; إلى عجز المادة ليكون نصها الآتي: مدة الترخيص خمس سنوات، ويجوز تجديده بناء على طلب يقدمه المرخص له إلى المجلس الأعلى قبل ستة أشهر من انتهائه، وذلك طبقا للأحكام المبينة فى هذا القانون، ومقابل دفع قيمة الرسوم المشار إليها بالمادة (60) من هذا القانون.</span></p>\r\n\r\n<p><span dir=\"rtl\">11. &quot; مادة 63&quot;: إضافة فقرة جديدة للمادة نصها الآتي: ولا يجوز تأجير مساحات البث داخل الوسائل الإعلامية المرخص بها إلى الغير إلا بتصريح يصدر من المجلس الأعلى وبعد موافقة الجهات المعنية المختصة.</span></p>\r\n\r\n<p><span dir=\"rtl\">12. &quot; مادة 67&quot;: استبدال نصى الفقرتين الثانية والثالثة من المادة ليكون نصهما الآتى: ومع عدم الإخلال بأحكام قوانين الضرائب، يشترط لموافقة المجلس الأعلى على الترخيص لممارسة نشاط تقديم الخدمات الإعلانية التجارية الإلكترونية أو جلب إعلانات من السوق المصرى، أن يكون طالب الترخيص حاصلًا على بطاقة ضريبية. وعلى المجلس إخطار مصلحة الضرائب المصرية بأسماء، ومقار من يتم الترخيص لهم بذلك.</span></p>\r\n\r\n<p><span dir=\"rtl\">وعلى المرخص له إخطار مصلحة الضرائب المصرية بكل إعلان يجريه من خلال أى من الوسائل الإعلانية المنصوص عليها فى الفقرة الأولى من هذه المادة، كما أن عليه تحصيل ضريبة الدمغة المستحقة على صاحب الإعلان من الأشخاص الطبيعيين، وتوريدها إلى مأمورية الضرائب المختصة، وذلك كله طبقا للقواعد والإجراءات التى تحددها اللائحة التنفيذية للقانون.</span></p>\r\n\r\n<p><span dir=\"rtl\">13. &quot; مادة 70&quot;:&nbsp;إضافة عبارة فى عجز البند (2) نصها الآتي: على أن تلتزم الهيئة العامة للاستثمار قبل قيد الشركات ذات رأس المال المصرى أو الأجنبى المشار إليها بالحصول على موافقة الجهات الأمنية والمختصة.</span></p>\r\n\r\n<p><span dir=\"rtl\">14. &quot; مادة 72 &quot; : إضافة الجهاز القومى لتنظيم الاتصالات إلى جوار المجلس الأعلى عند الحصول على ترخيص المنصات الفضائية والرقمية المشفرة (PLATFORM) ليكون نص المادة الآتي: يكون إنشاء المنصات الفضائية والرقمية المشفرة (platform) على أراضى جمهورية مصر العربية والترخيص بها من حق المجلس الأعلى وحده دون غيره، ويصدر الترخيص فى هذه الحالة بعد موافقة الجهاز القومى لتنظيم الاتصالات، ويشترط لمنح الترخيص أن تكون الشركات العاملة فى هذا المجال مملوكة للدولة.</span></p>\r\n\r\n<p><span dir=\"rtl\">15. &quot; مادة 87&quot;:&nbsp;استبدال نص البند (4) بالنص الآتي:ـ المنح والهبات والتبرعات الوصايا التى يقبلها المجلس بموافقة ثلثى عدد أعضائه، ويكون ذلك وفقا للقواعد المقررة قانونا، وبشرط التحقق من عدم تعارض المصالح، وبعد استطلاع رأى الجهات الأمنية المعنية.</span></p>\r\n\r\n<p><span dir=\"rtl\">16. &quot; مادة 94&quot;: استبدال نص المادة بالنص الآتي: يضع المجلس الأعلى لائحة بالجزاءات والتدابير الإدارية والمالية التى يجوز توقيعها على المؤسسات الصحفية والمؤسسات الصحفية القومية والمؤسسات الإعلامية والمؤسسات الإعلامية العامة حال مخالفة أحكام هذا القانون، وإجراءات التظلم منها.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتعتبر هذه اللوائح جزءًا لا يتجزأ من التراخيص أو الموافقات الصادرة أو غيرها من التصرفات والإجراءات والأعمال بين المجلس الأعلى وتلك الجهات.</span></p>\r\n\r\n<h2><span dir=\"rtl\">ويجوز أن تتضمن هذه اللائحة ما يأتي:</span></h2>\r\n\r\n<p><span dir=\"rtl\">1- إلزام المؤسسة أو الوسيلة بإزالة أسباب المخالفة خلال مدة محددة أو إزالتها على نفقتها.</span></p>\r\n\r\n<p><span dir=\"rtl\">2-توقيع الجزاءات المالية المنصوص عليها فى حالة عدم الالتزام بشروط الترخيص.</span></p>\r\n\r\n<p><span dir=\"rtl\">3- منع نشر أو بث المادة الإعلامية لفترة محددة أو بصفة دائمة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفى جميع الأحوال، لا يجوز توقيع أى من تلك الجزاءات أو التدابير إلا فى حالة انتهاك أى مؤسسة صحفية أو إعلامية للقواعد أو المعايير المهنية أو الأعراف المكتوبة( الأكواد) وبعد إجراء الفحص اللازم من المجلس الأعلى ويكون توقيع الجزاء بقرار مسبب.</span></p>\r\n\r\n<p><span dir=\"rtl\">ويتم إخطار النقابة المختصة لاتخاذ الإجراءات اللازمة فى المخالفات التى تقع من أحد أعضائها بمناسبة توقيع المجلس أحد الجزاءات على إحدى الجهات الخاضعة للمجلس الأعلى، وتلتزم النقابة المعنية باتخاذ الإجراءات التأديبية فى مواجهة الشخص المسئول عن المخالفة وفقًا لقانونها.</span></p>\r\n\r\n<p><span dir=\"rtl\">ولذوى الشأن الطعن على هذه الجزاءات أو التدابير أمام محكمة القضاء الإدارى، ولا يقبل الطعن إلا بعد تقديم التظلم منه إلى المجلس الأعلى.</span></p>\r\n\r\n<p><span dir=\"rtl\">17. &quot; مادة 96 &quot;: استبدال عبارة &quot; تشكل جريمة &quot; بعبارة &quot; أو إذا رصدت لجنة تقويم المحتوى انتهاكًا من أية مؤسسة صحفية أو إعلامية للقواعد والمعايير المهنية والأعراف المكتوبة (الأكواب).&quot; الواردة بعجز المادة ليكون نصها الآتي: مع عدم الإخلال باختصاص النيابة العامة، للمجلس الأعلى من تلقاء نفسه، أو بناء على شكوى تقدم إليه، أن يقيم الدعاوى القضائية عن أى مخالفة لأحكام هذا القانون تشكل جريمة.</span></p>\r\n\r\n<p><span dir=\"rtl\">18. &quot; مادة 110 &quot;:&nbsp;مادة(110) مستحدثة&nbsp;إضافة مادة جديدة مستحدثة إلى نهاية القانون تحظر استيراد أجهزة البث عن غير طريق الهيئة الوطنية للإعلام أو الشركة المصرية لمدينة الإنتاج الإعلامى أو الشركة العاملة فى هذا المجال والمملوكة للدولة والعقوبة المقررة لمخالفة ذلك نصها الآتي: يعاقب بالسجن مدة لا تزيد على خمس سنوات كل من قام باستيراد أجهزة البث المحمولة عبر الأقمار الصناعية أو شبكة المعلومات الدولية&quot; الانترنت&quot; ومنها أجهزة ( (SNGو (LIVE VIEW) عن غير طريق الهيئة الوطنية للإعلام أو الشركة المصرية لمدينة الإنتاج الإعلامى أو الشركة العاملة فى هذا المجال والمملوكة للدولة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفى جميع الأحوال تقضى المحكمة بمصادرة الأجهزة والأدوات المضبوطة.&nbsp;فعلى الرغم من خطورة استيراد أجهزة البث المحمولة عبر الأقمار الصناعية أو شبكة المعلومات الدولية&quot; الانترنت&quot; بالمخالفة لأحكام مشروع القانون المعروض، فقد جاء المشروع خلوا من نص عقابى يعالج هذه الحالة، ومن ثم يأتى النص المقترح لتجنب هذا القصور.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">ثانيًا : التعديلات الخاصة بمشروع قانون الهيئة الوطنية للصحافة :</span></h2>\r\n\r\n<p><span dir=\"rtl\">19. إضافة مادة جديدة لمواد الإصدار الخاصة بمشروع قانون الهيئة الوطنية للصحافة، برقم (المادة الثالثة) تتضمن حكم انتقالى باستمرار عمل الهيئة الحالية لحين تشكيلها الجديد، نصها الآتي: تستمر الهيئة، بتشكيلها الحالى، فى مباشرة مهامها واختصاصاتها إلى حين صدور قرار بتشكيلها الجديد وفقًا لأحكام القانون المرافق.</span></p>\r\n\r\n<p><span dir=\"rtl\">20. &quot; مادة 5 &quot;: استبدال نصى البند ين (17، 23) بالنصين الآتيين:</span></p>\r\n\r\n<p><span dir=\"rtl\">- اعتماد قرارات مجالس إدارة المؤسسات الصحفية القومية بمد السن بالنسبة للصحفيين والإداريين والعمال إذا اقتضت حاجة العمل ذلك.</span></p>\r\n\r\n<p><span dir=\"rtl\">- دمج المؤسسات، ودمج وإلغاء الإصدارات الصحفية داخل المؤسسة الواحدة.</span></p>\r\n\r\n<p><span dir=\"rtl\">21. &quot; مادة 16 &quot; : استبدال نص المادة بالنص الآتي: يحظر على رئيس وأعضاء الهيئة القيام بأى عمل يتعارض مع استقلال الهيئة، ويحظر عليهم بوجه خاص قبول هدايا أو عطايا، كما يحظر عليهم القيام بأى أعمال استشارية بمقابل أو بغير مقابل.</span></p>\r\n\r\n<p><span dir=\"rtl\">22. &quot; مادة 23 &quot; : إضافة السطة المختصة بتعيين الأمين العام وتحديد معاملته المالية إلى صدر المادة ليكون نصها الآتي: يصدر بتعيين الأمين العام للهيئة وتحديد معاملته المالية قرار من الهيئة، ويكون مسئولًا أمامها عن سير العمل بالهيئة فنيًا وإداريًا وماليًا.</span></p>\r\n\r\n<p><span dir=\"rtl\">23. &quot; مادة 25 &quot; : استبدال نصى البندين ( 3، 6) ليكون نصهما الآتى :</span></p>\r\n\r\n<p><span dir=\"rtl\">- المنح والهبات والتبرعات الوصايا التى تقبلها الهيئة بموافقة ثلثى عدد أعضائها، ويكون ذلك وفقا للقواعد المقررة قانونا، وبشرط التحقق من عدم تعارض المصالح، وبعد استطلاع رأى الجهات الأمنية المعنية.</span></p>\r\n\r\n<p><span dir=\"rtl\">- 1% من الإيرادات السنوية للمؤسسات الصحفية القومية وتخصص لدعم المؤسسات الصحفية القومية التى تقرر الهيئة حاجتها للدعم.</span></p>\r\n\r\n<p><span dir=\"rtl\">24. &quot; مادة 39&quot; : إضافة كلمة &quot; الصحفية &quot; بعد كلمة الخبرات الواردة بالبند الثالث ليكون نصه الآتي: ستة أعضاء يختارهم رئيس مجلس الإدارة من ذوى الخبرات الصحفية والمالية والاقتصادية والمحاسبية والقانونية.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">ثالثًا : التعديلات الخاصة بمشروع قانون الهيئة الوطنية للإعلام :</span></h2>\r\n\r\n<p><span dir=\"rtl\">25. إضافة مادة جديدة لمواد الإصدار برقم (المادة الثالثة) تتضمن حكم انتقالى باستمرار عمل مجلس إدارة الحالى لحين تشكيل الهيئة الجديد، نصها الآتي: يستمر مجلس إدارة الهيئة، بتشكيله الحالى، فى مباشرة مهامه واختصاصاته إلى حين صدور قرار بتشكيل الهيئة الجديد وفقًا لأحكام القانون المرافق.</span></p>\r\n\r\n<p><span dir=\"rtl\">26. &quot; مادة 6 &quot; : استبدال نص البند (11) بالنص الآتي: الحق الحصرى فى إشارة بث أو إعادة بث البطولات الرياضية المقامة داخل جمهورية مصر العربية، وانتاجها وتصويرها، ويكون لها وحدها الحق فى السماح للآخرين للقيام بذلك مقابل نسبة يجرى الاتفاق عليها بشرط ألا تقل عن 10% من مقابل حق البث، وفى جميع الأحوال، لا يجوز منح هذا الحق إلا للشركات المصرية المملوكة للدولة.</span></p>\r\n\r\n<p><span dir=\"rtl\">27. &quot; مادة 22 &quot; : إضافة السطة المختصة بتعيين الأمين العام وتحديد معاملته المالية إلى صدر المادة ليكون نصها الآتي: يصدر بتعيين الأمين العام للهيئة وتحديد معاملته المالية قرار من الهيئة، ويكون مسئولًا أمامها عن سير العمل بالهيئة فنيًا وإداريًا وماليًا</span></p>\r\n\r\n<p><span dir=\"rtl\">28.&quot; مادة 24 &quot; :&nbsp;استبدال نص البند ( 4 ) بالنص الآتى : المنح والهبات والتبرعات الوصايا التى تقبلها الهيئة بموافقة ثلثى عدد أعضائها، ويكون ذلك وفقا للقواعد المقررة قانونا، وبشرط التحقق من عدم تعارض المصالح، وبعد استطلاع رأى الجهات الأمنية المعنية.</span></p>', 'storage/app/public/news/1531660272الجلسه العااااامه.jpg', 1, '2018-07-15 15:11:12', '2018-07-15 15:11:12', '2018-07-15 15:11:12', 416, 416, 1, 1);
INSERT INTO `news` VALUES (237, 'البرلمان يوافق مبدئيا على منح الجنسية للأجنبى المقيم بوديعة بنكية 5 سنوات', '<p><span dir=\"rtl\">وافق مجلس النواب خلال الجلسة العامة الصباحية برئاسة الدكتور على عبد العال، من حيث المبدأ على مشروع قانون الحكومة بتعديل بعض أحكام القرار بقانون رقم 89 لسنة 1960، بشأن دخول وإقامة الأجانب فى مصر والخروج منها، والقانون 26 لسنة 1975بشأن الجنسية المصرية.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">ويسمح مشروع القانون، للأجنبى المقيم فى مصر بوديعة لمدة 5 سنوات، بالحصول على الجنسية المصرية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال اللواء كمال عامر، رئيس لجنة الدفاع والأمن القومى بمجلس النواب، إن القانون يتماشى مع المصالح المصرية وليس به أى عوار يؤثر على الأمن القومى بل داعم له فى الشق الاقتصادى، مشيرًا إلى أن الهدف من مشروع القانون إرساء نظام جديد لإقامة الأجانب بمصر، حيث نظام &quot;الإقامة بوديعة&quot; لاسيما مع زيادة ظاهرة طلب الأجانب للحصول على الإقامة بجمهورية مصر العربية فى ظل المتغيرات الدولية التى تشهدها المنطقة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتابع عامر، أن إيداع وديعة نقدية بالعملة الأجنبية ليس غاية فى حد ذاتها، إنما مجرد تنظيم لإجراءات النظام المستحدث للإقامة بوديعة، مشيراً إلى إيداع وديعة نقدية بالعملة الأجنبية فى البنوك المصرية استثمار غير مباشر، ويشجع الاستثمار العربى والأجنبى للدخول فى المشروعات الاقتصادية بمصر.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح أن منح الجنسية حال طلبها من الأجنبى المقيم فى مصر بوديعة لمدة 5 سنوات ليست حتمية، إنما جوازية، حيث يتم منح الجنسية لهذا الأجنبى إذا انطبقت عليه شروط الجنسية ووافقت الأجهزة المختلفة على منحه إياها.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531663114مجلس النواب.jpg', 1, '2018-07-15 15:58:34', '2018-07-15 15:58:34', '2018-07-15 15:58:34', 416, 416, 1, 1);
INSERT INTO `news` VALUES (238, 'مجلس النواب يناقش عددا من مشروعات القوانين أبرزها اتفاقية أكبر حقل غاز بمصر', '<p><span dir=\"rtl\">تناقش الجلسة العامة لمجلس النواب اليوم الإثنين برئاسة الدكتور على عبد العال، عددا من القضايا والملفات الهامة، فى مقدمتها أخذ الرأى النهائى على مشروع قانون معـاملة بعـض كبـار قـادة القـوات المسلحـة، ومشروعات قوانين بإصدار قانون تنظيم الصحافة والإعلام والمجلس الأعلى لتنظيم الإعلام وقانون الهيئة الوطنية للصحافة وقانون الهيئة الوطنية للإعلام.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وتناقش الجلسة العامة مشروع قانون بإنشاء صندوق مصر السيادي، بالإضافة إلى 6 مشروعات قوانين بالترخيص لوزير البترول والثروة المعدنية فى التعاقد للترخيص بالبحث عن البترول، منها قرار رئيس مجلس الوزراء بمشروع قانون مقدم من الحكومة بالترخيص لوزير البترول والثروة المعدنية فى التعاقد مع الشركة المصرىة القابضة للغازات الطبيعية وشركة أيوك يرودكشن بى فى وشركة ثروة للبترول للبحث عن الغاز والزيت الخام و استغلالهما فى منطقة نور بشمال سيناء البحرية بالبحر المتوسط.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">كما تناقش الجلسة، قرار رئيس مجلس الوزراء بمشروع قانون مقدم من الحكومة بالترخيص لوزير البترول والثروة المعدنية فى التعاقد مع الهيئة المصرية العامة للبترول وشركة أيوك برودكشنبي. فى. وشركة إينا &ndash; اندستريا نافتا دي. ديل لتعديل اتفاقية الالتزام الصادرة بموجب القانون رقم 4 لسنة 1989 المعدل بالقانون رقم 17 لسنة 1994 للبحث عن البترول واستغلاله فى منطقة رأس قطارة بالصحراء الغربية ج. م. ع.</span></p>', 'storage/app/public/news/1531732357علي عبدالعال.jpg', 1, '2018-07-16 11:12:37', '2018-07-16 11:12:37', '2018-07-16 11:12:37', 416, 416, 1, 1);
INSERT INTO `news` VALUES (239, 'النائب محمود عطية يقدم سؤالا للحكومة حول شكاوى البطاقات التموينية', '<p><span dir=\"rtl\">&nbsp;تاكد النائب&nbsp;محمود عطية عضو مجلس النواب عن حزب الوفد بحى غرب شبرا الخيمة فى محافظة القليوبية، أنه سوف يقدم سؤالا إلى الدكتور على المصيلحى، وزير التموين، بسبب شكاوى المواطنين من البطاقات التموينية، وذلك عقب حصول حكومة المهندس مصطفى مدبولى على ثقة الأغلبية البرلمانية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف &quot;عطية&quot; أنه تلقى العديد من شكاوى المواطنين خلال الفترة الماضية تفيد بإسقاط أحد أفراد البطاقات التموينية الخاصة بهم، مطالبا بأن تتحقق وزارة التموين من هذا الأمر، ومعرفة سبب شكاوى المواطنين.</span></p>\r\n\r\n<p><span dir=\"rtl\">وطالب النائب بإحالة طلبه إلى اللجنة النوعية المختصة، من أجل مناقشته خلال الاجتماعات الخاصة باللجنة، بحضور ممثلى الحكومة لكشف استفسارات المواطنين عن البطاقات التموينية.</span></p>', 'storage/app/public/news/1531735721محمود عطيه.jpg', 1, '2018-07-16 12:08:41', '2018-07-16 12:08:41', '2018-07-16 12:08:41', 416, 416, 1, 1);
INSERT INTO `news` VALUES (241, 'مجلس النواب يوافق على تعديل أحكام قانون الشرطة', '<p><span dir=\"rtl\">وافق مجلس النواب فى الجلسة العامة المنعقدة الآن، برئاسة الدكتور على عبد العال، على تعديل مشروع قانون المقدم من الحكومة بشأن تعديل أحكام قانون الشرطة109&nbsp;لسنة 1971، فى مجموعه.</span></p>', 'storage/app/public/news/1531747099الج.jpg', 1, '2018-07-16 15:18:19', '2018-07-16 15:18:19', '2018-07-16 15:18:19', 416, 416, 1, 1);
INSERT INTO `news` VALUES (242, 'عبد العال يهدد نوابا بإسقاط عضويتهم.. وتأجيل جلسة منح الثقة للحكومة', '<p>هدد رئيس مجلس النواب الدكتور علي عبد العال نواباً لم يحددهم، بالتصويت على إسقاط عضويتهم بسبب ما قال إنها &quot;مخالفات&quot; ارتكبوها تضمنت محاولات &quot;تعطيل المجلس عن عمله&quot;، وأن لجنة القيم فحصت الشكاوى المقدمة ضدهم وسيجري التصويت على الأمر الأسبوع المقبل.</p>\r\n\r\n<p>واستكمل عبد العال توعده بالقول &quot;لن تكونوا أعضاء بالمجلس بداية من الأسبوع القادم&quot;، وكان ذلك بعد تعليق من عضو تكتل 25-30 المعارض، ضياء داوود، والذي قال أثناء جلسة التصويت بالاسم على تعديلات قوانين ناقشها المجلس أمس إنه &quot;رفضها كمشروعات ويرفض التعديلات عليها&quot;.</p>\r\n\r\n<p>وفور تعليق داوود دعا المتحدث باسم النواب، صلاح حسب الله، إلى تطبيق اللائحة ضد المخالفين لها، مستنداً إلى أن &quot;جلسة التصويت بالاسم يكون الرد فيها بالموافقة أو الرفض دون إعادة للمناقشة&quot;.</p>\r\n\r\n<p>وعاد عبد العال للقول إن &quot;لدي تحقيق في لجنة القيم يتضمن إعاقة نواب لأعمال المجلس والإساءة إلى رموز الدولة في وسائل إعلام معروفة بالاسم. هل هؤلاء يكون لهم شرف الجلوس في هذه القاعة؟. بالتأكيد الإجابة لا&quot;.</p>\r\n\r\n<p>وفي سياق قريب، أعلن وكيل البرلمان، السيد الشريف، أن الجلسة المخصصة لعرض تقرير اللجنة المكلفة بالرد على بيان الحكومة، تم تأجيلها إلى يوم الثلاثاء المقبل.</p>\r\n\r\n<p>وكان من المقرر أن تكون جلسة التصويت، أمس، لكن وكيل المجلس قال إن البرلمان &quot;يرغب في الانتهاء من التشريعات المدرجة على جدول أعماله في دور الانعقاد الحالي، وقبل جلسة منح الثقة للحكومة&quot;، وأشار إلى أن &quot;قرار التأجيل جاء بالتنسيق مع الحكومة&quot;.</p>\r\n\r\n<p>وسبق للبرلمان تأجيل جلسة التصويت على منح الثقة للحكومة، يوم الأحد الماضي، بسبب تغيب الأعضاء عن الحضور.</p>', 'storage/app/public/news/1531914219th.jpg', 1, '2018-07-18 13:43:39', '2018-07-18 13:43:39', '2018-07-18 13:43:39', 1, 1, 1, 1);
INSERT INTO `news` VALUES (243, '\"البرلمان\" يتجه لتخفيض استخدام الورق بسبب التكلفة', '<p>أعلن علي عبد العال رئيس مجلس النواب أمس، أن المجلس سيتوقف عن استخدام الأوراق في التعامل تحت القبة، وفي اللجان النيابية النوعية، بسبب الأعباء المالية الكبيرة التي يرتبها ذلك على ميزانيته.</p>\r\n\r\n<p>وقال &quot;سأتوقف عن استخدام الأوراق فيما يخص طباعة مشروعات القوانين المعروضة على المجلس وكذلك في اللجان&quot;، وأضاف أن أغلبية النواب يحملون مؤهلات عليا، والجميع يستطيع التعامل إلكترونيا مع أجهزة &quot;التاب&quot;.</p>\r\n\r\n<p>وأضاف أن &quot;شركة واحدة تحتكر الأحبار المستخدمة في الطباعة، ولا نستطيع تحمل التكاليف الكبيرة لطباعة الأوراق، كما أن هناك مجالس نيابية كثيرة سبقتنا إلى إلغاء طباعة الأوراق وتتعامل إلكترونيا&quot;.</p>', 'storage/app/public/news/1531914299th.jpg', 1, '2018-07-18 13:44:59', '2018-07-18 13:44:59', '2018-07-18 13:44:59', 1, 1, 1, 1);
INSERT INTO `news` VALUES (244, 'أبرز القرارات الرسمية', '<p>نشرت &quot;الجريدة الرسمية&quot; في عددها رقم 28 مكرر &quot;ب&quot;، الصادر بتاريخ 16 يوليو 2018، قانونا رقم 157 لسنة 2018، الصادر عن رئيس الجمهورية عبد الفتاح السيسي، بإنشاء هيئة تنمية الصعيد.</p>\r\n\r\n<p>ونص القانون على أن تنشأ هيئة عامة خدمية تسمى هيئة تنمية الصعيد، تتمتع بالشخصية الاعتبارية، وتتبع رئيس مجلس الوزراء، ويكون مقرها الرئيسي مدينة القاهرة، ويكون لها فروع أخرى.. ويحدد رئيس الوزراء المناطق الجغرافية ذات الأولوية والمستهدفة من التنمية.</p>\r\n\r\n<p>كما ينص على أن تكون الأولوية في الخطط التي تضعها الهيئة العامة الخدمية للمشروعات التي تحقق عائدًا تنمويًا ونسبًا مرتفعة من التشغيل، مع العمل على جذب الاستثمارات اللازمة لتحقيق التنمية المستدامة، والعوائد الاقتصادية والاجتماعية للمناطق المستهدفة، وذلك بمشاركة أهلها في مشروعات التنمية وأولية الاستفادة منها.</p>\r\n\r\n<p>ويكون للهيئة موازنة خاصة تعد طبقًا للقواعد التي تحددها لائحة الهيئة وباتباع قواعد نظام المحاسبة الحكومية، دون التقيد بالقواعد والنظم الحكومية، وتتكون موارد الهيئة من الاعتمادات التي تخصصها الدولة والهبات والمنح والتبرعات والإعانات والقروض التي يوافق عليها مجلس إدارة الهيئة، ومقابل الخدمات التى تؤديها للغير، وعائد استثمار أموال الهيئة، وأي موارد أخري يوافق مجلس إدارة الهيئة عليها، ويصدر رئيس مجلس الوزراء بعد موافقة مجلس الوزراء اللائحة التنفيذية للقانون خلال ثلاثة أشهر من تاريخ العمل به.</p>', 'storage/app/public/news/1531914454th (1).jpg', 1, '2018-07-18 13:47:34', '2018-07-18 13:47:34', '2018-07-18 13:47:34', 1, 1, 1, 1);
INSERT INTO `news` VALUES (245, 'جلسة طارئة لـ\"اتحاد البرلمان العربى\" السبت القادم حول تداعيات قضية القدس', '<p><span dir=\"rtl\">يشهد مجلس النواب المصرى السبت القادم، انعقاد الدورة الثامنة والعشرين الاستثنائية لاتحاد البرلمان العربى برئاسة الدكتور على عبد العال رئيس الاتحاد بمقر مجلس النواب بالقاهرة السبت القادم، فى جلستها الطارئة لرؤساء المجلس والبرلمانات العربية.</span></p>\r\n\r\n<p><span dir=\"rtl\">يأتى الاجتماع بناء على طلب دولة الكويت وعدد من الدول العربية لبحث تداعيات قضية القدس فى ضوء الممارسات الإسرائيلية والمستجدات الاخيرة فى القضية الفلسطينية.</span></p>\r\n\r\n<p><span dir=\"rtl\">ومن المتوقع أن يقيم الدكتور على عبد العال، مأدبة غذاء على شرف رؤساء البرلمانات والوفود العربية المشاركة في الاجتماعات في (البهو الفرعوني) بمقر المجلس.</span></p>\r\n\r\n<p><span dir=\"rtl\">وكان الدكتور على عبد العال رئيس مجلس النواب المصرى قد تسلم فى الخامس من أبريل الماضى، رئاسة الاتحاد البرلماني العربي، رسميا، لمدة عام، وذلك خلفاً لنظيرة المغربي حبيب المالكى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفي سياق متصل، غادر الدكتور عبد العال القاهرة صباح اليوم الأربعاء متوجها إلى صربيا ومن المقرر عودته قبل يوم انعقاد الجمعية للمشاركة في الجلسة الطارئة.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531920804مجلس النواااااااااااااااااااااااااااااب.jpg', 1, '2018-07-18 15:33:24', '2018-07-18 15:33:24', '2018-07-18 15:33:24', 416, 416, 1, 1);
INSERT INTO `news` VALUES (246, 'رئيس أكاديمية الشرطة فى حواره  مافيش واسطة ولا محسوبية للالتحاق بالكلية..', '<h1><small><span dir=\"rtl\">رئيس أكاديمية الشرطة فى حواره : مافيش واسطة ولا محسوبية للالتحاق بالكلية.. اللواء دكتور أحمد إبراهيم يؤكد: طورنا المناهج واستحدثنا مادة علمية لإتقان التعامل بالحاسب الآلى وكشف الجرائم المستحدثة.</span></small></h1>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">قال اللواء الدكتور أحمد إبراهيم، مساعد وزير الداخلية، رئيس أكاديمية الشرطة، إنه لا يوجد واسطة ولا محسوبية للالتحاق بكلية الشرطة، ولا نبحث عن المستوى المعيشى لأسرة الطالب، ويتم تطوير المناهج واستحداث مادة علمية عن كيفية التعامل مع الحاسب الآلى والجرائم المستحدثة، محذرًا أولياء الأمور من التعامل مع النصابين الذين يوهمونهم بقدرتهم على إلحاق ذويهم بالكلية من أجل المال، مضيفًا، على هامش المؤتمر الصحفى للإعلان عن قبول دفعة جديدة من الراغبين فى الالتحاق بكلية الشرطة، أن التقديم للطلاب يكون عبر الإنترنت، ويتم منح الحاصلين على البكالوريوس رتبة ملازم أول والحاصلين على الماجستير رتبة &laquo;نقيب&raquo; وحاملى الدكتوراه رتبة &laquo;رائد&raquo;</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1531997058اللواءءءءءءء.jpg', 1, '2018-07-19 12:44:18', '2018-07-19 12:44:18', '2018-07-19 12:44:18', 416, 416, 1, 1);
INSERT INTO `news` VALUES (247, 'تعرف على ضوابط وزارة السياحة لموسم حج بدون أزمات', '<p>التزاما من وزارة السياحة بموسم حج ناجح وضعت مجموعة من الضوابط ألزمت بها الشركات السياحية، حفاظا على حقوق 36 ألف حاج سياحى فى سكن لائق ومواصلات مريحة، حيث شددت الوزارة &nbsp;على أنها لن تتهاون فى تطبيق الضوابط والقواعد المنظمة لرحلات الحج للموسم الحالى 1439هـ الصادرة من الوزارة.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;وقالت وزارة السياحة إن الضوابط سيتم الالتزام بها هذا العام ولن يسمح بأى استثناءات خاصة فى السكن، لافتا إلى أن الوزارة أرسلت بعثتها مبكرا حتى تتيح للشركات اختيار سكن حجاجها بما يتناسب والضوابط.</p>\r\n\r\n<p>تتلخص الضوابط فى :-</p>\r\n\r\n<p>- ألا يقل موديل الباصات المستخدمة فى الحج البرى عن 2009، وأن تكون السيارة مرخصة سياحياً وأن تكون رخصة السائق و السيارة سارية حتى تاريخ عودة الرحلة&nbsp;.</p>\r\n\r\n<p>- يمنع استخدام السيارات ذو الطابقين حمولة 72 راكبا، ويلزم ترك مقاعد خالية فى حدود نسبة 10% من طاقة الأتوبيس لاستخدامها فى الحالات الطارئة&nbsp;.</p>\r\n\r\n<p>- أن يكون السائق مسجلاً بالوزارة ، ويلتزم السائق بعدم ارتكاب أى أعمال من شأنها تأخير وصول الرحلة فى الميعاد المحدد لها دون سبب&nbsp;.</p>\r\n\r\n<p><span dir=\"rtl\">- يلتزم السائق بعدم تحصيل أى إتاوات أو إكراميات تحت أى مسمى من الحجاج، كما يلتزم بعدم ارتكاب&nbsp;أى أعمال من شأنها الإهمال أثناء الرحلة وفى حالة مخالفة تلك الواجبات والمهام سوف يطبق علية الجزاء القانونى فى هذا الشأن.</span></p>\r\n\r\n<p><span dir=\"rtl\">- فيما يتعلق بضوابط سكن الحجاج لجان الوزارة ستقوم بإجراء المعاينات اللازمة للسكن التى تتقدم به الشركة وذلك للتأكد من مطابقته.</span></p>\r\n\r\n<p><span dir=\"rtl\">- عدم تسكين الحجاج بالميزانين أو دور الخدمات أو الأسطح، و لن يعتد بغير التصنيف السعودى الصادر من الهيئة العامة للسياحة والآثار السعودية فى تحديد مستوى&nbsp;الفنادق.</span></p>\r\n\r\n<p><span dir=\"rtl\">- ضرورة توفير شاتل باص من وإلى الحرم للفنادق الواقعة بعد 1250 متر وأن يكون السكن مرخص.</span></p>\r\n\r\n<p><span dir=\"rtl\">- إيواء سياحى على الأقل للمستويين البرى والاقتصادى، ويجوز التحسين على مسافة أكثر من 2500 متر داخل مكة شريطة معاينة اللجنة، واعتمادها للسكن والتى تتماثل مع منطقة العزيزية&nbsp;.</span></p>\r\n\r\n<p><span dir=\"rtl\">- أقصى مسافة مسموح التسكين بها بالمدينة المنورة 800 متر من الحرم النبوى الشريف.</span></p>', 'storage/app/public/news/1532009178الحجاج.jpg', 1, '2018-07-19 16:06:18', '2018-07-19 16:06:18', '2018-07-19 16:06:18', 416, 416, 1, 1);
INSERT INTO `news` VALUES (248, 'إحالة موظف للمحاكمة التأديبية لتزويره شهادات نجاح 4 طلاب بالثانوية العامة', '<p><span dir=\"rtl\">أمرت المستشار أمانى الرافعى رئيس هيئة النيابة الإدارية، بإحالة رئيس قسم شئون الطلبة بمديرية التربية والتعليم بمحافظة القليوبية للمحاكمة التأديبية العاجلة، وذلك لما نسب إليه من التلاعب والتزوير فى بيانات النجاح لـ4 طلاب فى امتحانات شهادة إتمام دراسة الثانوية العامة واستخراج تلك البيانات المزورة عن دور أول لعام 2015 والمنسوب صدورها لمديرية التربية والتعليم بمحافظة القليوبية .</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وقال المستشار محمد سمير المتحدث باسم هيئة النيابة الإدارية، فى بيان اليوم الأحد، أن النيابة تلقت بلاغًا من هيئة الرقابة الإدارية بشأن الواقعة والتى أثبتت تحرياتها عن رسوب الطلاب وعدم الاستدلال على بيانات طالبة منهم بسجلات وزارة التربية والتعليم المدرج بها أسماء الناجحين فى امتحان الثانوية العامة عن ذات العام، وكذا عدم الاستدلال على نجاح طالب آخر، وأنه تبين نجاحه فى امتحان الثانوية العامة لعام 2016 الدور الأول بمجموع 308 درجات ووجود اثنين من الطلبة مقيدين ضمن الراسبين عن عام 2015، كما تبين عدم تدوين أرقام قسائم سداد استخراج البيانات الخاصة بعدد اثنين من الطلبة، وكذا عدم صحة أرقام قسائم سداد الرسوم الثابتة على بيانى النجاح المزورين للطالبين الآخرين .</span></p>', 'storage/app/public/news/1532252848ال2يااااااااااااااااب5.jpg', 1, '2018-07-22 11:47:28', '2018-07-22 11:47:28', '2018-07-22 11:47:28', 416, 416, 1, 1);
INSERT INTO `news` VALUES (249, 'حالات يجوز فيها التعويض عن فترة الحبس الاحتياطى.. تعرف عليها', '<p><span dir=\"rtl\">نصت المادة 9 من الإعلان العالمى لحقوق الإنسان، على أنه لا يجوز القبض على أى إنسان أو حجزه أو نفيه تعسفًا، كما أن مشروع قانون الإجراءات الجنائية الذى تمت الموافقة عليه من قبل مجلس النواب، حدد الحالات التى يتم فيها التعويض عن مدة الحبس الاحتياطى، حيث أوضحت كل من المادتين &quot;562 و563&quot; من قانون الإجراءات الجنائية الذى وافقت عليه اللجنة التشريعية بالبرلمان الحالات والمواقف التى يتم التعويض فيها عن مدة الحبس الاحتياطى.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">المادة &quot;562&quot; نصت بشكل مباشر، وأوضحت فى نصها أنه &quot;التعويض لكل شخص تم حبسه احتياطيا أو حكم عليه بعقوبة ثم صدر من النيابة العامة بأنه لا وجه لإقامة الدعوى الجنائية، أو صدر حكم ببراءته من جميع التهم المنسوبة إليه&quot;.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span dir=\"rtl\">فيما نصت المادة 9 من العهد الدولى الخاص بالحقوق المدنية والسياسية على الآتى:</span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">1- لكل فرد حق فى الحرية وفى الأمان على شخصه. ولا يجوز توقيف أحد أو اعتقاله تعسفًا ولا يجوز حرمان أحد من حريته إلا لأسباب ينص عليها القانون وطبقًا للإجراء المقرر فيه.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">2- يتوجب إبلاغ أى شخص يتم توقيفه بأسباب هذا التوقيف لدى وقوعه كما يتوجب إبلاغه سريعًا بأية تهمة توجه إليه.</span></p>\r\n\r\n<p><span dir=\"rtl\">3- يقدم الموقوف أو المعتقل بتهمة جزائية، سريعًا، إلى أحد القضاة أو أحد الموظفين المخولين قانونًا مباشرة وظائف قضائية، ويكون من حقه أن يُحاكم خلال مهلة معقولة أو أن يفرج عنه. ولا يجوز أن يكون احتجاز الأشخاص الذين ينتظرون المحاكمة هو القاعدة العامة، ولكن من الجائز تعليق الإفراج عنهم على ضمانات لكفالة حضورهم المحاكمة، فى أية مرحلة أخرى من مراحل الإجراءات القضائية، ولكفالة تنفيذ الحكم عند الاقتضاء.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">4- لكل شخص حُرِمَ من حريته بالتوقيف أو الاعتقال حق الرجوع إلى محكمة لكى تُفصل هذه المحكمة دون إبطاء فى قانونية اعتقاله، وتأمر بالإفراج عنه إذا كان الاعتقال غير قانونى.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">5- لكل شخص كان ضحية توقيف أو اعتقال غير قانونى حق فى الحصول على تعويض.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">قانون الإجراءات الجنائية عند إصداره 1950، لم يذكر أية نصوص تحكم عملية التعويض عن الحبس الاحتياطى، وعلى جانب آخر لم تكن هناك أية أحكام قضائية لمناقشة تلك المسألة، وهو ما جعل العديد من الفقهاء القانونين ينتقدون القانون.</span></p>\r\n\r\n<p><span dir=\"rtl\">وعلى خلفية التعديلات التى أجريت على قانون الإجراءات الجنائية، بالقانون رقم 145 لسنة 2006، تم إضافت المادة 312 مكرر كمادة جديدة على القانون، حيث نصت هذه المادة على مبدأ التعويض عن الحبس الاحتياطى بشكل مباشر وصريح.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وعلى الجانب الآخر، هناك 4 حالات حددها نفس القانون لا يجوز التعويض فيها عن مدة الحبس الاحتياطى، وهى:</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">1- من صدر ضده قرار من النيابة بأنه لا وجه لإقامة الدعوى، أو صدر حكم بالبراءة مبنى على عدم كفاية الأدلة، أو الاتهام أو لأى سبب الإباحة، وهى الأسباب التى تبيح الفعل المجرم أو امتناع المسئولية، أو الإعفاء من العقاب أو انقضاء الدعوى الجنائية، أو لصدور حكم أو قانون يرفع وصف التجريم أو لعدم الأهمية.</span></p>\r\n\r\n<p><span dir=\"rtl\">2- إذا صدر عفو شامل.</span></p>\r\n\r\n<p><span dir=\"rtl\">3- إذا كان المتهم محبوسا على ذمة قضية أخرى.</span></p>\r\n\r\n<p><span dir=\"rtl\">4- لو ثبت أن المتهم تم حبسه رغبة منه لتمكين المجرم الحقيقى للإفلات من العقاب.</span></p>', 'storage/app/public/news/153225791755555555555555555555555.jpg', 1, '2018-07-22 13:11:57', '2018-07-22 13:11:57', '2018-07-22 13:11:57', 416, 416, 1, 1);
INSERT INTO `news` VALUES (250, 'موجز اخبار  الثلاثاء .. الرئيس السيسى يفتتح عددا من المشروعات القومية بمجال الكهرباء', '<p><span dir=\"rtl\">أشاد الرئيس عبد الفتاح السيسى، بدور وزارة البترول فى حل أزمات قطاع الكهرباء، مشيرا بدورها فى توفير الغاز ومشتقات المازوت فى تشغيل محطات توليد الكهرباء.</span></p>', 'storage/app/public/news/1532597143سوسو.jpg', 1, '2018-07-26 11:25:43', '2018-07-26 11:25:43', '2018-07-26 11:25:43', 416, 416, 1, 1);
INSERT INTO `news` VALUES (251, 'موجز اخبار الاربعاء \"النقض\" تصدر حكمها على 16 متهمًا فى أحداث \"السفارة الأمريكية الثانية', '<p><span dir=\"rtl\">اصدرت محكمة النقض، يوم الأربعاء، الدائرة (د) برئاسة المستشار أحمد عبد القوى الطعن المقدم من 16 متهمًا فى القضية المعروفة إعلاميًا بأحداث السفارة الأمريكية الثانية، والتى وقعت أحداثها فى 22 يوليو 2013.</span></p>\r\n\r\n<p><span dir=\"rtl\">كانت محكمة جنايات القاهرة برئاسة المستشار محمد ناجى شحاتة، قد قضت بمعاقبة متهمين اثنين بالإعدام شنقًا، كما عاقبت 14 متهمًا حضوريًا بالسجن المؤبد، و6 متهمين آخرين غيابيًا بالسجن المؤبد أيضًا فى القضية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأسندت النيابة إلى المتهمين عدة تهم، من بينها، تعريض السلم العام للخطر، وارتكاب جرائم القتل، إصابة آخرين والإتلاف العمدى وتخريب مبانى ومؤسسات الدولة، بالإضافة إلى حيازتهم الأسلحة.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1532597925محكمه النقض.jpg', 1, '2018-07-26 11:39:16', '2018-07-26 11:38:45', '2018-07-26 11:39:16', 416, 416, 1, 1);
INSERT INTO `news` VALUES (252, 'موجز الاربعاء _متحدث الرئاسة: محطات الكهرباء الجديدة تنتج 50% من إنتاج مصر سابقًا', '<p><span dir=\"rtl\"><q>قال السفير بسام راضى المتحدث الرسمى باسم رئاسة الجمهورية، أن افتتاح الـ 3 محطات الكهربائية الجديدة وإنجازهم فى زمن قياسى، وبدء دخولهم الخدمة لإضافة 14.4 ميجا وات، هو إعجاز بكل المقاييس.</q></span></p>\r\n\r\n<p><span dir=\"rtl\"><q>وأكد راضى، فى مداخلته الهاتفية مع الإعلامى أسامة كمال ببرنامج &quot; مساء dmc&quot;، &quot;أن الـ3 محطات الكهربائية الجديدة، هم الأحدث من نوعهم بالعالم، ويضيفون 50 % تقريبًا، من إنتاج مصر من الكهرباء سابقا.</q></span></p>\r\n\r\n<p><span dir=\"rtl\"><q>وأوضح راضى، أنه يجب على رئيس شركة سيمينز، تسجيله فى موسوعة الأرقام القياسية &quot;جينيس ريكورد&quot;، كما ذكر الدكتور محمد شاكر، وزير الكهرباء والطاقة المتجددة، خلال كلمته صباح اليوم أثناء الافتتاح.</q></span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1532598352المتحدث الرسمي.jpg', 1, '2018-07-26 11:45:52', '2018-07-26 11:45:52', '2018-07-26 11:45:52', 416, 416, 1, 1);
INSERT INTO `news` VALUES (253, 'الجريدة الرسمية تنشر موافقة السيسى على 300 مليون يوان منحة لمشروع مصر سات', '<p><span dir=\"rtl\">أصدر الرئيس عبد الفتاح السيسى، القرار رقم 40 لسنة 2018، بالموافقة على اتفاقية التعاون الاقتصادى والفنى بين حكومتى مصر والصين، والموقعة فى الصين بتاريخ 5/9/2017 وذلك مع التحفظ بشرط التصديق.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">ووفقاً للاتفاقية وافقت الحكومة الصينية على تقديم منحة لا ترد بمبلغ إجمالى قدره 300 مليون يوان صينى إلى الحكومة المصرية، لتنفيذ مشروع القمر الصناعى مصر سات، وأمية مشروعات أخرى يتفق عليها الجانبان لاحقاً، ونشر القرار بالجريدة الرسمية.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1532601556الجريده.jpg', 1, '2018-07-26 12:39:16', '2018-07-26 12:39:16', '2018-07-26 12:39:16', 416, 416, 1, 1);
INSERT INTO `news` VALUES (254, 'الحكومة: قدمنا 544 مشروعا بقانون للبرلمان منذ انعقاده ووافق على 88% منها', '<p>كشف المستشار عمر مروان وزير شئون مجلس النواب، عن إجمالى عدد المشروعات بقوانين التى تقدمت بها الحكومة للبرلمان خلال أدوار الانعقاد الثلاثة الماضية، والتى بلغ عددها 544 مشروعا بقانون.</p>\r\n\r\n<p>وأوضح مروان فى لقاء له بالمحررين البرلمانيين صباح اليوم، أن مجلس النواب وافق على 480 مشروع بقانون منها بنسبة 88% ويتبقى 64 قانون منها على سبيل المثال الإجراءات الجنائية والإدارة المحلية.</p>\r\n\r\n<p>كما أشار إلى أن الحكومة تقدمت بـ161 اتفاقية دولية للبرلمان خلال أدوار النعقاد الماضة تمت الموافقة على 158 اتفاقية بنسبة 98% ليتبقى 3 اتفاقيات فقط، بالإضافة إلى 14 قرار جمهورى تمت الموافقة عليهم جميعا، نصفهم كان فى دور الانعقاد الأخير.</p>', 'storage/app/public/news/1532604428عمر مروان.jpg', 1, '2018-07-26 13:27:08', '2018-07-26 13:27:08', '2018-07-26 13:27:08', 416, 416, 1, 1);
INSERT INTO `news` VALUES (257, 'جسر الأمان تشارك فى الحفل الختامى لنموذج \"محاكاة البورصة المصرية \"', '<p>تشارك شركة جسر الأمان يوم السبت &nbsp;28 من الشهر الجارى فى الحفل الختامى لنموذج &nbsp;محاكاة البورصة المصرية MESE، وذلك فى فاعليته الأخيرة لهذه السنة، بحضور &nbsp;كلاً من &nbsp;عبد الرحيم أسامة المدير التنفيذى للشركة ونائب رئيس مجلس الإدارة آحمد عمر صادق و مدير العلاقات العامة أحمد ماهر.</p>\r\n\r\n<p>تتضمن الفاعلية كلمات لعدداً من المدعوين وعلى رأسهم د.فخرى الفقى مساعد رئيس البنك الدولى ودكتور الأقتصاد بجامعة القاهرة، وفاء مرزوق عميد كلية إدارة أعمال فى جامعة الأهرام الكندية.</p>\r\n\r\n<p>تأتى مشاركة جسر الأمان فى هذا النشاط الطلابى تباعاً لرؤيتها فى نشر الوعى الحقوقى وخلق مجتمع قانونى بمفهوم مختلف، وإيماناً منها بدور الشباب الصاعد فى المجتمع.</p>\r\n\r\n<p>جدير بالذكر أن نموذج محاكاة البورصة المصرية، أنشئ منذ 21 عاماً على يد وزير الاستثمار الأسبق الدكتور محمود محى الدين بكلية الاقتصاد والعلوم السياسية، وتجرى فاعلياته سنوياً فى خمس جامعات هى عين شمس، القاهرة، الأهرام الكندية، الكندية، المستقبل.</p>', 'storage/app/public/news/1532619128WhatsApp Image 2018-07-26 at 5.31.02 PM.jpeg', 1, '2018-07-26 17:32:08', '2018-07-26 17:32:08', '2018-07-26 17:32:08', 412, 412, 1, 1);
INSERT INTO `news` VALUES (258, 'إغلاق طريق \"القاهرة - بلبيس\" الصحراوى جزئياً بسبب أعمال تطوير لمدة 10شهور', '<p><span dir=\"rtl\">تبدأ اليوم اﻷحد، الادارة العامة للمرور، برئاسة اللواء عصمت الأشقر مدير الإدارة، إجراءات الإغلاق الجزئى لطريق &quot; القاهرة - بلبيس&quot; الصحراوى لمدة 10 أشهر بسبب أعمال توسعة وتطوير للطريق، وسط انتشار الخدمات المرورية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأكد مصدر أمنى بأنه فى إطار قيام الهيئة العامة للطرق والكبارى، أعمال توسعة ورصف طريق القاهرة &ndash; بلبيس الصحراوى فى الاتجاهين سيتم إجراء إغلاق جزئى للطريق، لافتا إلى&nbsp;أن الاعمال تكون عبارة عن توسعة الطريق فى المسافة من تقاطعة مع الطريق الدائرى الأوسطى حتى بوابات تحصيل الرسوم ببلبيس بطوال 12 كيلو، مع استحداث حارة مرورية بكل اتجاه من الجزيرة الوسطى.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأشار المصدر، إلى أن الأعمال ستكون لمدة عشرة أشهر تبدأ اليوم اﻷحد 29 يوليو، وسوف تكون اﻷعمال ليلا ونهارا على مدار الـ24 ساعة، وسيتم وضع العلامات والإرشادات اللازمة.</span></p>', 'storage/app/public/news/1532862786طريق بلبيس.jpg', 1, '2018-07-29 13:13:06', '2018-07-29 13:13:06', '2018-07-29 13:13:06', 629, 629, 1, 1);
INSERT INTO `news` VALUES (259, '\"الرقابة الإدارية\": 900 مليار جنيه قيمة نزاعات بين الممولين ومصلحة الضرائب', '<p>أكد الوزير محمد عرفان، رئيس هيئة الرقابة الإدارية، أن المنظومة الجديدة للبنية المعلوماتية تسهم فى انسيابية تسديد الضرائب المستحقة ورصدها، مشيرًا إلى أنه يوجد 900 مليار جنيه قيمة نزاعات ضريبية بين الممولين ومصلحة الضرائب منذ 10 أو 15 عامًا ماضية.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>وقال &quot;عرفان&quot;، فى كلمته خلال جلسة &quot;المشروع القومى للبنية المعلوماتية للدولة المصرية&quot;، ضمن فعاليات المؤتمر الوطنى السادس للشباب بجامعة القاهرة، فى حضور الرئيس عبد الفتاح السيسي، إن الحكومة تهتم برضا الجمهور على الخدمات المقدمة لهم، وسيتم تفعيل المنظومة ليكون هناك انسيابية لسداد الضرائب المستحقة للدولة مع تبسيط إجراءات تحصيل هذه الضرائب.</p>\r\n\r\n<p>وأوضح أن المنظومة الجديدة للبنية المعلوماتية متغيرة بين لحظة وأخرى، لافتًا إلى أن موازنة الدولة 2018 &ndash; 2019، بها 998 مليار جنيه موارد.</p>\r\n\r\n<p>وذكر أنه يوجد 94 ألف ملف ضريبى، وحتى الأمس تم ربط ضرائب مستحقة على الممولين بقيمة 5 مليارات جنيه، مضيفًا أنه تم تفريغ 60 ألف ملف يدويًا وميكنة 33 ألف ملف، وخلال 15 يومًا سيتم تقيم الموقف للـ 900 مليار جنيه ورفع الأمر إلى رئيس الجمهورية.</p>', 'storage/app/public/1532866086عرفان .jpg', 1, '2018-07-29 14:08:06', '2018-07-29 14:05:46', '2018-07-29 14:08:06', 634, 634, 1, 1);
INSERT INTO `news` VALUES (260, 'من فعاليات اليوم الثانى لمؤتمر الشباب بحضور الرئيس السيسى', '<p>كان الرئيس عبد الفتاح السيسى، طالب أمس، الشعب المصرى بضرورة تحمل إجراءات الإصلاح الاقتصادى، قائلًا: &quot;لو انتم رفضتوه مش هيكمل وهتخرب.. هنعمل إيه.. أنتم من سيتحمل النجاح والفشل أيضًا بجانب الدولة&quot;.</p>\r\n\r\n<p>وشدد الرئيس خلال كلمته أمس بجلسة &quot;استراتيجية تطوير التعليم&quot; ضمن فعاليات المؤتمر الوطنى السادس للشباب، بجامعة القاهرة، على ضرورة استيعاب المواطنين لمنظومة التعليم الجديدة، كما طالب الإعلام بضرورة تكثيف الشرح وإلقاء الضوء على منظومات التعليم والصحة والإصلاح الإدارى، خاصة أنها الملفات الأبرز خلال الـ4 سنوات المقبلة.</p>', 'storage/app/public/news/1532871685السيسي.jpg', 1, '2018-07-29 15:41:25', '2018-07-29 15:41:25', '2018-07-29 15:41:25', 634, 634, 1, 1);
INSERT INTO `news` VALUES (261, 'الوزير محمد عرفان: هناك مشكلة حقيقية فى تحصيل الضرائب العقارية والميكنة الحل', '<p>قال اللواء محمد عرفان، رئيس هيئة الرقابة الإدارية، إن قانون الضرائب العقارية الذى يتم تطبيقه منذ 5 سنوات، كان يفترض له أن يجرى حصر لكل العقارات التى تخضع للضريبة فى مصر، ورغم مرور كل هذه السنوات لم يتم الانتهاء من الحصر حتى الآن.</p>\r\n\r\n<p>وأضاف عرفان، خلال جلسة &quot;المشروع القومى للبنية المعلوماتية للدولة المصرية&quot; ضمن فعاليات المؤتمر الوطنى السادس للشباب بجامعة القاهرة، فى حضور الرئيس عبد الفتاح السيسى، أن هناك الكثير من العقارات لم تحصر بعد، فبالتالى هناك الكثيرين لم يخطروا ليسددوا ما عليهم من ضرائب، وتابع &quot;ما حدث هو تحصل 50% من الضريبة التى يفترض تحصيلها، وهو ما يعنى وجود مشكلة فى عملية التحصيل&quot;.</p>\r\n\r\n<p>وأوضح رئيس هيئة الرقابة الإدارية، أنه يجب استخدام الميكنة فى عملية تحصيل الضرائب العقارية، أما عن ضريبة القيمة المضافة فلها أهمية كبرى بالنسبة للموازنة المصرية، إذ تمثل 256 مليار جنيه يجب أن تكون بموازنة 2018 &ndash; 2019، وهو رقم كبير جدا يمثل أقل من ثلث موارد الدولة بالكامل.</p>\r\n\r\n<p>وأشار رئيس هيئة الرقابة الإدارية، إلى أن الميكنة ستسهل حصول الدولة على حقها بانسيابية فى تحصل الضرائب الخاصة بها، لذلك هى أفضل طريقة فى عملية التحصيل من الطرق اليدوية التى ما زلنا نتبعها حتى الآن.</p>', 'storage/app/public/news/1532877693ضريبة عقارية.jpg', 1, '2018-07-29 17:21:33', '2018-07-29 17:21:33', '2018-07-29 17:21:33', 629, 629, 1, 1);
INSERT INTO `news` VALUES (262, 'التشريعات التي  أقرها البرلمان لمواجهة الإرهاب والحفاظ على الأمن', '<p><span dir=\"rtl\">شهد دور الأنعقاد الثالث لمجلس النواب، برئاسة الدكتور علي عبد العال، إقرار عدد من التشريعات الهامة في مجال الحفاظ علي الأمن وحماية مقدرات الوطن لاسيما فيما يتعلق بمكافحة الإرهاب، سواء مقدمة من الحكومة أو عُشر أعضاء مجلس النواب.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">ويأتي في مقدمة القوانين التى أقرها المجلس في إطار دوره التشريعي لمواجهة الإرهاب، مشروع قانون بإنشاء المجلس الأعلي لمواجهة الإرهاب والتطرف، &nbsp;ويتضمن 20 مادة، ويهدف إلى حشد الطاقات المؤسسية والمجتمعية للحد من مسببات الإرهاب ومعالجته، ومعالجة آثاره، ويتمتع المجلس بالشخصية الاعتبارية والاستقلال المالى والفنى والإدارى فى ممارسة مهامه وأنشطته واختصاصاته. ويكون مقر المجلس فى محافظة القاهرة، ويجوز انعقاده فى أى مكان آخر يحدده رئيس الجمهورية.</span></p>\r\n\r\n<p><span dir=\"rtl\">كما أقر المجلس مشروع قانون بتنظيم إجراءات الحصر والإدارة والتصرف في أموال الجماعات الإرهابية والإرهابيين، وينص على إنشاء لجنة مستقلة فى أدائها لعملها، ذات طبيعة قضائية، تختص دون غيرها باتخاذ كل الإجراءات المتعلقة بتنفيذ الأحكام القضائية الصادرة باعتبار جماعة أو كيان أو شخص منتميا لجماعة أو جماعات إرهابية، وذلك استثناءً من أحكام قانون المرافعات المدنية والتجارية، فيما يتعلق بتنفيذ الأحكام وتحديد القائمين على التنفيذ الجبرى للأحكام القضائية، ويكون مقر اللجنة مدينة القاهرة، وتعقد اجتماعاتها فى مقر محكمة الاستئناف لحين توفير مقر مستقل لها.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفيما يتعلق بمجال تكريم الشهداء ورعاية أسرهم، أقر مجلس النواب خلال دور الانعقاد الثالث مشروع قانون المقدم من عشر أعضاء المجلس بإنشاء صندوق تكريم شهداء وضحايا ومفقودي ومصابي العمليات الحربية والإرهابية والأمنية وأسرهم.</span></p>', 'storage/app/public/news/1532938946البرلمان.jpg', 1, '2018-07-30 10:22:26', '2018-07-30 10:22:26', '2018-07-30 10:22:26', 629, 629, 1, 1);
INSERT INTO `news` VALUES (263, 'رفض استئناف \"أصحاب المعاشات\" على حكم وقف تنفيذ إضافة العلاوات', '<p>قضت محكمة مستأنف القاهرة الأمور المستعجلة، برفض استئناف أصحاب المعاشات، وتأييد حكم وقف تنفيذ قرار محكمة القضاء الإداري إلزام مجلس الوزراء&nbsp; بصرف مستحقات أجورهم المتغيرة نسبة 80% من قيمة العلاوات الخمس&nbsp;التى كانوا يتقاضونها خلال خدمتهم .</p>\r\n\r\n<p>كان الاتحاد العام لأصحاب المعاشات، أقام استئنافا على الحكم الذي أصدرته محكمة القاهرة للأمور المستعجلة، بوقف تنفيذ حكم القضاء الإداري مؤقتا، وطالب بإلغاء الحكم لعدم اختصاص &laquo;الأمور المستعجلة&raquo; نظر الاستشكالات الصادرة على أحكام القضاء الإدارى، فقضت المحكمة برفض الاستئناف وتأييد الحكم الصادر.</p>', 'storage/app/public/news/1532947888وزارة التضامن الاجتماعي.jpg', 1, '2018-07-30 12:51:28', '2018-07-30 12:51:28', '2018-07-30 12:51:28', 629, 629, 1, 1);
INSERT INTO `news` VALUES (264, 'غادة والى: اليوم مغادرة مقدمة بعثة حج وزارة التضامن', '<p><span dir=\"rtl\">أعلنت غادة والى وزيرة التضامن الاجتماعى، أنه اليوم الاثنين، تغادر مقدمة البعثة الإشرافية لوزارة التضامن الاجتماعى إلى الأراضى السعودية استعدادا لاستقبال حجاج بيت الله الحرام، ومن المقرر أن تتوجه مقدمة البعثة إلى المدينة المنورة استعدادا لاستقبال أول أفواج حجاج الجمعيات والذى ستبدأ رحلاتهم يوم الخميس 2 أغسطس إلى المدينة المنورة.&nbsp;</span></p>\r\n\r\n<p><span dir=\"rtl\">وتضم مقدمة البعثة عدد من كبار مسئولى بالوزارة، وتقوم على إعداد المقر الإدارى للبعثة والانتهاء من الإجراءات الخاصة بتصاريح أمن المطار بالإضافة إلى تجهيز الخدمات والفنادق التى تم التعاقد عليها مع الجانب السعودى، كما تقوم البعثة بمراجعة المخيمات بالمشاعر المقدسة بمنى وعرفات، واستلامها عقب الانتهاء من تجهيزها من طرف السلطات السعودية استعدادا لخدمة 12 ألف حاج من حجاج الجمعيات الأهلية بخلاف 445 حاج منضمين من حصة الهيئات والوزارات.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتبدأ البعثة عملها فور وصولها للمدينة باستقبال أول أفواج الحجاج التابعين للجمعيات وتسكينهم على أن تتوالى رحلات الحجيج للمدينة المنورة حتى 9 أغسطس كما تغادر أولى رحلات أفواج الحجاج إلى مطار جده اعتبارا من 8 أغسطس.</span></p>', 'storage/app/public/news/1532953314غادة والي.jpg', 1, '2018-07-30 14:21:54', '2018-07-30 14:21:54', '2018-07-30 14:21:54', 629, 629, 1, 1);
INSERT INTO `news` VALUES (285, 'بيان من المالية حول ادعاءات عدم دستورية الضريبة العقارية', '<p><span dir=\"rtl\">قال محمد معيط وزير المالية، في بيان رسمي، اليوم الأحد، إن ما يتردد عن عدم دستورية قانون الضريبة العقارية &quot;غير صحيح، كما يروج له البعض&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف أنه لا توجد على الإطلاق دعوى أمام المحكمة الدستورية العليا تتعلق بهذا الشأن.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال الوزير إن قانون الضرائب العقارية صدر عام 2008 وتم تعديله عام 2013 وهو مطبق وسارٍ منذ ذلك التاريخ، &quot;فلماذا لم تظهر مسألة عدم الدستورية خلال العشر سنوات الماضية؟&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">جاء ذلك خلال تصريحات الوزير مساء أمس السبت على هامش الاجتماعات السنوية للتجمع الأفريقي بمدينة شرم الشيخ، والتي تنظمها وزارتا المالية والاستثمار والتعاون الدولي بالتعاون مع البنك المركزي.</span></p>\r\n\r\n<p><span dir=\"rtl\">وذكر الوزير، في البيان، أنه &quot;إذا كان هناك أي قرار بهذا الموضوع من أي جهة قضائية فإن الحكومة تحترم ذلك وتسارع بتطبيقه مثلما حدث من قبل وتم إلغاء سريان العديد من المواد بقوانين المعاشات والضرائب طبقا لأحكام صدرت عن المحكمة الدستورية العليا&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتابع: &quot;نحن دولة مؤسسات نحترم الدستور والقانون.. والضريبة على&nbsp;العقارات&nbsp;المبنية مطابقة للدستور طبقًا لحكم المحكمة الدستورية العليا والتي قضت في عام 2002 بدستورية فرض ضريبة عن الوحدات العقارية التي لا تدر دخلا&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;وكذلك سبق أن أصدرت المحكمة الدستورية حكما قضائيًا مهما برقم 96 لسنة 22 قضائية بتاريخ 10 مارس عام 2002 يتعلق بالقانون القديم للضريبة العقارية رقم 56 لسنة 1954، حيث قضت المحكمة بدستورية المادة الأولى من القانون والتي تفرض ضريبة عقارية على&nbsp;العقارات&nbsp;المشغولة بغير عوض أي تلك التي لا تدر دخلا&quot;، وفقا لمعيط.</span></p>\r\n\r\n<p><span dir=\"rtl\">وطالب معيط من يرى بغير دستورية القانون باللجوء للقضاء، &quot;القانون يحكمنا جميعًا ومن يرى في هذا القانون أو غيره عدم دستورية عليه اتخاذ الإجراءات القانونية المتبعة سواء باللجوء إلى القضاء الإداري أو الدستورية العليا&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وشدد الوزير على أنه سيتم تطبيق القانون على كل من يتقاعس عن سداد حقوق الخزانة العامة للدولة، مؤكدا أن الضريبة العقارية مطبقة في مصر منذ ما يزيد عن 200 سنة تحت اسم &quot;العوايد&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشار الوزير إلى أن &quot;ما يردده أصحاب الشائعات المغرضة على مواقع التواصل الاجتماعي بعدم دستورية الضريبة العقارية يهدف لإحداث بلبلة لدى الرأي العام حول الضريبة وإثارة الفوضى وحث المواطنين على الاستهانة بالحقوق المالية للدولة والتحريض على عدم سدادها بأسانيد باطلة&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتستمر مصلحة الضرائب العقارية في تنفيذ القانون وتحصيل حق الدولة المتمثل في الضريبة المستحقة على ملاك الوحدات العقارية، وفقا للوزير الذي حذر من اقتراب انتهاء مهلة سداد الضريبة بدون غرامة تأخير بعد 10 أيام من الآن.</span></p>\r\n\r\n<p><span dir=\"rtl\">وطالب الوزير الممولين بسرعة سداد الضريبة &quot;وإلا سيتحملون الغرامة التي حددها القانون أو سيتم الحجز الإداري على وحداتهم العقارية&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال معيط إن مد المهلة الخاصة بسداد الضريبة دون غرامة من 15 يوليو وحتى 15 أغسطس الحالي يأتي تيسيرا على المكلفين ومنعا للتزاحم.</span></p>\r\n\r\n<p><span dir=\"rtl\">وبحسب بيان المالية، قالت سامية حسين رئيسة مصلحة الضرائب العقارية، إن حكم المحكمة الدستورية العليا في القضية رقم 5 لسنة 10 بتاريخ 19 يونيو 1993 الذي استشهد به البعض فيما أثاروه بعدم دستورية الضريبة العقارية، يتعلق بعدم دستورية ضريبة الأرض الفضاء.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضافت: &quot;أما الضريبة العقارية فهي تخاطب الأرض المستغلة والعقارات المبنية&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال الوزير &quot;نحن ندرك أن هناك مشكلات تتعلق بالتزاحم على المأموريات ومعاناة المواطنين في معرفة الضريبة المستحقة لأنها ما زالت غير مميكنة ومدرجة بالدفاترالورقية ونحن نعمل الآن وبكل قوة على وضع آليات ميسرة تسهم في التيسير عليهم&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتم إنشاء مركز اتصال وموقع إلكتروني للاستعلام عن قيمة الضريبة العقارية المستحقة دون عناء للذهاب للمأمورية، ويتم ذلك أيضًا من خلال مساندة البنوك حيث تم وضع آلية للسداد من خلال فروع البنوك العامة (الأهلي - مصر - القاهرة) في كل المحافظات، بحسب الوزير.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف الوزير: &quot;نحن ندرك أن هناك ادعاءات من بعض المغرضين لإعاقة تطبيق القانون بغطلاق شائعات هنا وهناك وبطرق وأساليب ملتوية من الممكن أن تؤثر سلبا على المواطنين ولكني أود التأكيد بأن هذه الادعاءات والأساليب لن تنال من تكاتفنا جميعا في أن نساند هذا البلد وندفع حقه حتى ننهض به&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشار الوزير أن الضريبة العقارية تعفي السكن الخاص حتى 2 مليون جنيه، وما زاد عن ذلك يقوم صاحب الوحدة بسداد مبلغ 120 جنيهًا سنويا تزداد تدريجيا بزيادة قيمة الوحدة، وأن حصيلة هذه المبالغ سوف يتم إنفاقها على تمويل التنمية المحلية والمساهمة في زيادة السكن الاجتماعي وإصلاح منظومة النظافة وما تحتاجه الدولة من بنود للحماية الاجتماعية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وعلى مستوى تطبيق الضريبة على القطاع الصناعي والخدمي، قال الوزير إنه سيتم توقيع بروتوكول مع وزارة السياحة بحلول منتصف الشهر الجاري لوضع آلية حساب الضريبة على المنشآت السياحية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف أن هناك بروتوكولا مماثلا سيتم توقيعه مع وزارة البترول، وسوف تشهد الفترة المقبلة عددا من البروتوكولات مع قطاعات أخرى في إطار القانون وآليات تطبيقه.</span></p>', 'storage/app/public/news/1533565942وزير المالية.jpg', 1, '2018-08-06 16:32:22', '2018-08-06 16:32:22', '2018-08-06 16:32:22', 629, 629, 1, 1);
INSERT INTO `news` VALUES (286, 'وزير المالية ينتدب كمال نجم رئيسًا جديدًا لمصلحة الجمارك', '<p><span dir=\"rtl\">أصدر الدكتور محمد معيط وزير المالية اليوم الثلاثاء، قراراً رقم (343) بندب كمال حسن نجم رئيسًا لمصلحة الجمارك والذى كان يشغل رئيس الإدارة المركزية للمنطقة الشرقية بقطاع العمليات الجمركية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وإلتقى وزير المالية برئيس مصلحة الجمارك الجديد وسلمه قرار ندبه رئيسًا للمصلحة بحضور كل من الدكتور إيهاب أبو عيش نائب وزير المالية للخزانة وعمرو الخولى مساعد الوزير لشؤون الإيرادات والقائم بتسيير أعمال مصلحة الجمارك والدكتور مجدى عبد العزيز مستشار وزير المالية لشؤون الجمارك ورئيس المصلحة الأسبق والشحات الغتورى رئيس الإدارة المركزية لمكتب رئيس مصلحة الجمارك.</span></p>\r\n\r\n<p><span dir=\"rtl\">وخلال اللقاء أكد الدكتور معيط على الدور المهم لرجال الجمارك فى الحفاظ على صحة المواطن وحماية أمنه واستقراره وأمن البلاد من المخدرات والمفرقعات والممارسات الضارة وغير المشروعة، بالإضافة إلى حقوق الخزانة العامة، لافتًا إلى أن رجال الجمارك هم حماة الوطن ويقومون بدور وطنى فى تأمين المنافذ والحدود.</span></p>\r\n\r\n<p><span dir=\"rtl\">كما أكد الوزير أيضًا، فى بيان صحفى اليوم الثلاثاء، على ثقته الكاملة فى استمرار جهود رجال الجمارك فى دعم خطط الدولة وسياساتها الاقتصادية وتنشيط حركة التجارة الدولية لمصر، خاصة الصادرات المصرية بجانب تشجيع الاستثمار، مؤكدًا أن مصر تحتاج إلى تعظيم كل الجهود وتكاتفنا جميعًا حتى نستطيع النهوض بها على أحسن وجه وتحقيق التنمية المنشودة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأكد معيط، على المحاور الرئيسية للعمل فى الفترة المقبلة والتى تتضمن استكمال تجهيز قانون الجمارك الجديد، واستكمال أعمال الحوكمة وتبسيط الإجراءات والتطوير التنظيمى وصولًا إلى منظومة الشباك الواحد لخدمة المتعاملين مع المصلحة، وتطوير العنصر البشرى، واستكمال منظومة ميكنة المصلحة، والإسراع بتشغيل أجهزة الفحص بالأشعة فى المنافذ الجمركية المختلفة والتركيز على صيانتها.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1533636542مصلحة الضرائب.jpg', 1, '2018-08-07 12:09:02', '2018-08-07 12:09:02', '2018-08-07 12:09:02', 629, 629, 1, 1);
INSERT INTO `news` VALUES (287, 'الحبس سنة وغرامة لا تزيد على مليون جنيه لمن أتاح بيانات شخص دون إذنه', '<p><span dir=\"rtl\">عقد مجلس الوزراء اجتماعه اليوم الثلاثاء، برئاسة الدكتور مصطفى مدبولى رئيس مجلس الوزراء، وزير الإسكان والمرافق والمجتمعات العمرانية، وقد وافق المجلس خلال الاجتماع على مشروع قانون لحماية البيانات الشخصية، المعالجة إلكترونيًا، جزئيًا أو كليًا، لدى أى حائز أو متحكم أو معالج لها، وتسرى أحكامه على كل من ارتكب إحدى الجرائم المنصوص عليها فى هذا القانون من المصريين داخل الجمهورية أو خارجها، وغير المصريين المقيمين داخل الجمهورية، وغير المصريين خارج الجمهورية متى كان الفعل معاقبًا عليه فى الدولة التى وقع فيها تحت أى وصف قانونى، وكانت البيانات محل الجريمة لمصريين أو أجانب مقيمين داخل الجمهورية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح الدكتور عمرو طلعت، وزير الاتصالات وتكنولوجيا المعلومات فى التقرير الذى عرضه خلال الجلسة، أن مشروع القانون تم اعداده بتكليف من رئيس الجمهورية، حيث يهدف إلى رفع مستويات أمن البيانات داخل الدولة، وتنظيم عمليات نقلها عبر الحدود وأنشطة التسويق الإلكترونى، وذلك من خلال وضع التزامات على المتحكم والمعالج، لضبط عملية المعالجة الإلكترونية للبيانات الشخصية، لضمان حقوق المواطنين، ومواكبة التشريعات الدولية فى هذا المجال.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف طلعت، أن مشروع القانون ينص على عدم جواز جمع البيانات الشخصية أو معالجتها، أو الإفصاح أو الإفشاء عنها بأية وسيلة من الوسائل، إلا بموافقة الشخص المعنى أو فى الأحوال المصرح بها قانونًا، ويكون للشخص المعنى الحق فى الإطلاع والحصول على البيانات الشخصية الخاصة به الموجودة لدى أى حائز أو متحكم أو معالج، وكذا العدول عن الموافقة المسبقة على معالجة بياناته الشخصية، وله أيضًا الحق فى التصحيح أو التعديل أو المحو أو الاضافة أو التحديث للبيانات الشخصية، بالإضافة إلى تخصيص المعالجة فى نطاق محدد، فضلًا عن العلم والمعرفة بأى خرق أو انتهاك لبياناته الشخصية، على أن يؤدى الشخص المعنى مقابل تكلفة الخدمة المقدمة إليه من المتحكم أو المعالج فيما يخص ممارسته لحقوقه.</span></p>\r\n\r\n<p><span dir=\"rtl\">ويحدد مشروع القانون العقوبات المقررة على الجرائم المنصوص عليها فى هذا القانون المتعلقة بهذا الشأن، ومن بينها أن يعاقب بالحبس مدة لا تقل عن سنة وبغرامة لا تقل عن مائة ألف جنيه ولا تجاوز مليون جنيه، أو بإحدى هاتين العقوبتين، كل من جمع أو تداول أو عالج أو أفصح أو أفشى أو أتاح بيانات شخصية بأية وسيلة من الوسائل فى غير الأحوال المصرح بها قانونًا أو بدون موافقة الشخص المعنى.</span></p>\r\n\r\n<p><span dir=\"rtl\">كما ينص مشروع القانون على أن ينشأ مركز حماية البيانات الشخصية بهيئة تنمية صناعة تكنولوجيا المعلومات، يكون للعاملين به الذين يصدر بتحديدهم قرار من وزير العدل بناء على اقتراح الوزير المختص صفة الضبطية القضائية فى مجال عملهم، ويقوم المركز بصياغة وتطوير السياسات والخطط الاستراتيجية والبرامج اللازمة لحماية البيانات الشخصية والقيام على تنفيذها، ووضع وتطبيق القرارات والضوابط والتدابير والإجراءات والمعايير الخاصة بحماية تلك البيانات، إلى جانب دعم تطوير كفاءة الكوادر البشرية العاملة فى كافة الجهات الحكومية وغير الحكومية القائمة على حماية البيانات الشخصية.</span></p>', 'storage/app/public/news/1533643349رئيس الحكومة.jpg', 1, '2018-08-07 14:02:29', '2018-08-07 14:02:29', '2018-08-07 14:02:29', 629, 629, 1, 1);
INSERT INTO `news` VALUES (288, 'قطاع السجون يفرج عن 1118 سجينا بموجب العفو الرئاسى', '<p><span dir=\"rtl\">ودع 1118 سجينا الزنازين، وذلك تنفيذاً لقرار رئيس الجمهورية رقم (334/ 2018) الصادر بشأن الإفراج بالعفو عن باقى مدة العقوبة بالنسبة لبعض المحكوم عليهم بمناسبة الإحتفال بالعيد السادس والستين لثورة يوليو.</span></p>\r\n\r\n<p><span dir=\"rtl\">وواصل قطاع السجون بإشراف اللواء زكريا الغمري مساعد وزير الداخلية للسجون عقد اللجان لفحص ملفات نزلاء السجون على مستوى الجمهورية لتحديد مستحقى الإفراج بالعفو عن باقى مدة العقوبة ، حيث إنتهت أعمال اللجان إلى إنطباق القرار على 395 نزيلاً ممن يستحقون الإفراج عنهم بالعفو.</span></p>\r\n\r\n<p><span dir=\"rtl\">وباشرت اللجنة العليا للعفو فحص حالات مستحقى الإفراج الشرطى لبعض المحكوم عليهم وإنتهت أعمالها إلى الإفراج عن 723 نزيلاً إفراجاً شرطياً.. بإجمالى 1118 مفرجا عنهم.</span></p>\r\n\r\n<p><span dir=\"rtl\">يأتى ذلك فى إطار حرص وزارة الداخلية على تطبيق السياسة العقابية بمفهومها الحديث وتوفير أوجه الرعاية المختلفة للنزلاء وتفعيل أساليب الإفراج عن المحكوم عليهم الذين تم تأهيلهم للإنخراط فى المجتمع .</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/1533651954مصلحة السون.jpg', 1, '2018-08-07 16:25:54', '2018-08-07 16:24:04', '2018-08-07 16:25:54', 629, 629, 1, 1);
INSERT INTO `news` VALUES (289, 'تراجع أسعار الأراضى الزراعية بعد تطبيق قانون التعديات على الأراضى الجديد', '<p><span dir=\"rtl\">عصر جديد تدخله مصر بدون تعديات على أراضى زراعية وذلك بعد صدور أول حكم بالحبس من المحاكم المصرية على مواطن بالمنوفية ارتكب جريمة التعدى على الأراضى الزراعية، وهو ما يعاقب عليه قانون التعديات على الأراضى الزراعية الجديد، والذى تم إقراره مطلع العام الجارى.</span></p>\r\n\r\n<p><span dir=\"rtl\">اقرار قانون التعديات على الأراضى الزراعية الجديد كان يمثل أكبر ردع لظاهرة التعديات على الأراضى الزراعية، حيث تنص المادة رقم&nbsp; 156 من القانون الجديد بعد تعديلها على &nbsp;أن: &laquo;يعاقب على مخالفة أى حكم من أحكام المادة 152 من هذا القانون أو الشروع فيها بالحبس مدة لا تقل عن سنتين ولا تزيد على خمس سنوات وبغرامة لا تقل عن مائة ألف جنيه عشرين ألف جنيه ولا تزيد على مليون جنيه وتتعدد العقوبات بتعدد المخالفات&raquo;، ويجب أن يتضمن الحكم الصادر بالعقوبة الأمر بإزالة أسباب المخالفة على نفقة المخالف، كما أن لوزير الزراعة قبل الحكم فى الدعوى ان يأمر بوقف أسباب المخالفة بالطريق الإداري، وهذه المادة هى التى تسببت فى الحكم بحبس مواطن محافظة المنوفية الذى تعدى على قطعة أرض زراعية وقام بالبناء عليها.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتخوفات المواطنين من الحبس كانت سبب رئيسى فى تراجع أسعار الأراضى الزراعية فى محافظة الغربية، والتى تحتل أعلى نسبة تعديات على الأراضى الزراعية على مستوى الجمهورية، حيث تراجع سعر قيراط الأرض من 350 ألف جنيه، إلى 200 ألف جنيه.</span></p>\r\n\r\n<p><span dir=\"rtl\">كما وضح قال الدكتور مصطفى بدرة، الخبير الاقتصادى، أن التعديات على الأراضى الزراعية، تمثل أمن قومى، وعدم سرعة تطبيق القانون يهدد باستقرار بأمن المجتمع.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح أن مصر فقدت خلال الـ 7 سنوات الأخيرة، نحو مليون فدان، وهو ما تسبب فى زيادة كساد التشغيل، وارتفاع أسعار الخضروات والسلع، مشيرا إلى أن التراجع فى اسعار الأراضى خطوة جيدة لمواجهة هذه الظاهرة التى تهدد أمن المجتمع.</span></p>\r\n\r\n<p><span dir=\"rtl\">وطبقا للإحصائيات الأخيرة، وصل عدد العقارات المخالفة إلى 600 ألف عقار مخالف وأكثر من 7 ملايين وحدة سكنية غير مرخصة، وبلغ إجمالى حوادث انهيارات المنازل العام قبل الماضى 392 حادثة، نتج عنها 379 حالة إصابة، و192 حالة وفاة، و824 حالة أسر مشردة، وفقا لآخر إحصائية.</span></p>', 'storage/app/public/news/1533714941الاراضي الزراعية.jpg', 1, '2018-08-08 09:55:41', '2018-08-08 09:55:41', '2018-08-08 09:55:41', 629, 629, 1, 1);
INSERT INTO `news` VALUES (290, 'التحقيقات الأولية بحريق سينما ريفولى تكشف وصول الخسائر لمليون جنيه', '<p><span dir=\"rtl\">كشفت تحقيقات نيابة الأزبكية، في حريق سينما ريفولى، أن الحريق أتى على صالة العرض، كما أتت النيران على بعض أعمال التطوير والإنشاء داخل السينما، وقدرت الخسائر الأولية والتى بلغت حوالى مليون جنيه، وتبين من المعاينة أن هناك مخلفات وأوراق.</span></p>\r\n\r\n<p><span dir=\"rtl\">وانتقل فى الساعات الأولى من صباح اليوم، رجال المعمل الجنائى إلى موقع حريق سينما ريفولى بمنطقة التوفيقية وسط القاهرة، وذلك لإجراء معاينة للحريق الذى شب بالسينما، وأسفر عن تآكل صالة العرض بالكامل والدور العلوى للسينما، وذلك للوقوف على ملابسات وأسباب الحريق.</span></p>\r\n\r\n<p><span dir=\"rtl\">على الفور قام الأهالى بمحاولة إخماد النيران إلا أنهم فلشوا فى ذلك، وقاموا بإبلاغ إدارة الحماية المدنية التى قامت بالدفع بالعديد من سيارات الإطفاء لإخماد النيران ومحاصرة ألسنة اللهب.</span></p>\r\n\r\n<p><span dir=\"rtl\">وكانت منطقة التوفيقية قد شهدت فى الساعات الأولى من صباح اليوم الأربعاء نشوب حريق هائل داخل سينما ريفولى بمنطقة التوفيقية وسط القاهرة، نتج عن الحريق احتراق معظم أجزاء السينما، وذلك عقب انتشار ألسنة اللهب إلى باقى السينما.</span></p>\r\n\r\n<p><span dir=\"rtl\">كما انتقل اللواء هشام لطفى مساعد مدير أمن القاهرة إلى مكان الحريق، لمتابعة عملية إخماد النيران، وتم الدفع بالعديد من سيارات الإسعاف، استعدادا لاستقبال أى إصابات نتجت عن الحريق.</span></p>', 'storage/app/public/news/1533723593ريفولي.jpg', 1, '2018-08-08 12:19:53', '2018-08-08 12:19:53', '2018-08-08 12:19:53', 629, 629, 1, 1);
INSERT INTO `news` VALUES (291, 'النائب مجدى بيومى: إقرار حزمة قوانين تحمى محدودى الدخل بدور الانعقاد المقبل', '<p><span dir=\"rtl\">قال النائب مجدى بيومى، إن دور الانعقاد الرابع للبرلمان سيشهد إقرار حزمة من التشريعات من شأنها دعم محدودى الدخل وباقى فئات المجتمع، خاصة وأننا بدأنا جنى ثمار المشروعات القومية التى تم تنفيذها خلال السنوات الماضية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشار عضو مجلس النواب، فى بيان صحفى له اليوم، إلى أن البرلمان اقر منذ بدء عمله وحتى رفع دور الانعقاد الثالث من الفصل التشريعى الأول كم كبير من القوانين فى شتى المجالات، والتى كان لها اثر مباشر على الاستثمار والاقتصاد المصرى، والمنظومة الصحية، بإقرار قانون التامين الصحى الشامل، وحزمة آخر ى من القوانين، التى تهدف جميعها للمصلحة العامة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف مجدى بيومى، أن البرلمان سيقر قوانين التصالح فى مخالفات البناء وتعديل قانون البناء الموحد رقم 119 لسنة 2008، وقانون الإجراءات الجنائية، وقانون الإدارة المحلية، وقانون العمل، وقانون التأمينات والمعاشات، إلى جانب عدد آخر من مشروعات القوانين التى تتطلب صدورها خلال الفترة القادمة، لافتا إلى أن الفترة المقبلة ستشهد ايضا ألقاء الضوء بشكل مكثف على الشق الخدمى والخدمات المقدمة للمواطنين وخاصة فى القرى المحرومة من الخدمات.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1533740763مجدي بيومي.jpg', 1, '2018-08-08 17:06:03', '2018-08-08 17:06:03', '2018-08-08 17:06:03', 629, 629, 1, 1);
INSERT INTO `news` VALUES (292, 'الصحة تسابق الزمن لتطبيق \"التأمين الصحى الشامل\".. البداية فى بورسعيد بــ 6 مستشفيات و23 وحدة بالنصف الأول من 2019 .. هالة زايد: كول سنتر للرد على استفسارات وحجز الكشف للمنتفعين.. وحملات إعلامية لتوعية المواطنين', '<p>تسعى الحكومة ممثلة فى وزارة الصحة إلى الإسراع نحو تطبيق منظومة التأمين الصحى الشامل الجديدة، التى أطلق الرئيس أول مراحلها مطلع شهر يوليو الماضى، بتكلفة قيمتها 1.8 مليار جنيه، ليستفيد منها فى أولى مراحلها ما يقرب من 800 مواطن فى محافظة بورسعيد على أن يعقب ذلك تطبيق المنظومة فى 4 محافظات أخرى خلال عامين ليعمم التطبيق فى الجمهورية خلال 15 عاما.</p>\r\n\r\n<p>وأكدت الدكتورة هالة زايد وزيرة الصحة والسكان متوقع تطبيق التأمين الصحى الشامل فى بورسعيد النصف الأول من العام القادم، مؤكدة أن هناك إجراءات سريعة يتم اتخاذها للانتهاء من تجهيز البنية التحتية لأماكن تقديم الخدمة فى محافظات المرحلة الأولى.&quot;</p>\r\n\r\n<p>وقالت الدكتورة هالة زايد وزيرة الصحة والسكان أن هناك تعاونا كبيرا مع وزارة الاتصالات لبناء النظام الإلكترونى لتشغيل منظومة التأمين الصحى وعمليات إنتاج الكروت الذكية، التى سيتم بموجبها الحصول على الخدمة الطبية والعلاجية للمواطنين المنتفعين بالتأمين الصحى الشامل الجديد، لافتة إلى أن التأمين الصحى سيطبق إجباريا على جميع المواطنين والدولة تتحمل عن غير القادرين الاشتراكات.</p>\r\n\r\n<p>وأوضحت الدكتورة هالة زايد وزير الصحة والسكان أن الوزارة ستنظم بعثات إلى انجلترا حتى يحصل الأطباء الذين سيعملون فى مجال طب الأسرة على خبرات كبيرة لضمان تقديم خدمة متميزة للمريض بمعايير قياسية وتابعت سيتم الاعتماد فى بورسعيد على 6 مستشفيات و23 وحدة صحية بها قوى بشرية من تمريض وأطباء وصيادلة وفنين وموظفين مدربين بشكل جيد.</p>', 'storage/app/public/news/1533801091وزره الصحه.jpg', 1, '2018-08-09 09:51:31', '2018-08-09 09:51:31', '2018-08-09 09:51:31', 634, 634, 1, 1);
INSERT INTO `news` VALUES (293, 'النائب خالد خلف الله يتقدم باقتراح لإنشاء مشروعات بقرية \"هو\" بنجع حمادى', '<p>تقدم اللواء خالد خلف الله، عضو لجنة الدفاع والأمن القومى بالبرلمان، باقتراح برغبة لوزراء التخطيط والصناعة والتنمية المحلية، لإنشاء عدد من المشروعات التى تخدم أهالى قرية &quot;هو&quot; بمركز نجع حمادى.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>وقال النائب خالد خلف الله، فى بيان له اليوم : &quot;يوجد قرية &quot;هو&quot; بمركز نجع حمادى التى تقع غرب النيل قطعة أرض فضاء تقدر مساحتها بـ430 فدانا ملك مصنع السكر التابع لشركة السكر والصناعات التكاملية، وهذه الأرض غير مستغلة ومحاطة بسور غير كامل، الأمر الذى يتطلب استغلال تلك المساحة فى الاستثمار والتنمية.</p>\r\n\r\n<p>وطالب اللواء خالد خلف الله فى اقتراحه باستغلال هذه المساحة كمشروع استثمارى اقتصادى خدمى لخدمة الأهالى وحماية للبيئة وفتح فرص عمل للشباب واستغلال طاقاتهم فى خدمة هذه المنطقة، حيث يمكن من خلالها استغلالها لإقامة عدد من المصانع.</p>\r\n\r\n<p>واقترح &quot;خلف الله&quot; إنشاء عدد من المشروعات الهامة وهى &quot;مصنع خشب لإنتاج 130 ألف طن بدلاً من استيراده لجلب وتوفير العملة الصعبة، ومصنع ورق لإنتاج &quot;110&quot; ألف طن بدلاً من استيراده أيضاً وكذلك إنشاء مصنع غاز ثانى أكسيد الكربون لاستخدامه فى تشغيل وتسخين براجل المصنع التى تصنع السكر بعد العصر، بدلاً من إحداث تلوث بيئى، ومصنع كحول أو قود حيوى &ndash; مصنع خميرة ومصنع أعلاف ومصنع&nbsp;مستحضرات طبيبة وعطور وغيرها من المشروعات التى تساهم فى مجالات الانتاج والتنمية المختلفة.</p>', 'storage/app/public/news/1533805534النائب خالد خلف الله.jpg', 1, '2018-08-09 11:05:34', '2018-08-09 11:05:34', '2018-08-09 11:05:34', 634, 634, 1, 1);
INSERT INTO `news` VALUES (294, 'وزيرة الصحة تجتمع بالفريق المركزى المشرف على تنفيذ قانون التأمين الصحى', '<p><span dir=\"rtl\">قدت الدكتورة هالة زايد وزيرة الصحة والسكان، مساء أمس، اجتماعا بأعضاء الفريق المركزي المشرف على تنفيذ قانون التأمين الصحى الشامل الجديد بمحافظة بورسعيد، لمتابعة سير الأعمال الإنشائية وأعمال الميكنة وتوفير الموارد البشرية وخريطة الخدمات الصحية فى بورسعيد وذلك بديوان عام الوزارة.&nbsp;</span></p>\r\n\r\n<p><span dir=\"rtl\">حضر الاجتماع كل من الدكتور أحمد محيى القاصد، مساعد الوزير لشئون الطب العلاجى، واللواء سيد الشاهد، مساعد الوزير للشئون المالية والإدارية، والدكتور احمد السبكي، مساعد الوزير لشئون الرقابة والمتابعة، والدكتورة سعاد عبد المجيد، رئيس قطاع الرعاية الأساسية الأولية، والمهندس وائل شحاتة مدير مشروع ميكنة التأمين الصحي الشامل الجديد.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضحت الدكتورة هالة زايد، وزيرة الصحة والسكان، أن هذا الاجتماع &nbsp;ضمن سلسلة اجتماعات متتالية تأتى لمتابعة الأعمال الإنشائية للشركات المنفذة للوحدات والمستشفيات بمحافظة بورسعيد، ومناقشة نسب استكمال الأعمال أولا بأول، مشددة على الإسراع &nbsp;الانتهاء من الأعمال الإنشائية والتجهيزية للوحدات والمستشفيات التى تم اختيارها للمرحلة الأولى للتأمين الصحى الشامل الجديد فى المحافظة.</span></p>\r\n\r\n<p><span dir=\"rtl\">يأتى هذا الاجتماع ضمن خطة الوزارة، للمتابعة المستمرة والدورية لمشروعات الحزمة الرئاسية، وعلى رأسها المشروع القومي للقضاء على قوائم انتظار مرضى الجراحات والتدخلات الطبية الحرجة، ومشروع التامين الصحي الشامل الجديد، والمشروع &nbsp;القومي لمسح وعلاج فيروس سي، وتستمر هذه الخطة حتى الانتهاء والاطمئنان الكامل على تنفيذ كافة البرامج المحددة، والتي تم عرضها امام رئيس الجمهورية، وفقاً للبرنامج الزمني المحدد فى هذا الشأن.&nbsp;</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشارت وزيرة الصحة والسكان، الى أنه تم خلال الاجتماع التاكد من تعديل البرنامج الوظيفي لمستشفى النصر ببورسعيد لتكون مستشفى تخصصي للاطفال، كما تم مراجعة القوى البشية التي سيتم الدفع بها في تنفيذ المشروع وعلى راسهم اطباء الاسرة ، لافتة الى الاتفاق على البرامج التدربيية للاطباء ليصبحوا على كامل الاستعداد لتقديم الخدمة في وحدات الرعاية الاولية والتى تعتبر هى النواه الاولى فى المنظومة الجديدة، حيث سيتم تدريب الاطباء الذين سيتم اختيارهم عن طريق مسابقة بالمملكة المتحده واليابان، وتدريبهم على نظام مستشفيات الاحالة وهو النظام المتعارف عليه NHS&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">كما أكدت وزيرة الصحة والسكان، على الاهتمام بالتمريض والذى يعد أحد اهم ركائز بناء المنظومة الصحية، ووجهت بعمل اعلان داخلي لاختيار رئيسات ووكيلات تمريض وتدريبهم بعناية ليكونوا مؤهلين على أعلى مستوى لخدمة المريض المصري.</span></p>\r\n\r\n<p><span dir=\"rtl\">جدير بالذكر أنه تم تفعيل بروتوكول التعاون مع وزارة التربية والعليم والتعليم الفني، الممثل في بنك المعرفة وذلك لإطلاق حزمة من البرامج التدريبية للأطباء ، وذلك عن طريق التعلم عن بعد، &nbsp;تزامنا مع بدء تدريب القوى البشرية بمحافظة بورسعيد.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1533813325وزير الصحة.jpg', 1, '2018-08-09 13:15:25', '2018-08-09 13:15:25', '2018-08-09 13:15:25', 629, 629, 1, 1);
INSERT INTO `news` VALUES (295, 'تأجيل إعادة محاكمة محمد مرسى وقيادات الإخوان بقضية التخابر إلى 6 سبتمبر', '<p><span dir=\"rtl\">قررت محكمة جنايات القاهرة فى جلستها المنعقدة اليوم /الخميس/ برئاسة المستشار محمد شيرين فهمى، &nbsp;تأجيل إعادة محاكمة 22 متهما من قيادات وعناصر جماعة الإخوان الإرهابية، يتقدمهم الرئيس الأسبق محمد مرسى والمرشد العام للجماعة محمد بديع، إلى جلسة 6 سبتمبر المقبل، وذلك فى قضية اتهامهم بارتكاب جرائم التخابر مع منظمات وجهات أجنبية خارج البلاد، وإفشاء أسرار الأمن القومى، &nbsp;والتنسيق مع تنظيمات العنف المسلح داخل مصر وخارجها بقصد الإعداد لعمليات إرهابية داخل الأراضى المصرية.</span></p>\r\n\r\n<p><span dir=\"rtl\">كانت محكمة جنايات القاهرة برئاسة المستشار شعبان الشامى قد سبق وأصدرت حكما فى شهر يونيو 2015 ، بمعاقبة خيرت الشاطر ومحمد البلتاجى وأحمد عبد العاطى (محبوسين) بالإعدام شنقا.. ومعاقبة 13 متهما آخرين هاربين من قيادات وعناصر جماعة الإخوان، بذات العقوبة، وهم كل من محمود عزت نائب المرشد العام لجماعة الإخوان، وصلاح عبد المقصود وزير الإعلام الأسبق، وعمار فايد البنا، وأحمد رجب سليمان، والحسن خيرت الشاطر، وسندس عاصم شلبى، &nbsp;وأبو بكر كمال مشالى، &nbsp;وأحمد محمد الحكيم، ورضا فهمى محمد خليل، ومحمد أسامة العقيد، وحسين القزاز، وعماد الدين عطوه شاهين، وإبراهيم فاروق الزيات.<br />\r\nوتضمن حكم الجنايات فى المحاكمة الأولى معاقبة الرئيس الأسبق محمد مرسى و 16 آخرين (محبوسين) بالسجن المؤبد لمدة 25 عاما وهم كل من: محمد بديع ومحمد سعد الكتاتنى وعصام العريان وسعد الحسينى وحازم فاروق وعصام الحداد ومحى حامد وأيمن على سيد وصفوت حجازى وخالد سعد حسنين وجهاد عصام الحداد وعيد إسماعيل دحروج وإبراهيم خليل الدراوى وكمال السيد محمد وسامى أمين حسين وخليل أسامة العقيد.. ومعاقبة محمد رفاعه الطهطاوى وأسعد الشيخه بالسجن لمدة 7 سنوات لكل منهما.<br />\r\nوكانت النيابة العامة قد أسندت إلى المتهمين تهم التخابر مع منظمات أجنبية خارج البلاد، بغية ارتكاب أعمال إرهابية داخل البلاد، وإفشاء أسرار الدفاع عن البلاد لدولة أجنبية ومن يعملون لمصلحتها، وتمويل الإرهاب، والتدريب العسكرى لتحقيق أغراض التنظيم الدولى للإخوان، وارتكاب أفعال تؤدى إلى المساس باستقلال البلاد ووحدتها وسلامة أراضيها.<br />\r\nوكشفت تحقيقات نيابة أمن الدولة فى القضية، أن التنظيم الدولى الإخوان ارتكب أعمال عنف إرهابية داخل مصر، بغية إشاعة الفوضى العارمة بها، وأعد مخططا إرهابيا كان من ضمن بنوده تحالف قيادات جماعة الإخوان بمصر مع بعض المنظمات الأجنبية، وهى حركة المقاومة الإسلامية &quot;حماس&quot; وميليشيا حزب الله اللبنانى وثيق الصلة بالحرس الثورى الإيرانى، &nbsp;وتنظيمات أخرى داخل وخارج البلاد، تعتنق الأفكار التكفيرية المتطرفة، وتقوم بتهريب السلاح من جهة الحدود الغربية عبر الدروب الصحراوية.<br />\r\nكما كشفت التحقيقات عن وجود تدبير لوسائل تسلل لعناصر من جماعة الإخوان إلى قطاع غزة عبر الأنفاق السرية، وذلك بمساعدة عناصر من حركة حماس لتلقى التدريب العسكرى وفنون القتال واستخدام السلاح على يد عناصر من حزب الله اللبنانى والحرس الثورى الإيرانى، &nbsp;ثم إعادة تلك العناصر بالإضافة إلى آخرين ينتمون إلى تلك التنظيمات إلى داخل البلاد.<br />\r\nوأظهرت التحقيقات أن المتهمين اتحدوا مع عناصر أخرى تابعة للجماعات التكفيرية المتواجدة بسيناء، لتنفيذ ما تم التدريب عليه، وتأهيل عناصر أخرى من الجماعة إعلاميا بتلقى دورات خارج البلاد فى كيفية إطلاق الشائعات وتوجيه الرأى العام لخدمة أغراض التنظيم الدولى للإخوان، وفتح قنوات اتصال مع الغرب عن طريق دولتى قطر وتركيا.<br />\r\nكما كشفت التحقيقات أن التنظيم الدولى وبعض البلاد الأجنبية دعموا قيادات جماعة الإخوان بمصر، بتحويل الأموال اللازمة لهم لتنفيذ المخطط الإجرامى وخلق الفوضى بالبلاد.. حيث بدأ ذلك المخطط عام 2005 واستكملت حلقاته إبان ثورة يناير 2011 لاستغلال الأحداث الجارية بالبلاد، إذ تم الاعتداء بالأسلحة النارية على قوات الأمن والمواطنين فى أنحاء متفرقة إمعانا فى تكريس حالة الفوضى، وإضرارا بالأمن القومى المصرى.<br />\r\nوبينت التحقيقات أن المتهمين قاموا برصد المنشآت الأمنية بشمال سيناء، تمهيدا لفرض السيطرة عليها وإعلانها إمارة إسلامية فى حالة عدم إعلان فوز المتهم محمد مرسى العياط فى الانتخابات الرئاسية.<br />\r\nوأكدت التحقيقات أن المتهمين عصام الحداد وأحمد عبد العاطى ومحمد رفاعه الطهطاوى وأسعد الشيخه ومحى حامد، خلال فترة عملهم برئاسة الجمهورية، قاموا بإفشاء العديد من التقارير السرية الخاصة بهيئة الأمن القومى والمخصصة للعرض على رئيس الجمهورية، بتسريبها لقيادات التنظيم الدولى بالخارج، وقيادات الحرس الثورى الإيرانى، &nbsp;وحركة حماس، وحزب الله اللبنانى، &nbsp;كمكافأة على تنفيذ تلك العمليات الإرهابية، وما قدمته تلك التنظيمات من مساعدات لصالح جماعة الإخوان بمصر حتى تولت مقاليد السلطة.<br />\r\nوجاء بالتحقيقات أن عددا من تلك التقارير السرية، تم تسريبها عبر البريد الالكترونى الخاص برئاسة الجمهورية، وبعلم المتهم محمد مرسى، &nbsp;على نحو ترتب عليه الإضرار بالأمن القومى المصرى.<br />\r\nوأوضحت التحقيقات أنه فى أعقاب عزل محمد مرسى من منصبه، وتغير المشهد السياسى، &nbsp;سارعت جماعة الإخوان المسلمين، وتلك العناصر الإرهابية الآنف بيانها، بتنفيذ تفجيرات واعتداءات ضد القوات المسلحة والشرطة بسيناء، بهدف إرهاب الشعب المصرى، &nbsp;وإثارة الفوضى والنيل من استقلال البلاد وسلامة أراضيها ووحدة المواطنين، وإشعال الفتن الطائفية بينهم فى سبيل إشعال الحرب الأهلية بمصر، قاصدين من وراء ذلك عودة الرئيس المعزول، وإعادة قبضة جماعة الإخوان على البلاد.</span></p>', 'storage/app/public/news/1533824218محمد مرسي.jpg', 1, '2018-08-09 16:16:58', '2018-08-09 16:16:58', '2018-08-09 16:16:58', 629, 629, 1, 1);
INSERT INTO `news` VALUES (296, 'مفوضية \"الدستورية\" تنظر دعوى بطلان قانون الرسوم القضائية اليوم', '<p>تنظر هيئة المفوضين بالمحكمة الدستورية العليا، اليوم الأحد، الدعوى التى تطالب بعدم دستورية المادة الأولى من القانون 90 لسنة 1944 بشأن الرسوم القضائية ورسوم التوثيق فى المواد المدنية.</p>\r\n\r\n<p>وكانت إحدى شركات السياحة أقامت دعواها رقم 123 لسنة 37 دستورية، ودعوى أخرى رفعتها إحدى الشركات، وحملت رقم 45 لسنة 40 دستورية والتى طالبا فى الدعوتين بعدم دستورية المادة الأولى من القانون 90 لسنة 1944 بشأن الرسوم القضائية ورسوم التوثيق فى المواد المدنية، والمستبدلة بالقانون رقم 66 لسنة 1994 والمادة التاسعة من القانون رقم 126 لسنة 2009 فى شأن الرسوم القضائية من تسوية الرسوم باستيداء الفرق بين الطلبات الموضوعية المحكوم برفضها وبين ما سدد عند رفع الدعوى لمخالفتها للمواد ارقام 33 و34 و35 و36 و38 و39 و40 و97 و98 من الدستور.</p>\r\n\r\n<p>وتنص المادة الأولى من القانون رقم 90 لسنة 1944 بالرسوم القضائية ورسوم التوثيق فى المواد المدنية المعدل بالقانون رقم 126 لسنة 2009، يفرض فى الدعاوى، معلومة القيمة، رسم نسبى حسب الفئات الآتية: 2 ٪ لغاية 250 جنيهاً، 3 ٪ فيما زاد على 350 جنيهاً حتى 2000 جنيه، 4 ٪ فيما زاد على 2000 جنيها حتى 4000 جنيه، 5 ٪ فيما زاد على 4000، ويفرض فى الدعاوى مجهولة القيمة رسم ثابت كالآتى:- عشرة جنيهات فى المنازعات التى تطرح على القضاء المستعجل، خمسة جنيهات فى الدعاوى الجزئية، خمسة عشر جنيها فى الدعاوى الكلية الابتدائية، خمسون جنيها فى دعاوى شهر الإفلاس أو طلب الصلح الواقى من الإفلاس، ويشمل هذا الرسم الإجراءات القضائية حتى إنهاء التفليسة أو إجراءات الصلح الواقى من الإفلاس، ولا يدخل ضمن هذه الرسوم مصاريف النشر فى الصحف واللصق عن حكم الإفلاس والإجراءات الأخرى فى التفليسة، ويكون تقدير الرسم فى الحالتين طبقا للقواعد المبينة فى المادتين 75، 76 من هذا القانون.</p>', 'storage/app/public/news/1534060755المحكمة الدستورية.jpg', 1, '2018-08-12 09:59:15', '2018-08-12 09:59:15', '2018-08-12 09:59:15', 629, 629, 1, 1);
INSERT INTO `news` VALUES (297, 'الضرائب العقارية تعفى السكن الخاص للأسرة بعقد الملكية وسند المرافق العامة', '<p><span dir=\"rtl\">أصدرت مصلحة الضرائب العقارية، برئاسة الدكتورة سامية حسين كتابا دوريا تم توزيعه على جميع المأموريات الضريبية لتنظيم إجراءات إعفاء السكن الخاص من الضريبة العقارية وذلك تيسيرا على المجتمع الضريبى وتوضيحا لشروط الإعفاء من الضريبة للوحدات السكنية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف البيان أن الكتاب الدورى نص على إعفاء الوحدة العقارية التى يتخذها المكلف بالضريبة سكنا خاصا له ولأسرته والتى يقل صافى قيمتها الإيجارية عن 24 ألف جنيه سنويا ما يعادل قيمة سوقية للوحدة تبلغ 2 مليون جنيه على أن يخضع للضريبة ما زاد على ذلك، وتشمل الأسرة فى هذه الحالة المكلف بأداء الضريبة وزوجته وأولاده القصر.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح أنه حرصا على تسهيل إجراءات منح الإعفاء للمكلفين وتلافيا لأى إشكاليات قد تثور حول هذا الملف فقد رؤى تنظيم إجراءات منح الإعفاء وتشمل تلك الإجراءات، استلام المأمورية الضريبية المختصة لطلب الإعفاء الذى يتقدم به المكلف بأداء الضريبة العقارية على النماذج المعدة لذلك الغرض وهو نموذج 6 و6 مكرر متضمنة تحديد الوحدة العقارية التى يتخذها سكنا خاصا رئيسيا له ولأسرته، وصورة لبطاقة الرقم القومى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وحتى يقبل طلب الإعفاء أشار البيان إلى ضرورة تقديم المكلف بالضريبة لصورة من عقد شراء الوحدة العقارية أو أى مستند يدل على ملكيته لها أو انتفاعه أو استغلاله لهذه الوحدة العقارية المراد إعفائها من الضريبة أو صورة من إيصالات المرافق العامة المنتفعة بها مثل إيصال استهلاك الكهرباء أو المياه أو الغاز أو غير ذلك من المستندات الدالة على ملكيته وإقامته بالوحدة العقارية محل الطلب</span></p>', 'storage/app/public/news/153406590384118-الضرائب-العقارية.jpeg', 1, '2018-08-12 11:25:03', '2018-08-12 11:25:03', '2018-08-12 11:25:03', 629, 629, 1, 1);
INSERT INTO `news` VALUES (298, 'جنايات القاهرة تقضى بالمؤبد لبديع والبلتاجى والعريان بأحداث البحر الأعظم', '<p><span dir=\"rtl\">قضت محكمة جنايات القاهرة، والمنعقدة بمعهد أمناء الشرطة بطرة، برئاسة المستشار حسين قنديل، اليوم الأحد، بالسجن المؤبد لمحمد بديع، ومحمد البلتاجى، وعصام العريان، وصفوت حجازى، وحسين عنتر، والمشدد 15 سنة لباسم عودة، والسجن 10 سنوات لثلاثة متهمين آخرين بقضية &quot;أحداث البحر الأعظم&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">صدر الحكم برئاسة المستشار حسين قنديل وعضوية المستشارين سامي زين الدين وعفيفي عبد الله المنوفي وسكرتارية ممدوح عبد الرشيد<br />\r\nكانت محكمة النقض ألغت الاحكام الصادرة بمعاقبة بديع وباقي المتهمين بالسجن المؤبد لأتهامها بالتحريض علي ارتكاب &nbsp;أعمال العنف والقتل العمد والتجمهر وأمرت باعادة محاكمتهم امام دائرة اخري</span></p>', 'storage/app/public/news/1534078035جنايات القاهرة.jpg', 1, '2018-08-12 14:47:15', '2018-08-12 14:47:15', '2018-08-12 14:47:15', 629, 629, 1, 1);
INSERT INTO `news` VALUES (299, 'شروط استحقاق الزوجة لنفقة المتعة وفق قانون الأحوال الشخصية', '<p><span dir=\"rtl\">وضع القانون عدة شروط لحصول الزوجة على نفقة المتعة نستعرضها فى التقرير التالى:</span></p>\r\n\r\n<p><span dir=\"rtl\">شروط الحصول على نفقة المتعة وفق قانون الأحوال الشخصية، أن تكون الزوجة مدخول بها ، وقد طلقت بدون رضاها والطلاق قد وقع دون سببا من قبلها.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال أشرف البغدادى المحامى المختص بقانون &nbsp;الأحوال الشخصية، أن المتعة&nbsp;ليست نفقة إنما تعويض وجبر ضرر &nbsp;لخاطر المطلقة وتعويض عن آلامها النفسية التى ألمت بها من طلاقها ولا تعطى المتعة لمطلقة فى طلاق رجعى، كما تستحق&nbsp;المتعة متى ثبت الضرر بحكم تطليق نهائى طبقا للمادة 101 من قانون الإثبات.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتابع البغدادى فى حديثه لـ&quot;اليوم السابع&quot;: أن مقدار المتعة على الأقل سنتين ويكون وفق يسار حالة المطلق ومدة الزواج وسن الزوجة ووضعها الاجتماعي ويجوز أداء المتعة على أقساط، وفى حالة الطلاق الغيابى لا يعد سببا كافيا لنيل تلك النفقة حيث من الممكن أن تكون المطلقة دفعت زوجها بتطليقها بأفعالها وتحال الدعوى للتحقيق لإثبات أن الطلاق لم يتم بدون رضاها؟.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف البغدادى، أنه لابد أن تكون المطلقة أصبح طلاقها بائنا وانتهت مدة العدة لاستحقاق نفقة المتعة، وأن المطالبة بالمتعة لا يسقط بالتقادم لأن الشريعة الإسلامية لا تعرف سقوط الحقوق بالتقادم، فهى لا تسقط إلا بالأداء أو الإبراء، كما أن المتعة ومؤخر الصداق لا حبس فيهما لاختلافهما عن النفقات.</span></p>\r\n\r\n<p><span dir=\"rtl\">وعدد المختص بشئون محاكم الأسرة &nbsp;شروط الحصول على نفقة المتعة وفق للمادة 18 مكرر من المرسوم بقانون 25 لسنة 1929 المعدل بالقانون 100 لسنة 1985 ، أن تكون الزوجة مدخول بها، وأن تكون الزوجة قد طلقت، وأن يكون الطلاق تم بدون رضاها، وأن يكون الطلاق قد وقع دون سبباً من قبلها.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/1534084880الاحوال الشخصية.jpg', 1, '2018-08-12 16:41:20', '2018-08-12 16:40:14', '2018-08-12 16:41:20', 629, 629, 1, 1);
INSERT INTO `news` VALUES (300, '\"صحة البرلمان\": حسم قانون مزاولة مهنة الصيدلة بدور الانعقاد المقبل', '<p><span dir=\"rtl\">قال الدكتور أيمن أبو العلا، وكيل لجنة الشئون الصحية، إن اللجنة ستستكمل مناقشة مشروع قانون مزاولة مهنة الصيدلة فى دور الانعقاد المقبل، مؤكدا أن اللجنة لم تجمد مشروع القانون أو تضعه فى &quot;الدرج&quot;، ولكنها ارجأته بسبب مناقشتها قانون التأمين الصحى الشامل وقانون المسئولية الطبية وقانون الأبحاث الطبية الإكلينيكية، فكان إرجاء قانون مزاولة مهنة الصيدلة نظرا لأنه يحتاج وقت طويل لحسمه.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف &quot;أبو العلا&quot;، فى تصريح لـ&quot;اليوم السىابع&quot;، أن اللجنة لابد أن تناقش مشروع قانون إنشاء هيئة الدواء إما بالتزامن مع مشروع قانون مزاولة مهنة الصيدلة أو قبله، نظرا لارتباط القانونين ببعضهما.</span></p>\r\n\r\n<p><span dir=\"rtl\">&nbsp;وأوضح &quot;أبو العلا&quot;، أنه يؤيد النص فى قانون مزاولة مهند الصيدلة على أن يحظر على الصيدلى صرف دواء للمريض بدون روشتة من الطبيب، وعلى وزارة الصحة أن توفر الأطباء فى العيادات الخارجية، مؤكدا أن اللجنة تستهدف أن الخروج بقانون متوازن ومتكامل يحقق الصالح العام.</span></p>', 'storage/app/public/news/1534146100مجلس النواب.jpg', 1, '2018-08-13 09:41:40', '2018-08-13 09:41:40', '2018-08-13 09:41:40', 629, 629, 1, 1);
INSERT INTO `news` VALUES (301, 'تأجيل دعوى عدم قانون اتحاد الشاغلين والبناء لجلسة 9 سبتمبر', '<p>قررت هيئة المفوضين بالمحكمة الدستورية العليا، تأجيل الدعوى التى تطالب بعدم دستورية نص المادة 77 من قانون اتحاد الشاغلين رقم 119 لسنة 2008 بإصدار قانون البناء ولائحته التنفيذية لمخالفته الدستور لجلسة 9 سبتمبر .</p>\r\n\r\n<p>كان الممثل القانونى لشركة مدينة المفروشات أقام دعواه التى حملت رقم 19 لسنة 40 دستورية، والتى طالب فيها عدم دستورية نص المادة (77 ) من قانون اتحاد الشاغلين رقم 119 لسنة 2008 بإصدار قانون البناء ولائحته التنفيذية لمخالفته الدستور.</p>', 'storage/app/public/news/1534158138تأجيل.jpg', 1, '2018-08-13 13:02:18', '2018-08-13 13:02:18', '2018-08-13 13:02:18', 629, 629, 1, 1);
INSERT INTO `news` VALUES (302, 'اسكان البرلمان: قانون الإيجار القديم قنبلة موقوتة يجب نزع فتيلها', '<p><span dir=\"rtl\">قال النائب عبد الوهاب خليل، عضو لجنة الإسكان والمرافق بالبرلمان، أن قانون الإيجار القديم أحد القوانين الهامة التي لم تنتهى لجنة الإسكان منها فى دور الإنعقاد السابق، حتى أصبح قنبلة موقوته يجب نزع فتيلها بأسرع وقت.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتابع عضو لجنة الإسكان بالبرلمان، فى تصريحات لـ&quot;اليوم السابع&quot;، أن مقدم مشروع قانون الايجار القديم له الحق التمسك &nbsp;بمناقشته ومن حقة المطالبة بإقراراه فى &nbsp;دور الانعقاد القادم، متابعاً يجب أن يتم التوافق بين السلطة التنفيذية والتشريعية على خروج القانون للنور، &quot;يعنى نخرج القانون ومفيش آليات لتنفيذة&quot; .</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح &quot;خليل&quot; أن القانون الموجود فى لجنة الإسكان الآن قائم على عدم &nbsp;طرد أى مواطن، حيث لا يستطيع أحد طرد مواطن من سكنه، لكن ما سيحدث هو إيجاد حل فى إطار الدستور والقانون، كما أعلن النائب اسماعيل نصر الدين، وهو ما يحافظ على السلام الإجتماعى.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1534165928مجلس النواب2.jpg', 1, '2018-08-13 15:12:08', '2018-08-13 15:12:08', '2018-08-13 15:12:08', 629, 629, 1, 1);
INSERT INTO `news` VALUES (303, 'وزارة الاتصالات: إصدار قانون مكافحة جرائم تقنية المعلومات بعد موافقة الرئيس', '<p>قال الدكتور محمد حجازى، رئيس لجنة التشريعات والقوانين بوزارة الاتصالات، إن الوزراة بصدد تفعيل قانون مكافحة جرائم تقنية المعلومات، بعد الموافقة من مجلس الوزراء والمراجعة من قبل مجلس الدولة، مضيفا &laquo;تم إقراره من مجلس النواب، وفى انتظار توقيع الرئيس عبدالفتاح السيسى لتفعيله&raquo;.</p>\r\n\r\n<p>وأوضح &quot;حجازى&quot;&nbsp; أن التوجيه الرئاسىى كان دافع كبير لكى يسير القانون بشكل متسارع، فى ظل إطار الاجندة التشريعية بوزارة الاتصالات، مضيفا &laquo;ابتدينا فيه منذ 8 أو 9 شهور، ويشمل مجموعة تشريعات تتمثل فى مواجهة الأفعال الإجرامية على الانترنت، وعمليات انتحال الصفة، وسرقة المواقع والحسابات الشخصية، وكذلك مواجهة أفعال الاختراق، وكل الأمور التى تهم المواطنين تم تغطيتها فى القانون&raquo;.</p>\r\n\r\n<p>وتابع &laquo;أحد الأسباب التى دفعت إلى إصدار القانون، مكنش فيه إطار قانونى بيحمى المواطن فيما يتعلق بتداول بياناته الشخصية، خاصة أنه مكنش فيه نص قانونى يشير إلى أنه لا يجوز جمع البيانات وتخزينها ومعالجتها إلا بموافقة صاحب البيانات&raquo;، مطالبا المواطنين أن يكونوا على قدر من الوعى وعدم إعطاء البيانات إلا لجهة موثوق فيها فى حدود الأغراض المحددة.</p>', 'storage/app/public/news/1534234832محمدحجازي.jpg', 1, '2018-08-14 10:20:32', '2018-08-14 10:20:32', '2018-08-14 10:20:32', 629, 629, 1, 1);
INSERT INTO `news` VALUES (304, 'تأجيل محاكمة طارق النهرى و3 آخرين بـ\"أحداث مجلس الوزراء\" لجلسة 9 سبتمبر', '<p><span dir=\"rtl\">أجلت محكمة جنايات القاهرة، المنعقدة بأكاديمية الشرطة، اليوم الثلاثاء ، برئاسة المستشار ناجى شحاتة،&nbsp; محاكمة الفنان طارق النهرى، و3 آخرين فى إعادة محاكمتهم بـ&quot;أحداث مجلس الوزراء&quot; وحرق المجمع العلمى إدارى&nbsp; لـجلسه 9 سبتمبر، لتعذر حضور المتهمين.</span></p>\r\n\r\n<p><span dir=\"rtl\">عقدت الجلسة بعضوية المستشارين عبد الرحمن صفوت، وأحمد عبد الحكم وسكرتارية أحمد صبحى عباس وكانت المحكمة عاقبت طارق النهرى،غيابيا بالسجن المؤبد على خلفية اتهامه بالتورط فى حرق المجمع العلمى والتجمهر، والتظاهر وحيازة أسلحة نارية.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1534243855احداث مجلس الوزراء.jpg', 1, '2018-08-14 12:50:55', '2018-08-14 12:50:55', '2018-08-14 12:50:55', 629, 629, 1, 1);
INSERT INTO `news` VALUES (305, 'رئيس الوزراء يتابع قانون التأمين الصحي الشامل مع وزيرى المالية والصحة', '<p>عقد الدكتور مصطفي مدبولي ، رئيس الوزراء، اجتماعا مع الدكتور محمد معيط ، وزير المالية، والدكتورة هالة زايد، وزيرة الصحة، عقب انتهاء، اجتماع الحكومة الأسبوعي، لمتابعة قانون التأمين الصحي الشامل .</p>\r\n\r\n<p>وكان رئيس الوزراء، أكد في اجتماع سابق أهمية سرعة تشكيل الهيئات التي نص عليها قانون التأمين الصحي الشامل، مؤكداً اختيار أفضل الكفاءات في هذه الهيئات، على النحو الذي يمكنها من أداء الدور المنوط بها، لتحقيق أهداف هذه المنظومة في توفير أفضل سبل الرعاية الطبية للمواطنين.</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1534256991الدكتور مصطفي مدبولي.jpg', 1, '2018-08-14 16:29:51', '2018-08-14 16:29:51', '2018-08-14 16:29:51', 629, 629, 1, 1);
INSERT INTO `news` VALUES (306, 'الحكومة توافق على تعديل قانون ضريبة العقارات المبنية اعتبارا من شهر يوليو 2018', '<p><span dir=\"rtl\">الحكومة توافق على تعديل قانون ضريبة العقارات المبنية اعتبارا من يوليو 2013.</span></p>\r\n\r\n<p><span dir=\"rtl\">وافق مجلس الوزراء، اليوم الثلاثاء، على مشروع قرار بتعديل بعض أحكام القانون رقم 196 لسنة 2008 بإصدار قانون الضريبة على العقارات المبنية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وينص التعديل، على أن تستحق الضريبة المربوطة عن أول تقدير اعتبارًا من الأول من يوليو 2013، وتستحق بعد ذلك اعتبارًا من الأول من يناير من كل سنة، وفقًالأحكام القانون، على أن يستمر العمل بهذا التقدير حتى نهاية ديسمبر 2020</span></p>\r\n\r\n<p><span dir=\"rtl\">ويهدف التعديل إلى إعطاء فرصة لمصلحة الضرائب العقارية خلال هذين العامين الإضافيين للقيام بأعمال حصر العقارات المبنية الخاضعة لأحكام هذا القانون، ووضع التقدير الخمسى الجديد لتلك القيمة.</span></p>\r\n\r\n<p><span dir=\"rtl\">ووجه رئيس الوزراء فى هذا الصدد بإعداد بعض التعديلات بالقانون، بما يسهم فى تيسير إجراءات تحصيل الضريبة، ويعالج أى تشوهات ظهرت بالقانون بعد التطبيق.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1534259107مجلس الوزراء.jpg', 1, '2018-08-14 17:05:07', '2018-08-14 17:05:07', '2018-08-14 17:05:07', 629, 629, 1, 1);
INSERT INTO `news` VALUES (307, 'الرئيس السيسى يصل بنى سويف لافتتاح عدد من المشروعات القومية', '<p><span dir=\"rtl\">وصل منذ قليل الرئيس عبد الفتاح السيسى إلى محافظة بنى سويف لافتتاح أكبر مجمع صناعى لإنتاج الأسمنت والرخام والجرانيت.</span></p>\r\n\r\n<p><span dir=\"rtl\">ويوفر المجمع&nbsp; عدد كبير من&nbsp; فرص العمل المباشرة وغير المباشرة معظمها لأبناء الصعيد، حيث شارك فى بناء هذا المجمع العملاق آلاف البنائين المصريين عبر مراحل شاقة بدأت بفتح محجرين للحجر الجيرى والطفلة، وساهم فى بنائه العديد من الشركات الوطنية والأجنبية العالمية، وتتوافر أيضًا الكثير من فرص العمل مع رحلة إنتاج الأسمنت.</span></p>\r\n\r\n<p><span dir=\"rtl\">ويضم&nbsp; المجمع الصناعى العملاق، 3 مصانع كبرى للأسمنت تنتج 20% من إنتاج مصر من الأسمنت، ويحتوى كل مصنع على خطى إنتاج بإجمالى 6 خطوط تكفى لإنتاج ما يزيد على 11 مليون طن من الأسمنت سنوياً، وبما يزيد على 37 ألف طن يومياً.</span></p>\r\n\r\n<p><span dir=\"rtl\">واستغرق إنجاز مشروع مجمع الأسمنت والرخام والجرانيت &quot;وهو الأكبر من نوعه&quot; 18 شهراً على مساحة 5 كم2، بعدما أعطت القيادة السياسية أوامرها بالبدء فى إنشائه مايو 2016.</span></p>', 'storage/app/public/news/1534319915بني سويف.jpg', 1, '2018-08-15 09:58:35', '2018-08-15 09:58:35', '2018-08-15 09:58:35', 629, 629, 1, 1);
INSERT INTO `news` VALUES (308, 'مشروع قانون يحظر سير التوك توك بدون ترخيص..ونائب:50% من الجرائم ترتكب بواسطته', '<p><span dir=\"rtl\">قال النائب ممدودح الحسينى، عضو لجنة الإدارة المحلية المحلية بمجلس النواب، إنه سيعد اقتراح بقانون يعدل قانون المرور، لحظر سير التوك توك فى الشوارع والميادين بدون ترخيص، وذلك لمواجهة الجرائم التى ترتكب عن طريقه.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف &quot;الحسينى&quot;، فى تصريح لـ&quot;اليوم السابع&quot;، أن الاقتراح بقانون سيتضمن وضع عقوبات رادعة ضد سائقى التوك توك فى حالة السير بدون رخصة أو مخالفة الضوابط والقواعد المرورية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتابع عضو لجنة الإدارة المحلية: &quot;التوك توك أصبح الوسيلة الأولى لارتكاب الجرائم بمختلف أنواعها من قتل وخطف وسرقة واغتصاب وغيرها، وإذا رجعنا لسجل القضايا الجنائية فى المحاكم سنجد أن أكثر من 50% من الجرائم يستخدم التوك توك فى ارتكابها أو التسهيل فى ارتكابها، وذلك لأنه مركبة تعمل بدون ترخيص وبدون رقابة من الدولة، وكثير من السائقين مسجلين خطر</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال أن أغلب سائقى التوك توك من الأطفال والشباب تحت 18 سنة، وأنا أفضل أن يحدد سن معين لسائق التوك توك، علاوة على أن تقنين وضع التوك توك يجعله تحت رقابة القانون ويدخل موارد لخزينة الدولة من رسوم الترخيص&quot;.</span></p>', 'storage/app/public/news/1534330521ممدوح الحسيني.jpg', 1, '2018-08-15 12:55:21', '2018-08-15 12:55:21', '2018-08-15 12:55:21', 629, 629, 1, 1);
INSERT INTO `news` VALUES (309, 'توقف حركة السكة الحديد بسوهاج بسبب حريق بجرار قطار نتيجة ماس كهربائى', '<p>حركة قطارات السكة الحديد، عقب حدوث حريق بجرار قطار ركاب &quot;817 أسيوط سوهاج&quot; فور وصوله محطة سكك حديد جزيرة شندويل نتيجة حدوث ماس كهربائى، كما أشارت التحريات الأولية، تمت السيطرة على الحريق وإخماده بمعرفة قوات الحماية المدنية، وجار إرسال الإمداد لتحريك القطار وأعادة حركة السير.</p>\r\n\r\n<p>وكان اللواء هشام الشافعى مدير أمن سوهاج، قد تلقى بلاغا من شرطة السكة الحديد، يفيد بانبعاث بعض الأدخنة بجرار قطار ركاب بين المحافظات فور وصوله محطة سكك حديد جزيرة شندويل، وتم التعامل معه بمعرفة قوات الحماية المدنية.</p>\r\n\r\n<p>وعلى الفور، انتقل إلى مكان البلاغ العميد عبدالحميد حنفى عبد الحميد مدير إدارة المباحث الجنائية، والعميد طارق يحيى رئيس مباحث المديرية، وقوات الحماية المدنية، والتى تمكنت من السيطرة على الحريق وإخمادة ومنعه من الوصول إلى باقى عربات القطار ولم تحدث ثمة إصابات بين الركاب.</p>\r\n\r\n<p>وأشارت التحريات، أن القطار يحمل رقم 817 ركاب والقادم من أسيوط في إتجاه سوهاج، وحدث ماس كهربائى بالجرار تسبب فى نشوب الحريق، وانبعاثات للأدخنة وتمت السيطرة، وتم إخطار هندسة السكة الحديد للتعامل مع الموقف عليه جار تحرير محضرا بالواقعة تمهيدا للعرض على النيابة العامة لتتولى التحقيق.</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1534340331حريق القطار.jpg', 1, '2018-08-15 15:38:51', '2018-08-15 15:38:51', '2018-08-15 15:38:51', 629, 629, 1, 1);
INSERT INTO `news` VALUES (310, 'المالية: حزمة تشريعية لتوحيد وتبسيط الإجراءات الضريبية وقانون جديد للجمارك', '<p><span dir=\"rtl\">أعلن الدكتور محمد معيط وزير المالية أن الحكومة تحرص طوال الوقت علي طرح مبادرات وإصدار قرارات لمساندة رجال الصناعة وتشجيع المستثمرين علي زيادة استثماراتهم ومن هذه المبادرات مشروع قانون بإعفاء ممولي الضرائب من مقابل تأخير سداد الضريبة المستحقة سواء ضريبة الدخل او الدمغة او رسم التنمية وبذلك بنسب تتراوح بين 90% كأعلى شريحة و50%&nbsp; كأدنى شريحة وذلك في اطار التيسير علي القطاع الصناعي والاستثماري مشيرا الي ان مجلس النواب وافق مؤخرا علي ذلك القانون الذي أعدته وزارة المالية تخفيفا للأعباء علي&nbsp; المجتمع الضريبي وهو ما يتواكب مع مبادرة البنك المركزي بإسقاط فوائد قروض المشروعات المتعثرة خاصة الصغيرة والمتوسطة.</span></p>\r\n\r\n<p><span dir=\"rtl\">جاء ذلك خلال اجتماع الوزير اليوم، مع نائبيه أحمد كجوك نائب وزير المالية للسياسات المالية والتطوير المؤسسي ود. إيهاب أبو عيش نائب وزير المالية لشؤون الخزانة العامة مع مجلس إدارة اتحاد جمعيات المستثمرين برئاسة محمد فريد خميس رئيس مجلس إدارة اتحاد جمعيات المستثمرين وأعضاء الاتحاد المصري لجمعيات المستثمرين محرم هلال والدكتور محمد خميس شعبان والمهندس علاء السقطي والأستاذ أسامة حفيلة وأستاذ محمد المرشدي والمهندس محمود الشندويلي و الدكتور سمير عارف والأستاذ علي حمزة والأستاذ عبد الله الغزالي والدكتور صبحي نصر والدكتور محمد حلمي هلال والدكتور محيي حافظ والدكتور محمد سعد الدين والأستاذ سامي سليمان والأستاذ معتصم راشد لمناقشة جهود الحكومة لتحفيز النشاط الاقتصادي وحل مشكلات القطاعين الصناعي والاستثماري.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح الوزير أن القانون يتضمن إعفاء الممول من 90% من غرامات التأخير إذا قام بسداد اصل الضريبة خلال 90 يوما كما يعفي الممول من 70% إذا سدد اصل الدين خلال 45 يوما اما من قام بسدد أصل الدين خلال 45 يوم الأخيرة من مدة 6 اشهر المسموح بها في القانون فانه يعفي من غرامه التأخير بنسبة 50%</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال الوزير ان لدينا ملفات عديدة نريد إنهائها تستهدف صالح الدولة وصالح الممول منها انهاء ملفات الطعن الضريبي وكذلك قانون انهاء المنازعات الضريبية مشيرا الي ان لدينا نحو 120 الف ملف في لجان الطعن الضريبي ونبذل كل الجهد لإنهائها بأسرع وقت ودقة متعهدا بحسم تلك النزاعات قبل 31 ديسمبر المقبل من اجل استقرار المراكز المالية لممولي الضرائب وحفاظا علي حقوق الخزانة العامة، وهو ما سينعكس ايجابيا علي الأوضاع الاقتصادية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال الوزير ان الوزارة تعمل علي اصدار تعديلات تشريعية مهمة تشمل&nbsp; مشروع قانون لتبسيط وتوحيد الإجراءات الضريبية سواء في الدخل او القيمة المضافة وكذلك هناك مشروع قانون جديد للجمارك تم ارساله الي وزارة التجارة والصناعة ومنها الي اتحاد الصناعات والغرف التجارية لإبداء ملاحظاتهم ونحن بالتأكيد نأخذها بعين الاعتبار بما يتوافق مع مصلحة الدولة والمواطن</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف الوزير ان هناك مشروع قانون الفاتورة الالكترونية والتي ستسهم في تطبيق القيمة المضافة بشكل دقيق وفوري ويجري حاليا تركيب الأجهزة الخاصة بمنظومة الفاتورة الالكترونية في سلاسل المحلات التجارية والسوبر ماركت بما يضمن تسجيل لحظي الكترونيا لكل معاملة تجارية ويتم حاليا اختبار التجربة مع عدد من كبريات السلاسل التجارية للتأكد من دقة وسلامة التطبيق لحين تنفيذ المنظومة بشكل كامل مع بداية العام الجديد</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال ان الإصلاحات التشريعية تتواكب مع إصلاحات إدارية تتبناها الحكومة حاليا مثل تفعيل منظومة المدفوعات الالكترونية سواء لإيرادات الدولة من الضرائب والرسوم الجمركية أو لمدفوعاتها حيث تم إلغاء العمل بالشيكات الحكومية الورقية ومن أول يناير 2019 سيتم إلزام جميع المتعاملين مع الجهات الحكومية بسداد المبالغ المالية التي تزيد عن 100 ألف جنيه بأحدي وسائل الدفع الالكترونية مع تطبيق غرامة 10% من قيمة المبالغ المستحقة في حالة السداد النقدي أو بشيكات.&nbsp;&nbsp;</span></p>\r\n\r\n<p><span dir=\"rtl\">وحول إعادة هيكلة الإجراءات الضريبية أكد وزير المالية ان الحكومة تسعي إلي تطوير الإجراءات الضريبية والجمركية لضمان توحيد المعاملة في جميع الموانئ والمنافذ إلي جانب حوكمة تلك الإجراءات حيث نتعاون حاليا مع شركة &quot; ارنست اند يانج&quot; لإعادة هندسة الإجراءات الضريبية كما سوف نطرح سبتمبر المقبل (أي خلال أسبوعين) مناقصة عامة لميكنة تلك الإجراءات وتوحيدها خاصة في ضرائب الدخل وضرائب القيمة المضافة&nbsp; .</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشار الوزير الي قيام وزارة المالية بتطبيق نظام الخزانة الموحد (TSA) وميكنة إدارة المالية الحكومية (GFMIS) والذي يسهم في تحقيق أكبر قدر من الإنضباط المالى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال الوزير نحن كدولة مستفيدين من توسع الصناعة وزيادة الانتاج والتصدير لانها ستوفر فرص عمل اضافية وكذلك توسيع النشاط الاقتصادي الذى سيدر دخلا للدولة فى صورة ضرائب ورسوم وبالتالى فإننا حريصون على نجاح رجال الاعمال والصناعة لان هذا فى صالح الدولة ولأننا شركاء فى هذه الدولة مشيرا الى ان المستثمر الأجنبي ينظر الى قوة اقتصاد الدولة وهل تسير فى اتجاه صحيح ام لا مؤكدا حرص القيادة السياسية على تشجيع القطاعات الصناعية والزراعية والانتاجية وهناك متابعة مستمرة كذلك من رئيس مجلس الوزراء لكل الملفات والاصلاحات الاقتصادية والمالية التى تسهم فى استدامة النمو واستقرار الاوضاع الاقتصادية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال نحن نعمل الان بالتوافق والتنسيق المستمر مع وزارة التجارة والصناعة للانتهاء من مشروع قانون جديد للمشروعات الصغيرة والمتوسطة وبحيث يكون فيه اليات تحفيزية سواء على مستوى المعاملة الضريبية او الاجراءات الادارية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفى سياق اخر اشار الوزير الى ان مصر تسير بخطى سريعة فى اتجاه ربط البيانات الكترونيا فى الجهات الادارية للدولة بحيث يكون لدينا قاعدة بيانات واقعية عن المواطنين واسرهم واحتياجاتهم حتى يمكن تطبيق نظام التأمين الصحى ونظام الحماية الاجتماعية بشكل سليم.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال الوزير اننا نعمل علي تقنين منظومة البطاقات التموينية ورغيف الخبز مشيرا الي ان 82 مليون مواطن يحصلون علي رغيف الخبز المدعم وان هذا الرغيف يكلف الدولة 65 قرشا يدفع المواطن منها خمسة قروش فقط والباقي تتحمله الدولة وان 69 مليون مواطن يحصل علي دعم الدولة للبطاقات التموينية المقدرة بـ 50 جنيه للفرد ويجري حاليا تقنين ذلك بآليات متعددة حيث تم اكتشاف ان هناك بطاقات للوفيات او برقم قومي وهمي ومع بداية العام الجديد سوف ننتهي من تدقيق هذه البطاقات</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال الوزير اننا نقوم حاليا بتحديد مشاكل القيمة المضافة وتجميعها لندخل بتعديل تشريعى الى البرلمان وكذلك سيتم الامر بالنسبة للضريبة العقارية حيث سيتم النظر فى اعداد بعض التعديلات بالقانون بما يسهم فى تيسير اجراءات تحصيل الضريبة وعلاج اية تشوهات ظهرت فى التطبيق مؤخرا.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفى نهاية اللقاء طالب أعضاء اتحاد المستثمرين المشاركة في مبادرة الرئيس عبد الفتاح السيسي الخاصة بإنهاء طوابير الانتظار في العمليات الجراحية، حيث أعلن محمد فريد خميس أنه سيبدأ بنفسه ويقدم على الفور 60 مليون جنيه كمساهمة في المبادرة التي تقوم بها الدولة في إطار الحماية الاجتماعية، كما أبدى كل رؤساء جمعيات المستثمرين ترحيبهم بالمشاركة الفورية وتوالت الموافقات على الاشتراك حيث قرروا إنشاء صندوق لهذا الغرض مع توقيع بروتوكول مع وزارة الصحة للبدء فورًا في وضع الآليات التي تسهم في سرعة التنفيذ.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفي هذا السياق أكد وزير المالية تقديره للدور الذي يقوم به رجال الأعمال ليس في مجالي الصناعة والإنتاج فقط ولكن أيضًا بالدور الاجتماعي المعرفون به في المجتمع، من جانبه أكد محمد فريد خميس أهمية الإصلاحات التي تتبناها الدولة من اجل تخفيض حجم الدين العام وأعباء خدمته التي تستنزف جانب كبير من إيرادات الموازنة العامة، لافتا إلي أهمية توحيد الجهود لزيادة معدلات التنمية الاقتصادية من خلال رفع المعاناة عن الصانع المصرى، ووضعه على قدم المساواة مع منافسيه ، وحماية الصناعة الوطنية ، لزيادة الاستثمارات والإنتاج ومن ثم خلق المزيد من الوظائف ، فلا حل لمشكلات مصر إلا بالصناعة التى ستؤدى إلى تقليل العجز وتخفيف الضغط على العملة الأجنبية.</span></p>\r\n\r\n<p><span dir=\"rtl\">واقترح محمد فريد خميس تكوين مجموعات عمل من رجال الاعمال والجهات الحكومية لوضع خطة عمل للنهوض بالصناعات المصرية حيث يعمل بمصر أكثر من 40 ألف شركة أعضاء 45 جمعية للاستثمار في النشاط الصناعي والسياحي.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال إن الاقتراحات لابد ان تراعي الأوضاع التي تمر بها الدولة حاليا واحتياجها لزيادة مواردها وتخفيض الانفاق العام حتي لا نعتمد علي الاقتراض علي ان نراعي في نفس الوقت ما نمتلكه من قدرات صناعية وإنتاجية ضخمة والتي يمكنها زيادة الإنتاج ومضاعفة الصادرات.</span></p>\r\n\r\n<p><span dir=\"rtl\">ورحب وزير المالية باقتراحات اتحاد جمعيات المستثمرين مطالبا بإعداد دراسة عن أهم المشكلات ومقترحات حلها، لدراستها من خبراء وزارة المالية ،للخروج بحلول عادلة تحقق مصالح الدولة والمستثمرين .</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1534344943وزارة المالية.jpg', 1, '2018-08-15 16:55:43', '2018-08-15 16:55:43', '2018-08-15 16:55:43', 629, 629, 1, 1);
INSERT INTO `news` VALUES (311, 'قرارات جمهورية بإقامة تجمعات عمرانية جديدة بالمنيا وبنى سويف والأقصر', '<p><span dir=\"rtl\">أصدر الرئيس عبد الفتاح السيسى، قرارات جمهورية، نشرتها الجريدة الرسمية فى عددها اليوم، الخميس بتخصيص قطعتى أرض من الأراضى المملوكة للدولة ملكية خاصة بناحية محافظتي المنيا وبني سويف لاستخدامهما في إقامة تجمعين جديدين (الفشن الجديدة) و(ملوي الجديدة).</span></p>\r\n\r\n<p><br />\r\n<span dir=\"rtl\">وتضمنت القرارات، الموافقة علي إعادة تخصيص مساحة 94.49 فدان من الأراضي المملوكة للدولة ملكية خاصة بناحية رأس سدر محافظة جنوب سيناء لصالح القوات المسلحة.<br />\r\n<br />\r\nكما تضمنت، تخصيص مساحة (9675.43) فدان من الأراضي المملوكة للدولة ملكية خاصة بناحية محافظة الأقصر، لاستخدامها في إقامة مجتمع عمراني جديد (مدينة الأقصر الجديدة).</span><br />\r\n<br />\r\n&nbsp;</p>', 'storage/app/public/news/1534410691السيسي.jpg', 1, '2018-08-16 11:11:31', '2018-08-16 11:11:31', '2018-08-16 11:11:31', 629, 629, 1, 1);
INSERT INTO `news` VALUES (312, 'الرئيس يصدق علي  قانون منح الجنسية للأجانب المقيمين بوديعة', '<p>أصدر الرئيس عبد الفتاح السيسى، أمس، القانون رقم 173 لسنة 2018 بتعديل بعض أحكام القانون رقم 89 لسنة 1960 فى شأن دخول وإقامة الأجانب بأراضى جمهورية مصر العربية والخروج منها والقانون رقم 26 لسنة 1975 بشأن الجنسية المصرية، وتم نشره فى الجريدة الرسمية.</p>\r\n\r\n<p>التفاصيل بشأن هذا القانون الذى يستحدث ما يسمى بـ&quot;الأجنبى المقيم بوديعة&quot;، ويجيز منحه الجنسية المصرية شرط أن يسبق طلب التجنس الإقامة لمدة 5 سنوات متتالية، ونوضح هذه التفاصيل فى السطور التالية:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;1 ـ يُقسم الأجانب من حيث الإقامة إلى أربع فئات (أجانب ذوى إقامة خاصة، أجانب ذوى إقامة عادية، أجانب ذوى إقامة مؤقتة، أجانب ذوى إقامة بوديعة).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2 ـ الأجانب ذوو الإقامة بوديعة هم الأجانب القادمون للاستثمار فى مصر، والذين يقومون بإيداع وديعة نقدية فى أحد البنوك المصرية لا تقل قيمتها عن سبعة ملايين جنبه مصرى، أو ما يعادلها بالعملة الأجنبية التى يحدهها قرار من رئيس مجلس الوزراء.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>3 ـ يصدر بتحديد ضوابط المرخص لهم بالإقامة ومدتها، وقيمة الوديعة وتنظيم إيداعها واستردادها، والبنوك التى يتم الإيداع بها، ومجالات الاستثمار قرار من وزير الداخلية بعد موافقة مجلس الوزراء.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;4 ـ يجوز بقرار من وزير الداخلية منح الجنسية المصرية لكل أجنبى من ذوى الإقامة بوديعة متى أقام فى مصر لمدة خمس سنوات متتالية سابقة على تقديم طلب التجنس وفقا للضوابط المنصوص عليها فى المادة (20 مكررا) من القانون رقم 89 لسنة 1960 فى شأن دخول وإقامة الأجانب بجمهورية مصر العربية والخروج منها، وتوافرت فى شأنه الشروط أرقام (1)، (29)، (3) ، المنصوص عليها فى البند (رابعا) من المادة (4) من هذا القانون.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>5 ـ فى حالة قبول طلب التجنس، تؤول قيمة الوديعة إلى الخزانة العامة للدولة، ويسرى على منح الجنسية وفقا لحكم هذه المادة ذات القيود الواردة فى المادة (9) من هذا القانون.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>6 ـ يصدر بشروط وقواعد تقديم طلب التجنس، قرار من وزير&nbsp;&nbsp;الداخلية بعد موافقة مجلس الوزراء.</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1534419945قانون الجنسية.jpg', 1, '2018-08-16 15:40:47', '2018-08-16 13:45:45', '2018-08-16 15:40:47', 629, 629, 1, 1);
INSERT INTO `news` VALUES (313, 'جدل برلماني حول مشروع قانون عقوبة الزواج العرفي', '<p><span dir=\"rtl\">جدل برلمانى واسع حول مشروع القانون الذى أعلنت النائبة آمنة نصير عن إعداده، بشأن معاقبة الشاب المتزوج عرفياً عام على الأقل وتوجيه لوم أخلاقى للفتاة، لمواجهة ظاهرة انتشار الزواج العرفى فى مصر، بعدما رأى البعض أن التشريع لن يفيد فى حل المشكلة وأن التوعية الأخلاقية أهم، فيما اختلف آخرون على العقوبة الواردة فى التشريع المقترح.</span></p>\r\n\r\n<p><span dir=\"rtl\">أكدت الدكتورة آمنة نصير عضو مجلس النواب، أنها بدأت فى إعداد أول تشريع لمواجهة أزمة انتشار ظاهرة الزواج العرفى فى مصر بعد دراسة الأمر من كافة أبعاده وحيثياته حتى يستوعب هذه المأساة الاجتماعية المعاصرة، لافتة إلى أنها ستنتهى منه خلال الأيام المقبلة، وستقدمه فى أكتوبر المقبل مع بداية دور الانعقاد.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأضافت &quot;نصير&quot; فى تصريح لـ&quot;برلمانى&quot;، أن التشريع سيضبط عملية الزواج بالضوابط الشرعية، ومعاقبة الخارجين عن نطاق الزواج الرسمى أمام المأذون، بمعاقبة سجن الشاب المتزوج عرفياً عام على الأقل، ومضت: &quot;سيوجه للبنت لوم من منظور أخلاقى، يكفى خيبة أملها ومستقبلها، بعدما تركتها أسرتها لقمة طرية فى يد شاب خارج عن القانون والشريعة&quot;.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">وأشارت الدكتور آمنة نصير، إلى أنها ستستعين بكافة المستشارين من الجهات المعنية وذوى الشأن لمعرفة رأيهم فى مشروع القانون، فضلاً عن فتح نقاش موسع حوله بعد إحالته للجان النوعية من قبل الأعضاء، إلى جانب احتمالية عمل حوار مجتمعى حول المشروع ليحقق توافقا مجتمعيا، وتسانده وسائل الإعلام المختلفة.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">بينما اختلفت النائبة غادة عجمى مع العقوبة الواردة فى المشروع المقترح، مطالبة بمعاقبة الفتاة المتزوجة عرفياً هى الأخرى مثلها مثل الشاب، مضيفة: &quot;فيه ستات واخدينها سبوبة علشان يطَّلقوا وياخدوا المعاش فبتتجوز عرفى، ودى سرقة للدولة&quot;، مقترحة بأن تكون العقوبة للشاب والفتاة، هى الحبس من عام إلى ثلاثة أعوام حد أقصى.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">واعتبر إلهامى عجينة عضو مجلس النواب، أن العقوبة الواردة فى التشريع المقترح بها خلل، مقترحاً أن يُبرئ الشاب من العقوبة، وتكون العقوبة على الفتاة أغلظ، مضيفاً: &quot;المفروض البنت عندها حياء أكتر من الولد، ولو البنت ما وافقتش من الأول مش هيكون فيه زواج عرفى، إذن هى أساس المشكلة، وأنا غير موافق على عقوبة السجن، لأنها مكلفة جداً على الدولة&quot;.</span></p>', 'storage/app/public/news/1534429735مجلس النواب2.jpg', 1, '2018-08-16 16:30:31', '2018-08-16 16:28:55', '2018-08-16 16:30:31', 629, 629, 1, 1);
INSERT INTO `news` VALUES (314, '\"إسكان البرلمان\" تكشف تفاصيل تحرير عقود الوحدات السكنية بقانون الإيجار القديم', '<p><span dir=\"rtl\">أكد محمد إسماعيل، أمين سر لجنة الإسكان بمجلس النواب، أن مشروع قانون الإيجار القديم قائم على فلسفة لا ضرر ولا ضرار، وأن القاعدة الأساسية إيجاد آلية محترمة للتعامل بين المالك والمستأجر.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح أمين سر لجنة الإسكان بمجلس النواب، لـ&quot;اليوم السابع&quot;، أن هناك إجماع لدى جميع النواب على عدم تحرير العقود للوحدات فى حالة واحدة فقط، وهى الوحدات المغلقة لأسباب، أبرزها امتلاك أصحابها لوحدات غيرها، وهذا يعرف من خلال قاعدة البيانات بهيئة المجتمعات العمرانية، فمن يمتلك وحدة أخرى ليس من حقه امتلاك وحدة بنظام الإيجار القديم.</span></p>\r\n\r\n<p><span dir=\"rtl\">ونفى أمين سر لجنة الإسكان بمجلس النواب، تحرير كل العقود الخاصة بالإيجار القديم كما يروج البعض، مشيرا إلى أن مشروع القانون سيناقش على مراحل ثلاث، الأولى الوحدات المؤجرة من قبل الوزارات والهيئات الحكومية، والثانية وحدات النشاط التجارى، وفى المرحلة الأخيرة الوحدات السكنية، ولكن الفلسفة قائمة فى المراحل الثلاث على تحقيق قاعدة لا ضرر ولا ضرار، ومراعاة الأسر غير القادرة ومحدودة الدخل.</span></p>', 'storage/app/public/news/1534671047الاسكان.jpg', 1, '2018-08-19 11:30:47', '2018-08-19 11:30:47', '2018-08-19 11:30:47', 629, 629, 1, 1);
INSERT INTO `news` VALUES (315, 'السيسى يصدق على قانونين لتخفيف الأعباء الجمركية والضريبية ومكافحة جرائم المعلومات', '<p>&nbsp;</p>\r\n\r\n<p><span dir=\"rtl\">صدق الرئيس عبدالفتاح السيسى، على القانون رقم 174 لسنة 2018 بالتجاوز عن مقابل التأخير والضريبة الإضافية والغرامات، كما وافق الرئيس على تعديل بعض أحكام القانون رقم 14 لسنة 2018 بتجديد العمل بقانون إنهاء المنازعات، وأصدر الرئيس القانون رقم 175 لسنة 2018 فى شأن مكافحة جرائم تقنية المعلومات، وذلك بعد أن أقره مجلس النواب، وصدق الرئيس على القانون رقم 176 لسنة 2018 الخاص بتنظيم نشاطى التأجير التمويلى والتخصيم، ويأتى القانون فى إطار اهتمام الدولة بجذب مزيد من الاستثمارات المصرية والأجنبية، سعيًا لتحسين الأوضاع الاقتصادية فى البلاد، من خلال العمل على تحقيق برنامج الحكومة للإصلاح الاقتصادى</span></p>', 'storage/app/public/news/1534674948السيسيي.jpg', 1, '2018-08-19 12:35:48', '2018-08-19 12:35:48', '2018-08-19 12:35:48', 629, 629, 1, 1);
INSERT INTO `news` VALUES (316, 'ثلاثة  قوانين على أولوية لجنة القوى العاملة بالبرلمان بدور الانعقاد الرابع', '<p><span dir=\"rtl\">قال النائب محمد وهب الله وكيل لجنة القوى العاملة بمجلس النواب ، إن اللجنة لديها العديد من الأولويات خلال دور الانعقاد الرابع ، تسعى إلى انجازها، ويأتى فى مقدمتها سرعة الانتهاء من قانون العمل الجديد، نظرا لأهميته لأنه يساعد على تقليل نسبة البطالة ويتضمن توفير نظام تأمينى كامل وإنشاء محاكم متخصصة عمالية، وكذلك إلغاء استمارة 6 ، بالإضافة إلى الحقوق التى منحت للشباب والمرأة وذوى الاحتياجات الخاصة ويساعد على تطوير العمل بشركات القطاع الخاص.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف وهب الله في أن إصدار قانون صرف البدل النقدى للعاملين مقابل الإجازات فى منتهى الأهمية والتى لازالت قضاياهم فى المحاكم، بجانب سرعة الانتهاء من قانون التأمينات الاجتماعية، مؤكدا أنه فى حالة عدم تقديم الحكومة خلال بداية دور الانعقاد، سيتم مناقشة القانون المقدم منا التأمين والمعاشات الجديد يشمل على 153 مادة إنشاء هيئة مستقلة لإدارة أموال التأمينات التى تملك أكثر من &nbsp;700 مليار، وألا تقل العلاوة الدورية عن 15 % واستثمار أموال التأمينات فى استثمار آمن.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتابع وكيل لجنة القوى العاملة بالبرلمان: فى بداية دور الانعقاد ستوافق لجنة القوى العاملة على إعفاء الشركات فوائد الديون المستحقة عليها من التأمينات الاجتماعية، لافتا إلى أن مشروع القانون ورد إلى البرلمان من الحكومة.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/1534685234القوي العاملة.jpg', 1, '2018-08-19 15:27:35', '2018-08-19 15:26:49', '2018-08-19 15:27:35', 629, 629, 1, 1);
INSERT INTO `news` VALUES (317, 'تكنولوجيا البرلمان تطالب \"تنظيم الاتصالات\" بالانتهاء من لائحة قانون الجرائم الألكترونية', '<p><span dir=\"rtl\">قال النائب أحمد بدوى عضو لجنة الاتصالات وتكنولوجيا المعلومات بمجلس النواب، ان تصديق الرئيس عبد الفتاح السيسى على قانون الجرائم الالكترونية سيقضى على الشائعات والأكاذيب التى ملأت مواقع التواصل الاجتماعى، لافتا إلى أن لجنة الاتصالات بمجلس النواب طالبت هيئة تنظيم الاتصالات بالانتهاء من اللائحة التنفيذية للقانون فى أسرع وقت.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف بدوى في&nbsp;أن المواطن أصبح له حقوق وعليه واجبات والبعض يروج بأن الدولة ستراقب الفيس بوك وهذا الأمر غير صحيح فالقانون جاء لحماية بيانات المستخدمين قبل اى شيء ولو هناك اختراق للحسابات فهو من العصابات الدولية ومن الفيس بوك نفسه.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح بدوى أن الحكومة والإعلام عليهما دور كبير فى توعية المواطنين بأهمية القانون وتنفيذه خلال الفترة المقبلة، مشيرا إلى أن القانون يبعث برسالة طمأنينة للمواطنين لحمايتهم قبل أى شيء.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1534687928احمد بدوي.jpg', 1, '2018-08-19 16:12:08', '2018-08-19 16:12:08', '2018-08-19 16:12:08', 629, 629, 1, 1);
INSERT INTO `news` VALUES (318, 'محلية البرلمان تدعم الحكومة فى إجراءات تقنين التوك توك وحظره على الطرق الرئيسية', '<p>أشاد النائب محمد الدامى، أمين سر لجنة الإدارة المحلية بمجلس النواب، بقرار وزير التنمية المحلية بشأن تذليل معوقات تراخيص مركبات التوك توك وتحديد خطوط سير لها، وحظر سير التوك توك فى المناطق الحضرية المخططة أو الطرق الرئيسية أو الطرق السريعة أو بين المدن وبعضها البعض.</p>\r\n\r\n<p>وقال &quot;الدامى&quot;، إن مركبات التوك توك تسير بشكل فوضوى حاليا فى كل المحافظات والمدن والقرى على مستوى الجمهورية دون وجود رقابة وتسير بدون ترخيص، كما تتسبب فى الازدحام المرورى وتعطيل حركة وسير المواطنين وتستخدم فى ارتكاب بعض الجرائم، مما يتطلب تفعيل هيبة الدولة والضرب بيد من حديد، مع تسهيل إجراءات التراخيص.</p>\r\n\r\n<p>وأضاف &quot;الدامى&quot;، أن لجنة الإدارة المحلية بالبرلمان تدعم الإجراءات التى تتخذها الحكومة لتسهيل وتذليل إجراءات ترخيص التوك توك وتقنينه وتحديد خطوط سيره، مطالبا بمراقبة الطرق للتأكد من تفعيل هذه القرارات.</p>\r\n\r\n<p>وكان اللواء محمود شعراوى وزير التنمية المحلية، أصدر كتاباً دورياً لكافة المحافظات، بشأن تذليل معوقات تراخيص مركبات التوك توك وتحديد خطوط سير لها، وذلك فى إطار حرص الدولة على تسهيل كافة الإجراءات المطلوبة لترخيص مركبات التوك توك، تضمن ضرورة موافاة الوزارة بإجمالى عدد مركبات التوك توك على مستوى المحافظة، وعدد المركبات التى تم ترخيصها حتى تاريخ إرسال الرد، وأسباب التأخير فى استخراج التراخيص لباقى المركبات، كما طالب وزير التنمية المحلية المحافظات، بتحديد خطوط سير مركبات التوك توك لتكون فى المناطق غير المخططة، وبين القرى وبعضها البعض فقط لا غير، مشيرا إلى ضرورة عدم سير التوك توك فى المناطق الحضرية المخططة أو الطرق الرئيسية أو الطرق السريعة أو بين المدن وبعضها البعض.</p>', 'storage/app/public/news/1535269527لجنه محليه.jpg', 1, '2018-08-26 09:45:27', '2018-08-26 09:45:27', '2018-08-26 09:45:27', 634, 634, 1, 1);
INSERT INTO `news` VALUES (319, 'قانون الصيد الجديد.. يتضمن عقوبات تصل إلى 500 ألف جنيه', '<p><span dir=\"rtl\">قال النائب هشام الشعينى، رئيس لجنة الزراعة والرى بمجلس النواب، إن تعديل قانون صيد الأسماك والأحياء المائية وتنظيم المزراع السمكية والصادر بالقانون رقم 124 لسنة 1983، من اولويات اللجنة فى دور الانعقاد المقبل.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح الشعينى، لـ&quot;اليوم السابع&quot;، أن اللجنة أعادت التعديلات للحكومة مرة أخرى لضبط الصياغة، لافتا إلى أن التعديلات تضمنت حظر إنشاء جزر أو جسور أو سدود بالبحيرات إلا لتنفيذ أحد المشروعات القومية التى تحددها الدولة وبموافقة مجلس الوزراء، بناء على عرض الوزير المختص، بالإضافة لحظر على أى جهة أو هيئة أو وحدة محلية تجفيف أى مساحة من البحيرات إلا لتنفيذ أحد المشروعات القومية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشار رئيس لجنة الزراعة بمجلس النواب، إلى أن التعديلات تضمنت عقوبات بالحبس مدة لا تقل عن ستة أشهر ولا تزيد عن سنة وبغرامة لا تقل عن مائة ألف جنيه ولا تزيد عن خمسمائة ألف جنيه وفى حالة العودة تضاعف العقوبة، كل من يلقى أجسام صلبة فى مناطق الصيد، استخدام أو إدخال أسماك أجنبية أو بويضات لأى غرض إلا بتصريح من الهيئة، زراعة البوص فى مناطق الصيد.</span></p>', 'storage/app/public/news/1535272649لجنة الاخبار.jpg', 1, '2018-08-26 10:37:29', '2018-08-26 10:37:29', '2018-08-26 10:37:29', 629, 629, 1, 1);
INSERT INTO `news` VALUES (320, 'علاء والى يؤكد أولوية قانون التصالح بمخالفات البناء بدور الانعقاد الرابع', '<p>قال المهندس علاء والى، عضو مجلس النواب، إن مشروع قانون التصالح فى مخالفات البناء من القوانين الهامة وذات الأولوية للبرلمان لإقراره فى بداية دور الانعقاد الرابع، مشيراً إلى أن البرلمان يقوم بالتنسيق مع وزارات الاسكان والتنمية المحلية والزراعية والمالية لمراجعة ووضع اللمسات النهائية لمشروع قانون التصالح في مخالفات البناء لكيفية التصالح في ما يقرب من 20 مليون وحدة مخالفة، وخاصة المواد المعنية بآلية التصالح والقيمة المفروضة وكيفية وطرق سدادها إلى جانب كيفية صدور هذا المشروع الذى يرتبط ارتباطا وثيقاً بمشروع قانون البناء الموحد رقم 119 لسنة 2008 .</p>\r\n\r\n<p>واقترح &quot;والى&quot; في بيان صحفي اليوم، أنه لابد من سرعة اعتماد الأحوزة العمرانية والمخططات الاستراتيجية للقرى والمدن والانتهاء منها على مستوى محافظات الجمهورية قبل صدور قانون التصالح فى مخالفات البناء، وذلك لتذليل كافة المعوقات أمام مشروع القانون وبذلك يكون قد تم تحديد جميع المخالفات التى سوف يتم التصالح فيها وفقاً لأخر مخطط عمرانى ووفقاً للقانون.</p>\r\n\r\n<p>وأضاف عضو مجلس النواب، أن اعتماد المخططات العمرانية للقرى والمدن بالمحافظات ييسر على المواطنين في إقامة المسكن المناسب، وتوطين الخدمات العامة ويواجه ظاهرة العشوائيات والتعدي على الأراضي الزراعية.</p>', 'storage/app/public/1535281536المهندس علاء.jpg', 1, '2018-08-26 13:05:36', '2018-08-26 10:53:35', '2018-08-26 13:05:36', 634, 634, 1, 1);
INSERT INTO `news` VALUES (321, 'الضرائب تتقدم باقتراح بالكشف عن الحسابات المصرفية للحد من التهرب الضريبى', '<p><span dir=\"rtl\">قال رئيس مصلحة الضرائب اليوم الأحد، إن المصلحة تقدمت بمقترح لتعديل القانون بما يسمح لوزير المالية بالكشف على الحسابات المصرفية للشركات والأفراد فى خطوة تهدف للحد من التهرب الضريبى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف عماد سامى فى اتصال هاتفى مع رويترز &quot;تقدمنا بمقترح لتعديل المادة رقم 99 من قانون الدخل بما يسمح للوزير بالكشف على الحسابات البنكية بما لا يتعارض مع قانون البنك المركزى&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال سامى إن الهدف هو &quot;الحد من التهرب الضريبى... لن نطلب الكشف عن حسابات كل الناس والشركات بل من يقدم بيانات غير واقعية، حينها فقط سنطابقها بحسابه البنكى&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">ويستلزم التعديل موافقة مجلس النواب ثم تصديق رئيس الجمهورية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال سامى إنه بموجب التعديل &quot;تلتزم جميع الجهات بالسماح لمأمور الضرائب بالإطلاع على الحسابات البنكية بعد موافقة وزير المالية&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال مسؤول فى وزارة المالية لرويترز مشترطا عدم الكشف عن اسمه إن خطوة كتلك تتطلب تعديل مادة فى قانون البنك المركزى للسماح لوزير المالية بالإطلاع على الحسابات البنكية للشركات والأفراد، لكن عماد سامى استبعد ذلك فى تصريحاته لرويترز.</span></p>\r\n\r\n<p><span dir=\"rtl\">ولم يتسن الاتصال بالبنك المركزى للحصول على تعقيب.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1535281227الضرائب.jpg', 1, '2018-08-26 13:00:27', '2018-08-26 13:00:27', '2018-08-26 13:00:27', 629, 629, 1, 1);
INSERT INTO `news` VALUES (322, 'الرئيس السيسي يوافق على اتفاق قرض لشراء 13 قاطرة للخط الثانى لمترو الأنفاق', '<p>اصدر الرئيس عبد الفتاح السيسى القرار رقم 99 لسنة 2018 بالموافقة على اتفاق قرض شراء 13 قاطرة لخط المترو الثانى بين الحكومة المصرية والبنك الأوروبى لإعادة الإعمار والموقع فى القاهرة بتاريخ 8/12/2015.</p>\r\n\r\n<p>وذلك بعد ان وافق عليه مجلس الوزراء</p>', 'storage/app/public/news/1535284584الرئيس.jpg', 1, '2018-08-26 13:56:24', '2018-08-26 13:56:24', '2018-08-26 13:56:24', 629, 629, 1, 1);
INSERT INTO `news` VALUES (323, '\"البنك المركزى\" لمصلحة الضرائب: سرية حسابات العملاء \"خط أحمر\".. طارق عامر يرفض مقترح تمكين وزير المالية من الاطلاع على بيانات الشركات والأفراد لمواجهة \"التهرب الضريبى\".. ويؤكد: على الجميع الالتزام باختصاصاته', '<p><span dir=\"rtl\">قال طارق عامر، محافظ البنك المركزى المصرى : إن ما قاله رئيس مصلحة الضرائب، بشأن التقدم بمقترح لتعديل القانون بما يسمح لوزير المالية الكشف على الحسابات المصرفية للشركات والأفراد فى خطوة تهدف للحد من التهرب الضريبى، لن يلقى قبولاً من البنك المركزى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضاف محافظ البنك المركزى فى تصريحاته، أن البنك لن يوافق على تعديل قانون البنوك فى هذا الشأن لتعارض ذلك مع سرية الحسابات المصرفية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وطالب محافظ البنك المركزى المصرى، رئيس مصلحة الضرائب بأن يتريث فيما يقول ويتكلم فى حدود اختصاصاته، مؤكداً فى الوقت نفسه على التزام البنك واستمراره فى حماية سرية حسابات العملاء كافة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وكان عماد سامى، رئيس مصلحة الضرائب قد قال اليوم الأحد، إن المصلحة تقدمت بمقترح لتعديل القانون بما يسمح لوزير المالية بالكشف على الحسابات المصرفية للشركات والأفراد فى خطوة تهدف للحد من التهرب الضريبى، مضيفًا فى تصريحات نشرتها وكالة رويترز: &quot;تقدمنا بمقترح لتعديل المادة رقم 99 من قانون الدخل بما يسمح للوزير بالكشف على الحسابات البنكية بما لا يتعارض مع قانون البنك المركزى&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقال سامى فى تصريحاته إنه بموجب التعديل المرغوب &quot;تلتزم جميع الجهات بالسماح لمأمور الضرائب بالإطلاع على الحسابات البنكية بعد موافقة وزير المالية&quot;.</span></p>\r\n\r\n<p><span dir=\"rtl\">وتتبنى وزارة المالية حزمة تشريعية جديدة للتسير على رجال الصناعة والإنتاج، وفى مقدمتها تسهيلات ضريبية.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفى اجتماع سابق بين الدكتور محمد معيط، وزير المالية مع جمعيات المستثمرين منتصف الشهر الجارى، أكد معيط تبنى الحكومة مشروع قانون بإعفاء ممولى الضرائب من مقابل تأخير سداد الضريبة المستحقة سواء ضريبة الدخل أو الدمغة أو رسم التنمية، وذلك بنسب تتراوح بين 90% كأعلى شريحة و50% كأدنى شريحة، وذلك فى إطار التيسير على القطاع الصناعى والاستثمارى، مشيرًا إلى أن مجلس النواب وافق مؤخرا على ذلك القانون الذى أعدته الوزارة المالية تخفيفا للأعباء على المجتمع الضريبى، وهو ما يتواكب مع مبادرة البنك المركزى بإسقاط فوائد قروض المشروعات المتعثرة خاصة الصغيرة والمتوسطة.</span></p>', 'storage/app/public/news/1535288806البنك المركزي.jpg', 1, '2018-08-26 15:06:46', '2018-08-26 15:06:46', '2018-08-26 15:06:46', 629, 629, 1, 1);
INSERT INTO `news` VALUES (324, 'الشروط المنظمة للادعاء المدنى أمام النيابة العامة', '<p><span dir=\"rtl\">هناك العديد من الواجبات التى تقع على المدعى بالحق المدنى وملزمة له أمام النيابة العامة، و أبرز تلك الواجبات، و&nbsp;الآثار المترتبة على التنازل عن الادعاء المدنى أمام النيابة فى مرحلة لاحقة.</span></p>\r\n\r\n<p><span dir=\"rtl\">الواجبات الملزمة للمدعى بالحق المدنى..</span></p>\r\n\r\n<p><span dir=\"rtl\">سداد الرسوم التى تستحق من هذا الإجراء.تحديد محلاً له فى البلدة التى يقع فيها مقر المحكمة التى يتم التحقيق من دائرتها.عدم تقديم أى طلبات تتعلق بالحبس الاحتياطى والإفراج المؤقت عن المتهم.</span></p>\r\n\r\n<p><span dir=\"rtl\">الآثار المترتبة على التنازل عن الإدعاء المدنى..</span></p>\r\n\r\n<p><span dir=\"rtl\">بالنسبة للدعوى الجنائية: ليس لهذا التنازل أى أثر على إجراءات الدعوى الجنائية التى رفع الإدعاء المدنى بشأنها.بالنسبة للدعوى المدنية:يترتب على هذا التنازل زوال كل الإجراءات التى اتخذت بشأنها، ولكن لا يحول هذا التنازل دون أن يحدد المضرور ادعاءه مرة أخرى سواء أمام المحكمة الجنائية فيما بعد، أوأمام المحكمة المدنية حسب الأحوال.</span></p>', 'storage/app/public/news/1535292495محكمة.jpg', 1, '2018-08-26 16:08:15', '2018-08-26 16:08:15', '2018-08-26 16:08:15', 629, 629, 1, 1);
INSERT INTO `news` VALUES (325, 'ملامح مشروع قانون \"الرى\" المرتقب إقراراه فى دور الانعقاد المقبل', '<p><span dir=\"rtl\">تُنهى لجنة الزراعة والرى بمجلس النواب، فى مستهل دور الانعقاد المقبل، مشروع قانون الموارد المائية والرى بمجلس النواب، وفيما يلى نستعرض أبرز ما جاء فى مشروع القانون.</span></p>\r\n\r\n<p><span dir=\"rtl\">1- مشروع القانون مقدم من الحكومة.</span></p>\r\n\r\n<p><span dir=\"rtl\">2- يتكون من 133 مادة، موزعة على عشرة أبواب:</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;الباب الأول&quot; الموارد المائية والأملاك العامة والأعمال الخاصة ذات الصلة بها.</span></p>\r\n\r\n<p><span dir=\"rtl\">&nbsp;&quot;الباب الثاني&quot; حقوق الانتفاع بمصادر وطرق الري والصرف الخصوصية.</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;الباب الثالث&quot; توزيع المياه.</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;الباب الرابع&quot; روابط مستخدمي المياه وتطوير وتحسين نظم الري والصرف.</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;الباب الخامس&quot; ري وصرف الأراضي الجديدة.</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;الباب السادس&quot; حماية مجرى نهر النيل وجسوره.</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;الباب السابع&quot; المياه الجوفية.</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;الباب الثامن&quot; حماية الموارد والمنشآت المائية والشواطئ.</span></p>\r\n\r\n<p><span dir=\"rtl\">&quot;الباب التاسع&quot; في الأحكام العامة والختامية.</span></p>\r\n\r\n<p><span dir=\"rtl\">&nbsp;&quot;الباب العاشر&quot; في العقوبات.</span></p>\r\n\r\n<p><span dir=\"rtl\">3- تعظيم الاستفادة من الموارد المائية المتاجة.</span></p>\r\n\r\n<p><span dir=\"rtl\">4- اتخاذ كافة الوسائل الكفيلة بتحقيق الأمن المائى.</span></p>\r\n\r\n<p><span dir=\"rtl\">5- حظر التعدي على حرم نه النيل وإزالة التعديات.</span></p>\r\n\r\n<p><span dir=\"rtl\">6- الاستفادة من مياه السيول والأمطار والمياه الجوفية.</span></p>\r\n\r\n<p><span dir=\"rtl\">7- حماية الشواطىء.</span></p>\r\n\r\n<p><span dir=\"rtl\">8- الحفاظ على موارد الدولة المائية واستثمارها.</span></p>\r\n\r\n<p><span dir=\"rtl\">9- تغليظ عقوبات التعدى على نهر النيل أو على الموارد المائية بتبديدها وذلك من خلال استخدامها بالمخالفة فى زراعات شرهة للمياه أو إهدارها، والتى تصل إلى عقوبة الحبس.</span></p>\r\n\r\n<p><span dir=\"rtl\">10- تطوير منظومة الرى على مستوى الجمهورية لتعظيم الاستفادة من الموارد المائية.</span></p>\r\n\r\n<p><span dir=\"rtl\">11- لجنة الزراعة انتهت من مناقشة 9 أبواب ويتبقى باب العقوبات.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1535356101مشروع الري.jpg', 1, '2018-08-27 09:48:21', '2018-08-27 09:48:21', '2018-08-27 09:48:21', 629, 629, 1, 1);
INSERT INTO `news` VALUES (326, 'العلاقة بين المالك والمستأجر فى مشروع قانون الإيجار القديم', '<p><span dir=\"rtl\">يستعد مجلس النواب لإجراء حوار مجتمعى حول قانون الإيجار القديم قبل مناقشته فى دور الانعقاد المقبل، بحسب تصريحات قيادات لجنة الإسكان بالبرلمان.</span></p>\r\n\r\n<p><span dir=\"rtl\">اليكم أبرز المعلومات عن مشروع القانون الذى يضم 6 مواد و يضبط العلاقة بين المالك والمستأجر .</span></p>\r\n\r\n<p><span dir=\"rtl\">1 - يقضى بأن يسرى عقد إيجار العين المؤجرة بعد وفاة المستأجر الأصلى الوارد اسمه بالعقد، ويبقى فيها زوجه أو أولاده أو أى من والديه الذين كانون يقيمون معه، وذلك لمدة خمس سنوات من تاريخ الوفاة.</span></p>\r\n\r\n<p><span dir=\"rtl\">2- يقضى بأن يسرى العقد على العين المؤجرة عند وفاة مستأجرها الوارد اسمه بالعقد قبل العمل بأحكام هذا القانون، ويبقى فى العين من لهم الحق فى البقاء فيها لمدة خمسة سنوات من تاريخ العمل بأحكامه.</span></p>\r\n\r\n<p><span dir=\"rtl\">3- إذا كانت العين مؤجرة لمزاولة نشاط تجارى أو صناعى أو مهنى يسرى العقد بعد موت المستأجر، ويستمر لصالح الذين يستعملون العين من ورثته أو شركائهم، أو أى من ورد أسمائهم بالفقرتين السابقتين فى ذات النشاط الذى كان يمارسه المستأجر الأصلى طبقا للعقد،&nbsp; وفى كل الأحوال تسرى عليهم المدد المبينة بالفقرتين السابقتين.</span></p>\r\n\r\n<p><span dir=\"rtl\">4- فى جميع الأحوال يلتزم المؤجر بتحرير عقد الإيجار خلال هذه المدة لمن لهم الحق فى الاستمرار فى شغل العين، ويلتزم هؤلاء الشاغلون بطريق التضامن بكافة أحكام العقد،&nbsp; بشرط ألا يكون لكل منهم وحدة سكنية بالملك أو الميراث أو الإيجار أو بأى وسيلة آخر ى داخل الوحدة المحلية الكائن فى دائرتها العين المؤجرة.</span></p>\r\n\r\n<p><span dir=\"rtl\">5- تطبق أحكام القانون المدنى على العلاقة الإيجارية بين المؤجر والمستأجر على الأماكن التى انتهت أو تنتهى عقود إيجارها دون أن يكون لأحد حق البقاء فيها.</span></p>\r\n\r\n<p><span dir=\"rtl\">6- تلتزم الحكومة بتوفير وحدة سكنية لكل مستأجر أو لشاغل العين المؤجرة، وانتهت عقد إيجارها وفقا لأحكام هذا القانون، دون أن يكون لأحد حق البقاء فيها إذا كان صافى الدخل لكل واحد منهم أقل من 1200 جنيه شهريا،&nbsp; قيمة الحد الأدنى للأجور.</span></p>', 'storage/app/public/news/1535361366قانون الايجار القديم.jpg', 1, '2018-08-27 11:16:06', '2018-08-27 11:16:06', '2018-08-27 11:16:06', 629, 629, 1, 1);
INSERT INTO `news` VALUES (327, 'الجريدة الرسمية تنشر تعديل مواد بلائحة قانون حماية النيل والمجارى من التلوث', '<p>نشرت الجريدة الرسمية، اليوم، تعديل بعض مواد اللائحة التنفيذية للقانون رقم 48 لسنة 1982 الصادر بالقرار الوزارى رقم 92 لسنة 2013، بشأن حماية نهر النيل والمجارى المائية من التلوث.</p>\r\n\r\n<p>وتضمنت التعديلات المادة رقم 51 من اللائحة فيما يخص المواد الصلبة الذائبة الكلية لتصبح 2000 مليجرام/ لتر بدلاً من 1000 مليجرام/ لتر، والمادة رقم 52 بإضافة &quot; فى حالة إلقاء مياه الصرف الصحى المالج والمخلفات الصناعية السائلة المعالجة على مسطحات المياه غير العذبة والتى تؤول إلى مسطحات المياه العذبة فيتم عمل دراسة لكل حالة على حدة بحيث تتضمن الدراسة المعايير والمواصفات الواجب توافرها فى المياه التى يتم صرفها على ألا تتجاوز المعايير المنصوص عليها بالمادة 51 من اللائحة والتى تضمن عدم التأثير على مسطحات المياه العذبة.</p>\r\n\r\n<p>ويستمر العمل بباقى بنود اللائحة التنفيذية، كما وردت بالقرار الوزارى رقم 92 لسنة 2013 بدون تغيير، أخذاً فى الاعتبار كافة التعديلات التالية على اللائحة.</p>', 'storage/app/public/news/1535364999وزير الري.jpg', 1, '2018-08-27 12:16:39', '2018-08-27 12:16:39', '2018-08-27 12:16:39', 629, 629, 1, 1);
INSERT INTO `news` VALUES (328, 'رئيس مجلس الدولة يمنع القضاة من إنشاء صفحات «فيس بوك»: المخالف يُحال للتفتيش', '<p>كشف المستشار أحمد أبوالعزم، رئيس مجلس الدولة، عن اتخاذه قراراً عقب توليه المنصب، بمنع قضاة المجلس من إنشاء صفحات على موقع التواصل الاجتماعي &laquo;فيس بوك&raquo;.</p>\r\n\r\n<p>وأضاف &laquo;أبوالعزم&raquo;&nbsp; أن من ينشر شيئاً على صفحات الفيس بوك من القضاة يتم إحالته للتفتيش للتحقيق معه.</p>\r\n\r\n<p>وأشار رئيس مجلس الدولة إلى أن يرفض إبداء القضاة أرائهم في قضايا الرأى العام حرصاً على عدم دخولهم في تجاذبات أو تجاوزات، منبهاً إلى أن مناقشة الأحكام في وسائل الإعلام &laquo;قد يؤدي إل الاستهانة بها&raquo;، وأن القانون حدداً طريقاً محدداً للاعتراض على الأحكام عن طريق الطعن عليها.</p>', 'storage/app/public/news/1535369517رئيس مجلس الدولة.jpg', 1, '2018-08-27 13:31:57', '2018-08-27 13:31:57', '2018-08-27 13:31:57', 629, 629, 1, 1);
INSERT INTO `news` VALUES (329, 'الضرائب تُحصِل 20 جنيها ضريبة للدعاوى الابتدائية و50 للاستئناف و100 للنقض', '<p><span dir=\"rtl\">أكدت مصلحة الضرائب المصرية التزامها بتطبيق جميع القوانين الضريبية بمنتهى العدالة والشفافية من أجل تحصيل حقوق الخزانة العامة من الإيرادات الضريبية من جميع المواطنين، بما فيها أعضاء المهن الحرة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وقالت فى بيان لها اليوم الاثنين، إن هذه الإيرادات الضريبية تمثل الجانب الأكبر من إيرادات الموازنة العامة، وتمكن الدولة من الإنفاق على جميع الخدمات الأساسية للمواطنين مثل التعليم والصحة والسكن الاجتماعى وبرامج الحماية الاجتماعية وغيرها من بنود الإنفاق العام المتعددة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشارت المصلحة فى بيانها إلى أن قرار وزير المالية رقم 381 لسنة 2018 بتعديل القرار الوزارى رقم 530 لسنة 2005 والخاص بتحديد قيمة المبالغ المحصلة تحت حساب ضريبة الدخل من السادة المحامين من أعضاء المهن الحرة، يستهدف التوافق مع تغيرات الأوضاع الاقتصادية، حيث إن القرار السابق صدر منذ 13 عامًا شهدت خلالها البلاد تغيرات اقتصادية كثيرة أصبحت معها القيم الواردة بالقرار جد هزيلة ولا تتماشى مع القيم الحقيقية لأسعار القطاعات الخدمية والنشاط الاقتصادى.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأكدت مصلحة الضرائب المصرية، أنها حرصت على إيضاح جميع الحقائق أمام الرأى العام ردًا على ما أثير فى بعض وسائل الإعلام من اعتراض نقابة المحامين على قرار تعديل قيم المبالغ المحصلة تحت حساب الضريبة.</span></p>\r\n\r\n<p><span dir=\"rtl\">كما أكدت أن قرار الوزير راعى اختلافات الدخل بين السادة المحامون حيث جاءت القيم الجديدة متدرجة، فمثلا تم رفع قيم المبالغ المحصلة من المحامين المقيدين بجداول المحاكم الابتدائية من 5 جنيهات فى عام 2005 إلى 20 جنيها حاليًا، وللمحامين المقيدين بجداول محاكم الاستئناف من 10 جنيهات فى عام 2005 إلى 50 جنيها حاليا.</span></p>\r\n\r\n<p><span dir=\"rtl\">وللمحامين المقيدين بجداول محكمة النقض وهى أعلى درجات التقاضى من 20 جنيهًا فى عام 2005 إلى 100 جنيه حاليًا، وأن هذه المبالغ يتم تسويتها فى نهاية السنة مع الضريبة المستحقة على الممول مع ردها أو رد جزء منها إذا كانت أكبر من قيمة ضريبة الدخل المستحقة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وذكر البيان أن هذه المبالغ لا تؤثر من قريب أو من بعيد على إجراءات التقاضى لأن هذه المبالغ لا تحمل علي أي من أطراف الدعوى القضائية وإنما يلتزم بسدادها المحامي شخصيًا وكجزء من ضريبة الدخل الملتزم بها كأحد أعضاء المهن الحرة وبدلًا من سدادها مرة واحدة مع تقديم إقراره الضريبي السنوي فإنه يسدد جزء منها كلما تولى قضية جديدة وتدخل فى رصيده لدى مصلحة الضرائب وتسوى فى نهاية العام مع تقديمه لإقراره الضريبى وترد له فى حال ما إذا كانت أكبر من قيمة الدخل المستحقة</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح البيان أن مراعاة القرار لتغير أسعار الخدمات التي يقدمها المحامون فى عام 2018 مقارنة بما كانت عليه أسعارها 2005 إنما يعكس فقط التغير في القيم المالية منذ عام 2005 وحتى 2018 وفي جميع الأحوال فهي ما زالت تمثل مبلغ تحت حساب الضريبة تخصم من ضريبة المهن الحرة المستحقة عليه في نهاية العام ولذلك فلا يجب أن ينظر إليها على أنها تمثل عبئًا إضافيًا عليه، فهي لا تمثل عبئًا باي حال من الاحوال على المحامي ولا معوقة للتقاضى ولا مخالفة لأحكام القانون، خاصة أن المُشرع ترك لوزير المالية سلطة تحديد قيم المبالغ التي تخصم تحت حساب الضريبة، دون أن يعين حدًا أقصى لها وكذلك لم يحظر أو يحدد عدد مرات تعديل القرار وهذا ضمانا لمراعاة ملائمة قيم هذه المبالغ مع حقيقة أسعار الخدمات التي يقدمها المحامون وغيرهم من أصحاب المهن الحرة وبما يتفق مع الهدف من تحصيل مبالغ تحت حساب الضريبة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح البيان أن المادة 73 من قانون ضرائب الدخل تنص على أنه لا تسري أحكام التحصيل تحت حساب الضريبة على الممول خلال فترة إعفائه أو عدم خضوعه للضريبة وبالتالي فإن القرار الوزاري رقم 381 لسنة 2018 وما قبله رقم 530 لسنة 2005 لا يسري على المحامين أثناء فترة الإعفاء الضريبي المقرر قانونًا مؤكدا أن القرار الوزاري الجديد نص في مادته الثالثة على هذا الإعفاء صراحة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأكد البيان أن نظام الخصم تحت حساب الضريبة يقدم آلية عمل مبسطة تضمن حقوق الطرفين المحامي والخزانة العامة حيث تلزم المادة رقم 71 من قانون الضريبة على الدخل رقم 91 لسنة 2005 أقلام كتاب المحاكم على اختلاف درجاتها عند تقديم صحف الدعاوي أو الطعون لقيدها وكذلك مأموريات الشهر العقاري عند التأشير على المحررات بالصلاحية للشهر، بتحصيل مبلغ تحت حساب الضريبة المستحقة على المحامي الموقع على الصحيفة أو المُحرر.</span></p>\r\n\r\n<p><span dir=\"rtl\">وحول ما أثير من تخوف النقابة العامة للمحامين من التقديرات الجزافية للضريبة أكد البيان التزام مصلحة الضرائب المصرية وفقًا لقانون الضريبة على الدخل على أصحاب المهن الحرة بتحديد قيمة الضريبة على الممولين وفقًا للإقرارات المقدمة منهم خاصة إذا كانت مؤيدة بمستندات ومنع القانون مصلحة الضرائب من أن تلجأ إلى تقدير الضريبة جزافيًا وتجاهل الاقرار الضريبي للممول إلا بناءً على أدلة تثبت خطأ الإقرار، كما كفل القانون لكل ممول الحق في الاعتراض والطعن على أي تقدير للضريبة يراه مخالفًا لحقيقة دخله.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح البيان أن المُشرع يستهدف من نظام التحصيل تحت حساب الضريبة، تخفيف عبء الضريبة النهائي المستحق على الممولين حيث سمح القانون بتحصيل جزء من الضريبة تحت الحساب خلال العام بدلًا من تحملها كاملة عند تقديم الإقرار، وهذا النظام متعارف عليه في معظم قوانين الضرائب في دول العالم، وإلى جانب ذلك فانه يوفر إيرادات للدولة علي مدار العام تساعدها في القيام بأعبائها المالية وتقليل حجم الاقتراض والدين العام وهو أمر لاشك في صالح جميع المواطنين وهو أيضًا ما ينادي به المجتمع على مدار السنوات والفترات السابقة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأكد البيان أن وزارة المالية تنظر إلى نقابة المحامين بأنها أحد أعمدة الدولة وكيان له قدره ومكانته في حماية الحقوق والدفاع عن المواطنين وعن ممتلكات الدولة، لأنها صرح من صروح القانون وأحد أعمدة إعلاء كلمته ولذا فإن وزارة المالية ومصلحة الضرائب المصرية على يقين من أن هذا الصرح لن يتوانى عن أداء حق الدولة تطبيقًا للقانون وإعلاء لدولة المؤسسات كما هو شأن جميع الفئات المستنيرة في المجتمع.</span></p>', 'storage/app/public/news/1535376720الضراب.jpg', 1, '2018-08-27 15:32:00', '2018-08-27 15:32:00', '2018-08-27 15:32:00', 629, 629, 1, 1);
INSERT INTO `news` VALUES (330, 'اليوم نظر طعون الغاء قرار إصدار اللائحة التنفيذية لقانون المنظمات النقابية', '<p>تستانف&nbsp;الدائرة&nbsp;الثانية&nbsp;بمجلس&nbsp;الدولة&nbsp;برئاسة&nbsp;المستشار&nbsp;سامى&nbsp;عبد&nbsp;الحميد&nbsp;، اليوم الثلاثاء نظر الطعون المقامة من&nbsp;عدد&nbsp;من&nbsp;القيادات&nbsp;النقابية&nbsp;المستقلة،&nbsp;حملت&nbsp;رقم&nbsp;41025&nbsp;لسنة&nbsp;72&nbsp;قضائية&nbsp;،طالب&nbsp;فيها&nbsp;المدعون&nbsp;بإلغاء&nbsp;قرار&nbsp;وزير&nbsp;القوى&nbsp;العاملة&nbsp;المطعون&nbsp;ضده&nbsp;رقم&nbsp;35&nbsp;لسنة&nbsp;2018&nbsp;&nbsp;بإصدار&nbsp;اللائحة&nbsp;التنفيذية&nbsp;لقانون المنظمات&nbsp;النقابية&nbsp;والعمالية&nbsp;رقم&nbsp;213&nbsp;لسنة&nbsp;2017،&nbsp;وبصفة&nbsp;مستعجلة&nbsp;وقف&nbsp;القرار&nbsp;المطعون&nbsp;عليه&nbsp;.</p>\r\n\r\n<p>يذكر&nbsp;أن&nbsp;المدعين&nbsp;قد&nbsp;وكلوا&nbsp;محامي&nbsp;دار&nbsp;الخدمات&nbsp;النقابية&nbsp;والعمالية&nbsp;للسير&nbsp;فى&nbsp;إجراءات&nbsp;الدعوى،&nbsp;وترأس&nbsp;فريق&nbsp;المحامين&nbsp;الدكتور&nbsp;أحمد&nbsp;حسن&nbsp;البرعى&nbsp;وزير&nbsp;القوى</p>\r\n\r\n<p>&nbsp;العاملة&nbsp;الأسبق&nbsp;وأستاذ&nbsp;التشريعات&nbsp;الاجتماعية&nbsp;بجامعة&nbsp;القاهرة.</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1535441424مجلس الدولة.jpg', 1, '2018-08-28 09:30:24', '2018-08-28 09:30:24', '2018-08-28 09:30:24', 629, 629, 1, 1);
INSERT INTO `news` VALUES (331, 'الجريدة الرسمية تنشر قرار تخويل عاملين بالإدارة العليا لتوزيع الكهرباء صفة مأمور الضبط القضائى', '<p>نشرت الجريدة الرسمية، قرار المستشار حسام عبد الرحيم وزير العدل، بتخويل بعض العاملين بالإدارة العليا بشركات توزيع الكهرباء التابعة للشركة القابضة لكهرباء مصر صفة مأمور الضبط القضائي.</p>\r\n\r\n<p>كما قرر الوزير الغاء القرار رقم 8124 لسنة 2016 فيما تضمنه من منح صفة مأمور الضبط القضائي لعاملين شاغلي الادارة العليا بشركات توزيع الكهرباء التابعة للشركة القابضة للكهرباء</p>', 'storage/app/public/news/1535448175وزير العدل.jpg', 1, '2018-08-28 11:22:55', '2018-08-28 11:22:55', '2018-08-28 11:22:55', 629, 629, 1, 1);
INSERT INTO `news` VALUES (332, 'المخالفات والعقوبات الواردة بشأن الصيادين فى القانون الجديد', '<p><span dir=\"rtl\">تستعد لجنة الزراعة والرى بمجلس النواب، لتعديل قانون صيد الأسماك والأحياء المائية وتنظيم المزراع السمكية والصادر بالقانون رقم 124 لسنة 1983، بحسب ما أكده النائب هشام الشعينى رئيس اللجنة.</span></p>\r\n\r\n<p><span dir=\"rtl\">أبرز التعديلات التى ستضبط العلاقة بين الصيادين والحكومة الفترة المقبلة.</span></p>\r\n\r\n<p><span dir=\"rtl\">&nbsp;على النحو التالى:</span></p>\r\n\r\n<p><span dir=\"rtl\">1- حظر إنشاء جزر أو جسور أو سدود بالبحيرات إلا لتنفيذ أحد المشروعات القومية التى تحددها الدولة وبموافقة مجلس الوزراء.</span></p>\r\n\r\n<p><span dir=\"rtl\">2- حظر على أى جهة أو هيئة أو وحدة محلية تجفيف أى مساحة من البحيرات إلا لتنفيذ أحد المشروعات القومية.</span></p>\r\n\r\n<p><span dir=\"rtl\">3- المخالفات فى القانون تضمنت كل من يلقى أجسام صلبة فى مناطق الصيد، استخدام أو إدخال أسماك أجنبية أو بويضات لأى غرض إلا بتصريح من الهيئة، زراعة البوص فى مناطق الصيد.</span></p>\r\n\r\n<p><span dir=\"rtl\">4- العقوبات بالحبس مدة لا تقل عن ستة أشهر ولا تزيد عن سنة وبغر</span>امة لا تقل عن مائة ألف جنيه ولا تزيد عن خمسمائة ألف جنيه وفى حالة العودة تضاعف العقوبة.</p>', 'storage/app/public/news/1535456354قانون الصيد.jpg', 1, '2018-08-28 13:39:14', '2018-08-28 13:39:14', '2018-08-28 13:39:14', 629, 629, 1, 1);
INSERT INTO `news` VALUES (333, 'الحكومة تنفى فرض ضرائب على ودائع المصريين فى البنوك أو الفوائد', '<p><span dir=\"rtl\">كشف المركز الإعلامي لمجلس الوزراء أنه في ضوء ما تردد من أنباء تُفيد بفرض الحكومة ضرائب على ودائع المصريين في البنوك، أو الفوائد المتحققة عبر الأوعية وشهادات الادخار في الجهاز المصرفي، تواصل المركز&nbsp; مع وزارة المالية، والتي نفت صحة تلك الأنباء تماماً، مُشددة على عدم وجود نية على الإطلاق أو دراسة لفرض ضرائب على ودائع المصريين في البنوك، أو الفوائد المتحققة عبر الأوعية وشهادات الادخار في الجهاز المصرفي.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضحت الوزارة، أن البنك المركزي المصري يضمن ودائع البنوك، والتي تبلغ أكثر من 3,5 تريليون جنيهاً، مؤكدةً أن هذا الموضوع لم يتم التطرق له على الإطلاق بين وزارة المالية والبنك المركزي المصري في أي وقت, كما شددت الوزارة على أنه لا نية لفرض أي ضرائب جديدة خلال العام المالي الحالي 2018 &ndash; 2019، مؤكدةً أن مستهدف زيادة الإيرادات الضريبية سيتم عن طريق تفعيل أدوات مكافحة التهرب الضريبي، وتوسيع القاعدة الضريبية ومكافحة التهرب الضريبي ورفع كفاءة التحصيل، وليس عن طريق فرض ضرائب جديدة كما يتردد.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأضافت الوزارة، أن ودائع المصريين بالبنوك &ndash; بما فيها الودائع الحكومية &ndash; ارتفاعًا بنحو 11 مليار جنيه، حيث سجلت 3.536 تريليون جنيه، في نهاية شهر مايو 2018، مقارنة بـ 3.525 تريليون جنيه &ndash; التريليون يساوى 1000 مليار &ndash; وذلك بنهاية شهر أبريل 2018.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1535462200مركز مجلس المعلومات.jpg', 1, '2018-08-28 15:16:40', '2018-08-28 15:16:40', '2018-08-28 15:16:40', 629, 629, 1, 1);
INSERT INTO `news` VALUES (334, 'رئيس الوزراء يفوض وزير الصناعة فى مباشرة اختصاصاته بقانون حماية المنافسة', '<p>أصدر الدكتور مصطفي مدبولي ، رئيس الوزراء قرارا بتفويض وزير التجارة والصناعة في مباشرة اختصاصات رئيس الحكومة المنصوص عليها في قانون حماية المنافسة ومنع الممارسات الإحتكارية&nbsp;</p>', 'storage/app/public/news/1535466643رئيس.jpg', 1, '2018-08-28 16:30:43', '2018-08-28 16:30:43', '2018-08-28 16:30:43', 629, 629, 1, 1);
INSERT INTO `news` VALUES (335, 'مطالب برلمانية بتعديل تشريعى لتطبيق حكم الإعدام على خاطفى الأطفال', '<p>أعلن النائب بسام فليفل، تأييده لتغليظ عقوبة خطف الأطفال لتصبح الإعدام، وذلك من خلال تعديل قانون العقوبات للمواد المتعلقة بخطف الأطفال، قائلا: أوافق على تغليظ العقوبة وفقا للتشريع الذى تقدم به النائب محمد أبو حامد، الذى يقضى بهذا الصدد.</p>\r\n\r\n<p>وأوضح فليفل&nbsp; أن هذه الظاهرة أصبحت تتزايد فى المجتمع فى الفترة الأخيرة بشكل مخيف، ولابد من التصدى لها لمنع تكرارها للحفاظ على الأمن العام، وهذا لن يكون سوى بتطبيق عقوبة مغلظة ورادعة على ان تطبق فى ميدان عام امام الرأى العام حتى يكون الجانى عبرة لكل من تسول له نفسه الإقبال على هذه الجريمة، مطالبا وسائل الإعلام بتسليط الضوء على هذه القضية.</p>\r\n\r\n<p>وشدد عضو مجلس النواب، على تطبيق عقوبة الإعدام او السجن المشدد لهذه الجريمة، وذلك وفقا لمجريات كل جريمة على حدى، مؤكدا على ان القانون هو الرادع الوحيد، ولابد من خروج التعديلات الجديدة للنور، مؤكدا حرص مجلس النواب على خروج تشريعات تصب فى المصلحة العامة للدولة.</p>', 'storage/app/public/news/1535527489برلمان.jpg', 1, '2018-08-29 09:24:49', '2018-08-29 09:24:49', '2018-08-29 09:24:49', 629, 629, 1, 1);
INSERT INTO `news` VALUES (336, 'فرض عقوبة علي من يمتلك  أجهزة اللاسلكى وطائرات التجسس دون ترخيص', '<p>قال المستشار ميشيل حليم المحامى بالنقض،إن حيازة أجهزة الاسلكى وطائرات التجسس ممنوعة طبقًا لقرار نائب الحاكم العسكري رقم 3 لسنة 1998.</p>\r\n\r\n<p>وأوضح ميشيل&nbsp; القرار يحظر الإعلان عن هذه الأجهزة، أو استيرادها أو تصنيعها أو حيازتها، ويعد من تلك الأجهزة كل آلة أو جسم أيا كان شكله يمكن عن طريقة إلتقاط أو نقل أو تسجيل المحادثات أو الإشارات التي تتم في مكان خاص أو عام أو مجلس أو اجتماع أو تجرى عبر أي هاتف عن طريق جهاز من أجهزة الاتصال الأخرى سلكية كانت أو لاسلكية.</p>\r\n\r\n<p>وأردف ميشيل أن القرار تضمن عقوبة الحبس لمدة عام، وتشدد العقوبة عند استخدام هذه الأجهزة في أغراض إرهابية يعتبر هذا القانون هو ما يتم التعامل به مع أي راكب جالب لمثل هذه الأجهزة من الخارج.</p>\r\n\r\n<p>وأضاف ميشيل أن الجهاز القومي لتنظيم الاتصالات، يعمل بالتعاون مع مصلحة الجمارك على عدم دخول تلك الأجهزة إلى مصر، بشكل مستمر، بخلاف أنها مخالفة للقانون إلا أنها تمس الأمن القومي المصري وتهدده، ورغم انتشار أجهزة التنصت، إلا أن الجهاز القومي لتنظيم الاتصالات دائما ما يؤكد لأجهزة الجمارك في الموانئ المختلفة على تشديد وإحكام عمليات الرقابة لكثرة الحيل والخدع التي يستخدمها بعض المهربين لدخول هذه الأجهزة إلى البلاد.</p>\r\n\r\n<p>ومع مراعاة حكم الفقرة الأخيرة من المادة (48) من هذا القانون، يعاقب بالحبس مدة لا تقل عن سنة وبغرامة لا تقل عن عشرين ألف جنيه ولا تجاوز خمسين ألف جنيه أو بإحدى هاتين العقوبتين كل من قام دون الحصول على تصريح بذلك من الجهات المختصة بأحد الأفعال الآتية :</p>\r\n\r\n<p>1- استيراد أو تصنيع جهاز من أجهزة الاتصالات بغرض تسويقها في الداخل.</p>\r\n\r\n<p>2- حيازة أو تركيب أو تشغيل أية أجهزة اتصالات لاسلكية، ولا تسرى هذه العقوبة في حالة الأجهزة اللاسلكية التي يصدر الجهاز ترخيصاً عاماً باستخدامها أو تركيبها أو تشغيلها وبما لا يخل بأحكام المادة (44) من هذا القانون .</p>\r\n\r\n<p>وتضاعف العقوبة في حديها الأدنى والأقصى في حالة العودة.</p>\r\n\r\n<p>وتكون العقوبة السجن إذا كان الاستيراد أو التصنيع أو الحيازة بغير تصريح بغرض المساس بالأمن القومي.</p>\r\n\r\n<p>وتحكم المحكمة في جميع الأحوال بمصادرة المعدات والأجهزة محل الجريمة ومكوناتها.</p>\r\n\r\n<p>ومن جانب آخر كان قد وافق مجلس الوزراء على مشروع قانون بشأن تنظيم استخدام الطائرات المحركة لاسلكياً، وتداولها والاتجار فيها.</p>\r\n\r\n<p>ويحظر القانون رقم 28 لسنة 1981، على وحدات الجهاز الإداري للدولة، من وزارات ومصالح وأجهزة ووحدات الإدارة المحلية والهيئات العامة والشركات، وغيرها من الأشخاص الاعتبارية العامة أو الخاصة والأشخاص الطبيعيين، استيراد أو تصنيع أو تجميع أو تداول أو حيازة أو الاتجار أو استخدام الطائرات المحركة لاسلكياً، إلا بعد الحصول على تصريح بذلك من الجهة المختصة، وذلك بحسب ما نشرته بوابة الأهرام المصرية.</p>\r\n\r\n<p>وينص مشروع القانون الجديد على أن يعاقب كل من يخالف ما سبق ذكره بالحبس لمدة لا تقل عن سنة، ولا تتجاوز 7 سنوات، وبغرامة لا تقل عن 5 آلاف جنيه، ولا تتجاوز 50 ألف جنيه، أو بإحدى هاتين العقوبتين، وتضاعف العقوبة في حالة العودة، وفي جميع الأحوال تحكم المحكمة بمصادرة الآلات والأدوات المستخدمة في الجريمة.</p>\r\n\r\n<p>فيما تمكنت الإدارة العامة لتأمين محور قناة السويس، بقيادة اللواء دكتور أشرف عبد القادر مساعد وزير الداخلية و مدير الإداره العامة لتأمين محور القناة من ضبط حاوية بميناء بورسعيد بها&nbsp; 2 طائرة تجسس بكاميرا تعمل بالتحكم عن بعد، ومزودة بوحدة شاشة وسماعات ومايك، وعدد&nbsp; 10جهاز&nbsp;GPS&nbsp;، جهاز محطه لاسكى بمشتملاته، و 113 جهاز لاسلكي يدوى، وعدد كبير من قطع الغيار الخاصة بهم، عبارة عن 100 اريال صغير جهاز لاسلكى، و200 مايك جهاز لاسلكى، و200 سماعة جهاز لاسلكى، و 50 إريال بالقاعدة، و48 إريال، وتم تحرير محضر بالواقعة، وجارى العرض على النيابة العامة.</p>', 'storage/app/public/news/1535533313اجهزة لاسلكي.jpg', 1, '2018-08-29 11:08:19', '2018-08-29 11:01:53', '2018-08-29 11:08:19', 629, 629, 1, 1);
INSERT INTO `news` VALUES (337, 'أبرز 10 قوانين ترى النور فى دور الانعقاد الرابع للبرلمان.. الإجراءات الجنائية.. المحليات.. التصالح فى مخالفات البناء.. المرور.. تنظيم الفتوى.. قانون الرى.. تغليظ العقوبة لسارقى الدعم.. وتعديلات البناء الموحد', '<p>يشهد دور الانعقاد الرابع لمجلس النواب، والذى يبدأ فى مستهل شهر أكتوبر المقبل، إقرار عدد من مشروعات القوانين أبرزها مشروع قانون الإدارة المحلية، وفقا لتصريحات أعضاء لجنة الإدارة المحلية، بالإضافة لقانون التصالح فى مخالفات البناء، وتعديل قانون البناء الموحد 119 لسنة 2008، قانون الموارد المائية والرى، المرور، وعلى رأس هذه القوانين قانون الإجراءات الجنائية.</p>\r\n\r\n<p>ويعد مشروع قانون الإجراءات الجنائية، من أبرز هذه المشروعات المتوقع إقراراها فى مستهل دور الانعقاد الرابع.</p>\r\n\r\n<p>وتضمن مشروع القانون تغيرات جوهرية من شأنها الإسراع من وتيرة إنجاز القضايا الجنائية على نحو لا يخل بقواعد المحاكمات المنصفة، مع الأخذ بالوسائل البديلة كالصلح لتسوية المنازعات الجنائية ذات الطابع المالى، بجانب استحداث طريقة جديدة للإعلان وربطه بالرقم القومى، ونظام جديد لحماية الشهود والمبلغين، بجانب تنظيم إجراءات الحبس الاحتياطى وبدائله ومدده والتعويضات حال التضرر فى حالات حددها القانون.</p>\r\n\r\n<p>كما يعد مشروع قانون الإدارة المحلية من أهم القوانين المرتقب إقراراها خلال دور الانعقاد المقبل، حيث متوقع إجراء انتخابات المحليات قبل نهاية 2018.</p>\r\n\r\n<p>وتضمن مشروع القانون عدد من المزايا أبرزها، دعم اللامركزية الإدارية والمالية والاقتصادية، حيث يقر تطبيق اللامركزية المالية والإدارية والاقتصادية، وذلك من خلال إعطاء قيادات المحليات الحرية فى التصرف بجانب المسئولية الملقاة عليهم جراء الرقابة الشعبية من قبل المجالس المحلية المختلفة.</p>\r\n\r\n<p>كما منح مشروع القانون المحافظين صلاحيات كاملة ومحددة تمكنهم من أداء عملهم على الوجه الأمثل، بالإضافة لمنح للوحدات المحلية موازنة مستقلة، وإنشاء أكاديمية جديدة لتدريب قيادات المحليات، كما خصص 25% من المقاعد للشباب 25% للمرأة، بالإضافة إلى تخصيص مقاعد لذوى الإعاقة.</p>\r\n\r\n<p>كما يعد مشروع قانون التصالح فى مخالفات البناء، من القوانين الهامة ايضا، حيث انتهت اللجنة من مناقشته ومن المتوقع رفع تقرير نهائى لهيئة مكتب المجلس تهميدا لعرضه على الجلسة العامة.</p>\r\n\r\n<p>وتضمن مشروع القانون العديد من المميزات أبرزها انه سيحل مشاكل أكثر من نصف الثروة العقارية فى مصر والتى تعد مخالفة، وفقا للإحصائيات الأخيرة، وذلك من خلال معايير وشروط للتصالح اهمها السلامة الإنشائية وحدود الارتفاع وخطوط التنظيم، على أن يتم تطبيق القانون لمدة زمنية محددة حتى لا يكون ذريعة للمخالفة فيما بعد، وفيما يخص المادة المتعلقة بتحديد قيمة المخالفة اتفق أعضاء اللجنة على أن تكون القيمة متغيرة وفقا لكل منطقة على حدى حتى لا يكون هناك ظلم لأحد، وذلك وفقا للقيمة البيعية والسوقية للوحدة أو العقار المخالف، وفيما يخص المخالفات بعد صدور القانون سيتم إزالتها فورا.</p>\r\n\r\n<p>وتحظى تعديلات قانون البناء الموحد 119 لسنة 2008، بأهمية كبرى، فبالتزامن مع صدور قانون التصالح فى مخالفات البناء الذى لم ينص صراحة على آليات البناء لابد من وضع أطر جديدة وآليات لاستخراج تراخيص البناء، وهذا ما ستنظمه التعديلات الجديدة.</p>\r\n\r\n<p>حيث تضمن مشروع القانون تفعيل دور اتحاد الشاغلين، على أن تكون شهادة صلاحية الموقع للبناء يتم تحديد مدتها لأسبوعين، إصدار تراخيص البناء لمدة 3 سنوات بدلًا من سنة واحدة، منع تغيير نشاط الوحدة السكنية وتغليظ العقوبات فى حال مخالفة ذلك، وضع الضوابط اللازمة لضمان السلامة الإنشائية للمبانى والمنشآت، بالإضافة لمنح وزارة الزراعة سلطة تنفيذية بإزالة المبانى خارج الحيز العمرانى.</p>\r\n\r\n<p>مشروع قانون &quot;المرور&quot; الجديد من القوانين الهامة المتوقع إقراراه خلال دور الانعقاد المقبل، ويتضمن مشروع القانون 96 مادة مقسمة إلى 5 أبواب.</p>\r\n\r\n<p>وتضمن مشروع القانون الجديد التدرج فى تغليظ العقوبات، بما يتناسب مع كل مخالفة، وإسناد اختصاص كل عنصر من عناصر المنظومة المرورية لجهة الاختصاص، مع إيجاد قنوات تواصل مباشر مع باقى الجهات لتحقيق التكامل فى الأداء، بالإضافة للحد قدر الإمكان من تدخل العنصر البشرى فى الإجراءات الإدارية للتراخيص وضبط المخالفات، والاعتماد على المنظومة الإلكترونية.</p>\r\n\r\n<p>مشروع قانون تنظيم دار الإفتاء المقدم من النائب أسامة العبد، وضع مشروع القانون شكل الهيكل التنظيمى والوظيفى للعاملين بدار الإفتاء وترتيب الوظائف، كما حدد عددًا من المهام التى تسير عليها خلال الفترة المقبلة، كما وضع أيضًا ضوابط على من يصلح لأن يكون مفتيًا أو أمينًا للفتوى والفرق الدقيق بين الفتوى والرأى أو فقه المسائل ومعرفة مصادر الإفتاء وضوابطه.</p>\r\n\r\n<p>ومن مشروعات القوانين المتوقع إقراراها أيضا، قانون الموارد المائية والرى، الذى انتهت لجنة الزراعة والرى بمجلس النواب، من مناقشته ولم يتبق سوى عدد قليل من المواد، وأكد أعضاء اللجنة جميعهم على ضرورة خروج القانون للنور فى مستهل دور الانعقاد المقبل، وذلك من أجل حسن استغلال الموارد المائية المتاحة من خلال آليات جديدة تعتمد على ترشيد الاستهلاك وتغليظ عقوبة التعدى على الموارد بما فيها نهر النيل، والحفاظ على المياه الجوفية</p>\r\n\r\n<p>ومن مشروعات القوانين الهامة المنتظر مناقشتها أمام الجلسة العامة بمجلس النواب، قانون الحكومة بتعديل بعض أحكام المرسوم بقانون رقم 95 لسنة 1945 الخاص بشئون التموين وبعض أحكام قانون حماية المنافسة ومنع الممارسات الاحتكارية الصادر بالقانون رقم 3 لسنة 2005، وتنص التعديلات الجديدة على مواجهة الجرائم التى ترتكب فى احتكار السلع التموينية والمواد البترولية أو تسهيلها، لتصل إلى الحبس مدة لا تقل عن سنة ولا تزيد على 5 سنوات وبغرامة لا تقل عن 100 ألف جنيه ولا تزيد على مليون جنيه.</p>\r\n\r\n<p>مقترح بقانون بشأن تغليظ العقوبة على سارقى الدعم المقدم من النائب سليمان فضل العميرى، والذى وافقت عليه لجنة الاقتراحات والشكاوى فى دور الانعقاد الثالث، ومن المتوقع إقراراه فى مستهل دور الانعقاد المقبل.</p>\r\n\r\n<p>التسجيل العقارى، على الرغم من عدم إرسال مشروع القانون لمجلس النواب، إلا أن الدكتور على عبد العال، رئيس البرلمان طالب الحكومة بسرعة إعداد تشريع بشأن التسجيل العقارى وإرساله للبرلمان لمناقشته تمهيدا لإقراره.</p>', 'storage/app/public/news/1535541351البرلمان.jpg', 1, '2018-08-29 13:15:51', '2018-08-29 13:15:51', '2018-08-29 13:15:51', 629, 629, 1, 1);
INSERT INTO `news` VALUES (338, 'وزير المالية يكشف عن تغييرات تشريعات مرتقبة بقانون الضرائب العقارية', '<p><span dir=\"rtl\">قال الدكتور محمد معيط، وزير المالية، إن الضرائب العقارية ستشهد خلال الفترة القادمة نوعا من التغييرات سواء فى التشريعات أو البنية التحتية، موضحا أنه سيتم توقيع بروتوكول تعاون الأسبوع المقبل مع وزارة البترول فيما يتعلق بالضريبة العقارية لمنشآت قطاع البترول.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأوضح فى مؤتمر صحفى بمقر مجلس الوزراء، أن الحكومة وافقت على مشروع قانون سيتم تقديمه لمجلس النواب يتعلق بتأجيل إعادة التقدير بقانون الضرائب العقارية، مؤكدا حرص الحكومة على التخفيف على القطاعات الإنتاجية والخدمية فى الدولة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وأشار وزير المالية إلى أنه سيتم حل جميع المشاكل المتعلقة بالضرائب العقارية خلال الفترة القادمة.</span></p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1535549013وزير الماليه.jpg', 1, '2018-08-29 15:23:33', '2018-08-29 15:23:33', '2018-08-29 15:23:33', 629, 629, 1, 1);
INSERT INTO `news` VALUES (339, 'أبرز 7 قرارات للحكومة باجتماعها اليوم', '<p>وافق مجلس الوزراء على مشروع قرار رئيس مجلس الوزراء بتشكيل اللجنة العليا الدائمة لحقوق الانسان، وتتولى اللجنة إدارة آلية التعامل والرد على الادعاءات المثارة ضد مصر بشأن حقوق الإنسان، وتختص بمتابعة تنفيذ مصر لإلتزاماتها الدولية المتعلقة بحقوق الإنسان، وإعداد التقارير الدورية الخاصة بهذا الشأن، فضلاً عن رصد ودراسة ومعالجة المشكلات المتعلقة بهذا الملف والتي تثار على الصعيد الدولي، واقتراح السياسات والتدابير اللازمة لتنفيذ توصيات آلية المراجعة الدورية أمام مجلس حقوق الإنسان بالأمم المتحدة.</p>\r\n\r\n<p>ويأتي ذلك تنفيذاً لتوجيهات السيد رئيس الجمهورية باتخاذ إجراءات تنفيذية جوهرية على الصعيد المؤسسي والتنفيذي فيما يخص تعزيز وحماية حقوق الإنسان والحريات الأساسية على المستوى الوطني، وفقاً للاستحقاقات الدستورية ذات الصلة، وتوحيد اللجان الوطنية المعنية بمتابعة ملف حقوق الإنسان في لجنة واحدة.</p>\r\n\r\n<p>وتم التصويت بشأن الوزارة التى ستتولى رئاسة اللجنة، للاختيار بين وزارتى الخارجية، وشئون مجلس النواب، وحصلت وزارة الخارجية على أعلى الاصوات.</p>\r\n\r\n<p>ووافق مجلس الوزراء على التعاقد بالأمر المباشر مع المصانع أو الشركات التي انتهت من استيفاء الاشتراطات الخاصة بالهيئة القومية لسلامة الغذاء للعمل ضمن منظومة التغذية المدرسية، ويأتي ذلك من منطلق الحرص على الوصول إلى منظومة توفر وجبات غذائية متكاملة لنحو 11.5 مليون طالب في إطار من الحفاظ على سلامة الغذاء.&nbsp;</p>\r\n\r\n<p>ووافق مجلس الوزراء على قيام الهيئة القومية لسكك حديد مصر بالتعاقد بالأمر المباشر مع شركة ترانس ماش هولدنج المجر&nbsp;(TMHH)&nbsp;التحالف المجري/ الروسي، على شراء 1300 عربة ركاب طرازات مختلفة للقطارات، والتفاوض مع الشركة لعمل عقد صيانة للعربات لمدة 15 عاماً شاملة قطع الغيار المطلوبة، على أن تقوم الشركة بإنشاء ورشة جديدة مزودة بجميع الأدوات والمعدات المطلوبة لصيانة وعمرة العربات.</p>\r\n\r\n<p>ويأتي هذا القرار ضمن خطة الدولة لإحلال وتجديد أسطول الهيئة المتقادم، بهدف الوصول إلى شبكة سكك حديدية متطورة تقدم خدمة أفضل وآمنة للمواطنين وتحقق أعلى درجات التنافسية.</p>\r\n\r\n<p>ووافق مجلس الوزراء على طلب محافظة كفر الشيخ الموافقة على التصرف بالبيع بالاتفاق المباشر بمقابل الانتفاع لمدة 30 سنة في مساحة 150 فداناً بالمنطقة الصناعية بمطوبس بالمحافظة، للشركة القابضة للبتروكيماويات، لتنفيذ مشروع إنتاج المواد اللاصقة والألواح الخشبية متوسط الكثافة عالية الجودة&nbsp;(MDF).</p>\r\n\r\n<p>ويهدف هذا المشروع إلى تنشيط صناعة الأثاث والموبيليا، وسد حاجة السوق المحلية من خلال إحلال جزء من واردات السوق من هذه الألواح التي تصل إلى حوالي 685 ألف م2 وتوفير النقد الأجنبي، إلى جانب معالجة مشكلة البطالة بالمحافظة، حيث يتيح المشروع 500 فرصة عمل مباشرة وغير مباشرة، ذلك فضلاً عن إعادة تدوير كميات كبيرة من قش الأرز الذي تعتمد عليه هذه الصناعة وتجنب الآثار البيئية المترتبة على حرقه.</p>\r\n\r\n<p>كما وافق مجلس الوزراء على طلب الشركة القابضة لكهرباء مصر الموافقة على السير في إجراءات توقيع عدد من العقود والاتفاقيات مع الجانب الصيني على هامش الزيارة الرئاسية المرتقبة في سبتمبر 2018.</p>\r\n\r\n<p>ووافق مجلس الوزراء على زيادة الحد الأدنى السنوي المضمون لصالح المجلس الأعلى للآثار من 10 ملايين جنيه مصري إلى 20 مليون جنيه، مع الإلتزام بباقي بنود العرض المقدم من شركة أوراسكوم للاتصالات والإعلام والتكنولوجيا القابضة للحصول على حق تشغيل خدمات الزوار في منطقة الزيارة بهضبة أهرامات الجيزة لمدة 20 عاماً.</p>\r\n\r\n<p>ووافق مجلس الوزراء على طلب التعاقد بالاتفاق المباشر مع شركة بى إم سى إنترناشيونال اليابانية على توريد عدد (300 جهاز للرقابة على بيانات المبيعات + 2 نظام السيرفر + حزمة برامج السيرفر المربوطة على شبكة الإنترنت بأجهزة الرقابة على بيانات المبيعات) شاملة التركيب وصيانة البرامج لمدة عامين وتقديم الاستشارات والتدريب الخاص بالأجهزة والبرامج الموردة من الشركة بقيمة إجمالية تبلغ 900 ألف دولار، والممولة من خلال المنحة المقدمة من الحكومة اليابانية.</p>\r\n\r\n<p>&nbsp;</p>', 'storage/app/public/news/1535554395رئيس الحكوم.jpg', 1, '2018-08-29 16:53:33', '2018-08-29 16:53:15', '2018-08-29 16:53:33', 629, 629, 1, 1);
INSERT INTO `news` VALUES (340, 'تأجيل دعوى بطلان قانون الأحوال الشخصية فيما يخص قيام الزوج بمراجعة مطلقته', '<p>قررت هيئة المفوضين بالمحكمة الدستورية العليا، تأجيل الدعوى المطالبة بعدم دستورية نص المادة 22 من القانون رقم 1 لسنة 2000 بشأن تنظيم بعض أوضاع وإجراءات التقاضى فى مسائل الأحوال الشخصية، لجلسة 14 أكتوبر المقبل، فيما نصت عليه من أنه &quot;لا يقبل عند الإنكار ادعاء الزوج مراجعته مطلقته ما لم يعلنها بهذه المراجعة بورقة رسمية قبل انقضاء 60 يوما&quot;.</p>\r\n\r\n<p>وتضمنت الدعوى رقم 54 لسنة 40 دستورية المقامة من محمد عمر عبدالجواد ضد رئيس الوزراء وآخرين، المطالبة بعدم دستورية نص المادة 22 من القانون رقم 1 لسنة 2000 بشأن تنظيم بعض أوضاع وإجراءات التقاضى فى مسائل الأحوال الشخصية، فيما تضمنته من مخالفة لأحكام الشريعة الإسلامية وما تضمنته من ممايزة بين الرجل والمرأة من حقوق شرعية وقانونية فى الإثبات أمام القضاء للراجعة من الطلاق.</p>', 'storage/app/public/news/1535613314المحكمة الدستوري.jpg', 1, '2018-08-30 09:15:14', '2018-08-30 09:15:14', '2018-08-30 09:15:14', 629, 629, 1, 1);
INSERT INTO `news` VALUES (341, 'المالية: تحصيل المدفوعات المالية الحكومية إلكترونيا إلزاميا بدءا من يناير 2019', '<p><span dir=\"rtl\">أكدت وزارة المالية فى بيان لها اليوم &quot;الخميس&quot; أن الدولة حريصة على استقرار السياسات المالية والضريبية والجمركية وذلك بهدف تشجيع المستثمرين على زيادة استثماراتهم وتدعيم المناخ الجيد الذى تسعى له الدولة لتحسين بيئة الأعمال للاقتصاد المصري.</span></p>\r\n\r\n<p><span dir=\"rtl\">أكد البيان أن وزارة المالية تستعد لتطبيق نظام تحصيل المدفوعات المالية الحكومية الكترونيا الزاميا للمتعاملين مع الجهات الحكومية المختلفة بسداد المستحقات المالية التى تزيد قيمتها عن100 ألف جنيه بإحدى وسائل الدفع الالكترونى بدءا من 1 يناير 2019 وتعد هذه المنظومة الجديدة إنجاز جديد فى مسيرة مصر حيث يحقق الكثير لمستقبل الاقتصاد المصرى ويعنى اننا نسير بخطى ثابتة نحو التحول من مجتمع نقدى &quot;كاش&quot; إلى مجتمع الكترونى وأن الإقتصاد المصرى اصبح يرتكز على تقنيات حديثة تدفع لتزايد معدلات النمو وأن خطوة نظام تحصيل المستحقات الحكومية إلكترونيًا تعد استكمالًا لنجاح الإصلاح المالى الذى تتبناه الدولة.</span></p>\r\n\r\n<p><span dir=\"rtl\">واشار البيان إلى أنه يجرى حاليا العمل على قدم وساق للانتهاء من التجهيزات واليات تطبيق هذا النظام الجديد من خلال اصدار الكروت الالكترونية المستخدمة و انشاء البنية التحتية من خطوط انترنت ووسائل تكنولوجية حديثة وتوفير الاجهزة الالكترونية بالجهات الحكومية المختلفة بالاضافة إلى توفير ماكينات نقاط البيع الحكومية فى كافة انحاء الجمهورية وكذلك البدء قريبا فى تدريب العاملين على استخدام المنظومة الالكترونية الجديدة وتوفير بيئة العمل المناسبة.</span></p>\r\n\r\n<p><span dir=\"rtl\">وفى سياق آخر قامت مصلحة الضرائب فى الاونة الاخيرة بالبدء التجريبى فى استقبال الاقرارات الالكترونية وإتاحة جميع المعلومات المطلوبة للاستفادة من هذه الآلية المتطورة التى تسهل على الممولين والمسجلين فى تقديم اقراراتهم ليصبح بعدها الزاميا من اول أكتوبر المقبل ويأتى ذلك ضمن جهود وزارة المالية فى تحديث وميكنة دورة العمل بمصلحة الضرائب والاخذ بالآليات الالكترونية الحديثة التى تطبقها كبرى دول العالم بما يسهم فى احكام الرقابة على الإيرادات العامة وعمليات تلقى وفحص الاقرارات الضريبية وان مصلحة الضرائب جاهزة للتعامل مع الاقرارات الالكترونية حيث تم الانتهاء من جميع الترتيبات الخاصة بربط الموقع الالكترونى للمصلحة بقاعدة بيانات الممولين والمسجلين لتسجيل واثبات تقديم الاقرارات لحظيا فى ملفات الممولين والمسجلين.</span></p>', 'storage/app/public/1535622343محمد معيط.jpg', 1, '2018-08-30 11:45:43', '2018-08-30 11:45:10', '2018-08-30 11:45:43', 629, 629, 1, 1);
INSERT INTO `news` VALUES (342, 'رسمياً.. القائمة النهائية للمحافظين الجدد ونوابهم بعد أداء اليمين أمام الرئيس', '<p>شهد الرئيس عبد الفتاح السيسي اليوم أداء المحافظين ونوابهم، اليمين الدستورية، وهم</p>\r\n\r\n<p>المحافظون:</p>\r\n\r\n<ul>\r\n	<li>اللواء/ خالد فودة صديق محمد&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;محافظاً لجنوب سيناء &nbsp; &nbsp;&nbsp;&nbsp;</li>\r\n	<li>السيد اللواء/ عبد الحميد عبد العزيز الهجان&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;محافظاً لقنا &nbsp;</li>\r\n	<li>السيد اللواء/ أحمد عبد الله محمد عبد الله&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; محافظاً للبحر الأحمر</li>\r\n	<li>السيد اللواء/ إسماعيل عبد الحميد طه محمود &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; محافظاً لكفر الشيخ &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li>\r\n	<li>السيد اللواء/ عادل محمد إبراهيم يوسف الغضبان&nbsp; &nbsp; &nbsp;محافظاً لبورسعيد&nbsp;</li>\r\n	<li>السيد اللواء/ محمد سالمان موسى الزملوط &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; محافظاً للوادي الجديد &nbsp;&nbsp;</li>\r\n	<li>السيد الدكتور/ ممدوح مصطفى السيد أحمد غراب&nbsp; &nbsp; &nbsp;محافظاً للشرقية&nbsp;&nbsp;&nbsp;</li>\r\n	<li>السيد/ محمد عبد الفضيل شوشة &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; محافظاً لشمال سيناء</li>\r\n	<li>السيد المستشار/ هاني عبد الجابر محمد إبراهيم&nbsp;&nbsp;&nbsp; محافظاً لبني سويف&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li>\r\n	<li>السيد المستشار/ مصطفى محمد ألهم خالد&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; محافظاً للأقصر&nbsp;&nbsp;&nbsp;</li>\r\n	<li>السيد الدكتور/ كمال جاد شاروبيم سعد &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; محافظاً للدقهلية &nbsp;&nbsp;</li>\r\n	<li>السيد الدكتور/ علاء عبد الحليم محمد مرزوق &nbsp;&nbsp;&nbsp;&nbsp; محافظاً للقليوبية</li>\r\n	<li>السيد الدكتور/ عبد العزيز قنصوة &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; محافظاً للإسكندري